css:
@-moz-document domain("workflowy.com")
{

/* ==== HIGHLIGHT AND SELECTION COLORS ==== */

/* TEXT SELECTION COLOR */
*::selection {
	background: #000 !important;
	color: #fff;
}
*::-moz-selection {
	background: #000 !important;
	color: #fff;
}
*::-webkit-selection {
	background: #000 !important;
	color: #fff;
}

/* SEARCH RESULT COLOR */
.content .contentMatch {
	background-color: #000000;
	color: #fff;
}

.contentTag.contentMatch:hover {
	background-color: #000000;
	color: #fff;
} 


/* 'LAST-CHANGED' HIGHLIGHT COLOR */
.project.metaMatches:not(.addedToSelection) > .name > .content > .innerContentContainer,
.project.metaMatches:not(.addedToSelection) > .notes > .content > .innerContentContainer {
	background-color: whitesmoke;
}

/* ------------------------------------------------ */

/* ======= BIG PLUS/ ADD BUTTON ======= */

/* "ADD CHILD" BUTTON CIRCLE */
.circle {
	display: none !important;
}
    
/* "ADD CHILD" BUTTON */

.addChildButton {
    margin-top: 60px;
}

.addChildButton:hover {
    opacity: 0.3;
	-webkit-transition-duration: .3s;
    -moz-transition-duration: .3s;
}

.addChildButton {
	opacity: 0.1;
    transform: scale(6,6);
	-webkit-transition-duration: .3s;
    -moz-transition-duration: .3s;	
}


/* ------------------------------------------------ */

/* FADE OUT COMPLETED ITEMS & FADE IN ON HOVER OVER */
@media screen {
	.project.done .content,
	.project.doneAppearance .content {
		opacity: 0.4;
	}
	.project.done:hover .content,
	.project.done:focus .content,
	.project.done .content:focus,
	.project.doneAppearance:hover .content,
	.project.doneAppearance:focus .content,
	.project.doneAppearance .content:focus {
		opacity: 1;
	}
}

/* ------------------------------------------------ */

/* KEYBOARD SHORTCUT HELPER SHOW ON HOVER */

._cmz0uj {
    opacity: 0;
    transition: opacity 0.5s linear;
}

._cmz0uj:hover {
    opacity: 1;
}

._g7zb9c {
    border-left: none;
    background-color: transparent !important;
}

}
