css:
@-moz-document domain("workflowy.com")
{

/* mmm... Donuts */
a.bullet > svg {
  fill: transparent;
}
a.bullet > svg > circle {
  stroke-width: 0.8;
  stroke: rgb(160,160,160);
}
/* Bullets Lighter Halo */
.collapsed a.bullet {
  background-color: rgba(220, 224, 226, .6);
}
/* Bullets Mirror Parents */
a.bullet > svg > path[d="M9 4 L4 9 L9 14 L14 9"] {
  fill: deepskyblue;
}
/* Bullets Mirror Children DONUTS */
a.bullet[href^="/#/v_"] > svg > circle {
  stroke: deepskyblue;
}
/* Color Embedded Bullets */
.addedShared .bullet > svg, [data-tid] .bullet > svg {
  fill: rgb(145,92,215);
}
/* Color Embedded Root Halo */
.addedShared > .name > .bullet {
  background-color: rgba(185,172,203, .6);
}
/* Color Shared Bullets */
.shared .bullet > svg {
  fill: rgb(89,153,212);
}
/* Color Shared Root Halos */
.shared > .name > .bullet {
  background-color: rgba(167,183,198, .6);
}

}
