css:
@-moz-document domain("userstyles.org") {
body {
    background-color: #171616;
}

#navigation {
    background-color: #1d1c1c;
}

.navigation_title {
    color: #0875e2;
}

.navigation_link:hover {
    background-color: #555;
}

#nav-view {
    background-color: #272626;
}

#infomation_value_left {
    color: #3498db;
}

#information_key_left {
    color: #599bdc;
}

#information_key {
    color: #357ae8;
}

#information_value {
    color: #599bdc;
}

#headline {
    color: #357ae8;
}

.text_style_page {
    color: #3498db;
}

#notes_textarea {
    background-color: #1d1c1c;
    color: #3498db;
}

#discussion_author a, #discussion_author a:visited, #discussion_author a:visited:hover {
    color: #2635f7 !important;
}

.style_title {
    color: #08a9f9;
}

#applies_link {
    color: #3498db;
}

#share_div {
    color: #3498db;
}

.white_button, #send_feedback_button {
    background-color: #696969;
    border: solid 1px #555;
}

.white_button:active, #send_feedback_button:active {
    background-color: #696969;
}

.white_button:hover, #send_feedback_button:hover {
    background-color: #313030;
}

.style_card {
    background-color: #5f5d5d;
}

h1 {
    color: #357ae8;
}

* {
    color: #808080;
}

div.form-controls {
    background-color: #1d1c1c;
}

table.form-controls, .form-controls>tbody>tr>td, textarea, input {
    background-color: #1d1c1c;
    border-color: #555;
}

#footer {
    width: 100%;
    background-color: #1d1c1c;
}

.Item.Read {
    background: #222;
}

.Item.Read:hover {
    background: #444;
}

#Head .SiteSearch .InputBox, .TextBoxWrapper textarea, .TextBoxWrapper input:not([type=radio]):not([type=checkbox]), .DiscussionForm .Category, .DiscussionForm input.InputBox, .OptionsMenu .Flyout, .MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {
    background-color: #333;
    color: #bbb;
}

#Head a {
    color: #333;
}

.Button.Active, .ButtonGroup.Open .Button.Handle, .Button:focus, .Button:active {
    border-color: #888;
    background: #555;
    color: #000;
    text-shadow: 0 1px 0 #aaa;
}

.Button, .ButtonGroup.Open .Button.Handle {
    border-color: #888;
    background: #555;
    color: #000;
    text-shadow: 0 1px 0 #aaa;
}

Button {
    box-shadow: inset 0 1px 0 #555, inset 0 -1px 2px #888 !important;
    -webkit-box-shadow: inset 0 1px 0 #555, inset 0 -1px 2px #888 !important;
}
}
