css:
@-moz-document domain("wikihow.com") {
body {
    background-color: #222;
    color: #989898;
}

#intro.hasad {
    background-color: #323232;
}

h1 a:hover {
    color: #f6f5f4;
}

h1 a {
    color: #a6a6a6;
}

.section h2, .steps_list_2 li {
    color: #989898;
    background-color: #323232;
}

#sp_helpful_box, #sp_helpful_new, #sp_difficult_box, .sp_box, .sidebox, .don_desktop #donate_bottom, .qa_bubble, .ra_side_vote, #ratearticle_side_yes, #qa_asked_question, #qa_email, .qa_pa_email, .qa_aq_search, .uci_thumbnail.uci_empty, .uci_empty #uci_fileinput_square, #rating_container, .info, .image_map, .mainpage #bodycontents.minor_section {
    color: #989898;
    background-color: #323232;
    border-color: #3c3c3c;
}

#social_proof_sidebox.arrow_box h3, #sp_inner, #ratearticle_sidebar, .menu, .menu_login, .menu_messages, #slider_thanks_08, #hp_container .userlogin {
    background-color: #3c3c3c;
    color: #989898;
}

.section_text {
    background-color: #3c3c3c;
}

#ra_side_choices, .uci_empty #uci_fileinput_square:hover {
    background-color: #222;
}

a {
    color: #93b874;
}

a:hover, a:focus {
    color: #363;
}

.button.secondary {
    background-color: #93b874;
    color: #3c3c3c;
    border-color: #3c3c3c;
}

.button.secondary:hover {
    background-color: #363;
}
}
