css:
@-moz-document domain("kimcartoon.to") {
/* Import */
/* uncomment below to use a custom font */
/* @import url('https://fonts.googleapis.com/css?family=Quicksand'); */

/* Change below to your liking */
:root 
{
    /* Background Colours */
	--body-bg: #202020; 
	--first-card-bg: #191919; 
    --second-card-bg: #131313; 
    --third-card-bg: #101010; 
    
    /* Font */
    --font: Quicksand, Roboto, Tahoma, Verdana, Arial;
    
    /* Font Colours */
	--first-color: #777; /* Light Grey */
	--second-color: #555; /* Dark Grey */
    --third-color: #ff007e; /* Pink */
    --fourth-color: #00fff6; /* Cyan */
}

/* Body */
body,
html
{
    background: var(--body-bg);
    color: var(--first-color);
    font: normal 12px var(--font);
    font-family: var(--font);
    font-weight: normal;
}

a 
{
    color: var(--third-color);
    transition: color .3s, text-shadow .3s;
}

a:hover,
.episodeList td a:hover
{
    color: var(--fourth-color) !important;
    text-shadow: 0px 0px 5px var(--fourth-color);
}

/* Navbar */
#navcontainer ul
{
    font: normal 12px/27px var(--font);
}

#navbar
{
    background-color: var(--first-card-bg);
    border: 0px;
}

#headnav
{
    position: fixed;
    top: 0;
    z-index: 9;
    margin: 0;
    padding: 0;
}

#navsubbar p a 
{
    color: var(--first-color);
}

#navsubbar p a:hover
{
    color: var(--fourth-color);
}

#navcontainer a
{
    color: var(--first-color);
    transition: color .3s, text-shadow .3s;
}

#navcontainer a:hover, 
#navcontainer li a#currentTab:hover
{
    color: var(--fourth-color);
    text-shadow: 0px 0px 5px var(--fourth-color);
    background-color: var(--third-card-bg);
}

#navcontainer li a#currentTab,
#navsubbar
{
    background-color: var(--third-card-bg);
    color: var(--third-color);
    transition: color .3s, text-shadow .3s;
}

/* User */
#topHolderBox
{
    background-color: transparent;
    width: 188px !important;
	height: 50px !important;
    font-family: var(--font);
}

.boxDropDown
{
    background: transparent;
    border: 1px solid var(--third-color);
    border-radius: 0px;
    color: var(--third-color);
    font-weight: normal;
    padding: 7px 0px 5px 0px;
    width: 198px !important;
    transition: color .3s, text-shadow .3s border-color .3s, box-shadow .3s;
}

#aDropDown:hover 
{
    color: var(--fourth-color);
    border-color: var(--fourth-color);
    box-shadow: 0px 0px 10px var(--fourth-color), 0px 0px 10px var(--fourth-color) inset;
}

#menu_box
{
    top: 52px;
    background: var(--first-card-bg);
    border: 0px;
    box-shadow: 0px 5px 10px #0b0b0b;
    left: 249px !important;
    width: 200px;
    font-family: var(--font);
    font-weight: normal;
}

#menu_box a
{
    color: var(--third-color);
    line-height: 45px;
    border: 0px;
    text-align: center;
}

#menu_box > a:nth-child(5) 
{
    margin-top: 10px;
}

#menu_box a:hover 
{
    background: var(--first-card-bg);
    color: var(--fourth-color);
    text-shadow: 0px 0px 5px var(--fourth-color);
}

#head > h3 > div:nth-child(1) 
{
    margin: -40px 13px 20px 0px !important;
}

/* Search */
input.text 
{
    border: 0px;
    background: var(--third-card-bg);
    font: normal 15px var(--font);
    color: var(--first-color);
    height: 30px;
}

#result_box
{
    top: 36px;
	left: 100px !important;
    width: 246px;
    background: var(--first-card-bg);
    opacity: 1;
    border-radius: 0px;
}

#result_box a 
{
    color: var(--third-color);
    display: inline-block;
    width: 100%;
    line-height: 30px;
    text-decoration: none;
}

#result_box a:hover 
{
    background: var(--first-card-bg);
    color: var(--fourth-color);
}

#search p 
{
    margin: 0px 0px 15px 0px;
}

#formSearch > div 
{
    margin: 0 !important;
}

#head h3
{
    font: normal 14px var(--font);
}

/* Main Page */
#containerRoot
{
    margin-top: 50px;
}

.title-list
{
    color: var(--third-color);
}

.rightBox
{
    border: 0px !important;
    background: var(--first-card-bg);
}

/* Latest Update */
.bigBarContainer
{
    background: var(--first-card-bg);
}

.textWhite 
{
    color: var(--first-color);
    text-shadow: none;
}

div.items img
{
    border: 0px;
}

.scrollable .items .item-title
{
	background: var(--second-card-bg);;
    font-weight: normal;
    bottom: 0px;
    left: 0px;
}

.scrollable .items a:hover
{
    color: var(--fourth-color);
}

/* New Cartoons, Top Day, Top Week, Top Month */
#subcontent span.title,
#subcontent div div,
.title
{
    font: normal 15px var(--font);
}

#tabmenucontainer ul
{
    border: 0px;
    font: normal 15px/25px var(--font);
}

#tabmenucontainer li a.tabactive 
{
    background-color: var(--first-card-bg);
    color: var(--third-color);
}

#tabmenucontainer li a
{
    background-color: var(--first-card-bg);
    color: var(--first-color);
    line-height: 20px;
}

#tabmenucontainer a:hover,
#tabmenucontainer li a.tabactive:hover
{
    color: var(--fourth-color);
    text-shadow: 0px 0px 5px var(--fourth-color);
}

#subcontent
{
    background: var(--first-card-bg);
    border: 0px;
}

#subcontent div div,
#subcontent div div.blue,
.blue > div
{
    background: var(--first-card-bg) !important;
}

.info
{
    color: var(--second-color);
}

#subcontent div a img
{
    border: 1px solid var(--third-color);
    transition: border-color .3s, box-shadow .3s;
}

#subcontent div a img:hover
{
    border-color: var(--fourth-color);
    box-shadow: 0px 0px 5px var(--fourth-color), 0px 0px 5px var(--fourth-color) inset;
}

/* Ongoing Series */
.title-list.icon
{
    background: transparent;
    padding-left: 0px;
    line-height: 40px;
}

.textDark
{
    color: var(--second-color);
}

/* Footer */
#footer
{
    background: var(--first-card-bg);
}

#footcontainer p a
{
    color: var(--third-color);
    transition: color .3s, text-shadow .3s;
}

#footcontainer p a:hover
{
    color: var(--fourth-color);
}

/* Video Page */

#myContainer > div:nth-child(2) 
{
	float: right !important;
}

.w-left 
{
    float: none !important;
    margin: 0 auto;
}

.video-js
{
	outline: none !important;
}

.btn-cs 
{
    border-radius: 0px;
    background: #000;
    box-shadow: none;
    padding: 10px 15px 10px 15px;
    border: 1px solid var(--third-color) !important;
    color: var(--third-color);
    margin-top: 5px;
    font-weight: normal !important;
    transition: color .3s, text-shadow .3s, border-color .3s, box-shadow .3s;
}

.btn-cs:hover
{
    color: var(--fourth-color);
    border-color: var(--fourth-color) !important;
    box-shadow: 0px 0px 10px var(--fourth-color), 0px 0px 10px var(--fourth-color) inset;
}

.btn-cs.disable 
{
    color: var(--second-color);
    border-color: var(--second-color) !important;
}

.btn-cs.disable:hover
{
    color: var(--second-color);
    border-color: var(--second-color) !important;
    text-shadow: none !important;
    cursor: not-allowed;
}

/* Rest */
.bigChar
{
    color: var(--third-color);
}

.episodeVisited 
{
    color: var(--second-color) !important;
}

.bigBarContainer
{
    border: 0px !important
}

select 
{
    background-color: var(--third-card-bg);
    color: var(--first-color);
    border: 0px;
    height: 30px;
}

#divListCategories > span 
{
    color: var(--second-color);
}

.listing > tbody 
{
    background-color: var(--second-card-bg);
}

.listing th
{
    border-bottom: 0px;
    height: 50px;
    color: var(--third-color);
    background-color: var(--third-card-bg);
}

.listing tr.odd
{
    background: var(--second-card-bg);
}

.listing tr 
{
    height: 30px;
}

.listing tr:hover td 
{
    background: none repeat scroll 0 0 var(--first-card-bg);
}

.txtSignUp 
{
    border: 0px;
    background: var(--third-card-bg);
    font: normal 12px var(--font);
    color: var(--first-color);
    height: 30px;
}

.aButton 
{
    color: var(--third-color);
    background: transparent;
    border: 1px solid var(--third-color);
    font-weight: normal !important;
    padding: 5px 0px 5px 0px;
    transition: color .3s, border-color .3s, box-shadow .3s;
}

.aButton:hover
{
    color: var(--fourth-color);
    box-shadow: 0px 0px 5px var(--fourth-color), 0px 0px 5px var(--fourth-color) inset;
    background: transparent;
    border-color: var(--fourth-color);
    font-weight: normal !important;
    padding: 5px 0px 5px 0px;
}

/* Remove Clutter */
#aDropDown > span > small,
#topHolderBox > img,
#topHolderBox > span,
#menu_box > a > img,
.rightBox > .barContent > div > img,
#formSearch > div:nth-child(3),
#___plusone_1,
#rightside > iframe,
.w-right,
.kcAds,
#o4ill5lhm75550891421
{
    display: none !important;
}
}
