css:
@-moz-document domain("jeuxvideo.com") {
/*
Flat & blue JVC. Par imThatGuy

v0.1.0.
*/

/*@import url(//fonts.googleapis.com/css?family=Ubuntu);*/
a { outline: none!important; }
/*#discussion .bloc-message-forum, .text-enrichi-forum { font-family: ubuntu; } /* Ajoute la font ubuntu pour les messages dans les topics */
/* Boutons */
.btn:not(.btn-jv-editor-toolbar) {
	border-radius: 3px;
    border: 1px solid #ccc!important;
    background-color: white!important;
    color: #888!important;
    font-family: 'Ubuntu', sans-serif;
    font-weight: normal;
    /*box-shadow: 0px 0px 2px #ccc;*/
    outline: none!important;
}

.btn:not(.btn-jv-editor-toolbar):hover {
	background-color: #f9f9f9!important;
    /*box-shadow: 0px 0px 2px #ccc;*/
    color: #888;
    border: 1px solid #ccc;
}

.btn:not(.btn-jv-editor-toolbar):active {
	background-color: #f7f7f7!important;
    box-shadow: inset 0px 0px 2px #ccc;
    color: #4274a0!important;
    border: 1px solid #ccc;
}

.btn:not(.btn-jv-editor-toolbar):focus {
	background-color: #f7f7f7!important;
    color: #4274a0!important;
    border: 1px solid #ccc;
}

/* Margin pour les boutons du bas */
.bloc-pre-pagi-forum { padding-bottom: 10px; margin-bottom: 0px!important; }

/* Bouton pour tourner les pages */
.bloc-pagi-default .pagi-debut-actif, 
.bloc-pagi-default .pagi-precedent-actif, 
.bloc-pagi-default .pagi-suivant-actif, 
.bloc-pagi-default .pagi-fin-actif {
    border-radius: 50px !important;
    background-color: #ddd;
}
.bloc-pagi-default:hover .pagi-debut-actif:hover, 
.bloc-pagi-default:hover .pagi-precedent-actif:hover, 
.bloc-pagi-default:hover .pagi-suivant-actif:hover, 
.bloc-pagi-default:hover .pagi-fin-actif:hover {
    background-color: #ccc;
}
.bloc-pagi-default {
	margin-top: 10px;
}

/* Inputs */
.form-control {
    border-left: 0.3125rem solid #ddd!important;
    border-color: #e4e4e4!important;
}
/*
.form-control:hover {
    border-color: #e7e7e7!important;
}
*/
.form-control:focus {
    border: 0.0325rem solid #4397e0!important;
    border-left: 0.3125rem solid #4397e0!important;
}
*
/* Topic head */
.topic-list .topic-head { background-color:rgb(235, 239, 241)!important; }
.topic-list .topic-head .topic-subject { padding-left: 5px!important; border-top-left-radius: 5px!important; }
.topic-list .topic-head .topic-select { padding-right: 5px!important; border-top-right-radius: 5px!important; }

/* Topic list */
.topic-list-admin {
    background: transparent!important; padding: 0px;
    border: none;
}

/* Container */
.container { background-color: #f9f9f9; }

/* #Content */
#content {
    background: none;
    background-color: #ddd;
    margin-top: 53px;
}

/* Recherche forum */
.bloc-rech-forum .txt-search {
    background-color: white;
    outline: none;
    width: 250px;
    border: 0.5px solid #f4f4f4;
    box-shadow: 0px 1px 1px #ccc;
    padding: 8px;
    border-radius: 1px;
    transition: box-shadow 0.1s
}
.bloc-rech-forum .txt-search:focus { box-shadow: 0px 1px 4px #bbb; }

.bloc-rech-forum .select-search { outline: none; border: 0.5px solid #f4f4f4; box-shadow: 0px 1px 1px #ccc; padding: 2px; border-radius: 1px; transition: box-shadow 0.1s }
.bloc-rech-forum .select-search:focus { box-shadow: 0px 1px 4px #bbb; }

.bloc-rech-forum { background-color: transparent!important; }

/* Footer */
footer { display: none!important; }

/* Enleve le border bottom de la recherche forum */
.bloc-pre-pagi-forum.bloc-outils-top, .bloc-pre-pagi-forum.bloc-outils-bottom {
	border: none!important;
}

/* Post topics */
.bloc-message-forum {
    background-color: white!important;
    border: none!important;
    box-shadow: 0px 1px 2px #bbb;
    border-radius: 1.2px;
}

/* Bouton page avtive topic */
span.page-active {
    background-color: #4274a0!important;
    border-radius: 50px;
}

/* Liens */
a, .lien-jv { color: #444; }
a:hover, .lien-jv:hover { color: #4274a0; }
a:active, .lien-jv:active, a:focus, .lien-jv:focus {
    color: #4274a0;
    /*text-decoration: underline!important;*/
}

/* Liens topics */
.text-enrichi-forum a, .text-enrichi-forum .lien-jv { color: #4274a0!important; }

/* Liens panels droit */
.panel-jv-forum .bloc-info-forum .lien-jv:hover, .panel-jv-forum .bloc-forums-preferes .lien-jv:hover, .panel-jv-forum .bloc-sous-forums .lien-jv:hover, .panel-jv-forum .bloc-info-forum .lien-jv:focus, .panel-jv-forum .bloc-forums-preferes .lien-jv:focus, .panel-jv-forum .bloc-sous-forums .lien-jv:focus { color: #4274a0!important; }

/* Pseudos dans les forums */
.bloc-message-forum .bloc-pseudo-msg, .bloc-message-forum .bloc-pseudo-msg:hover, .bloc-message-forum .bloc-pseudo-msg:active, .bloc-message-forum .bloc-pseudo-msg:focus { color: #111!important; }

/* Titre topics */
#bloc-title-forum, .titre-bloc { color: #4274a0; }

/* Bouton poster */
.btn-poster-msg {
	width: 180px!important;
    /*text-align: center!important;*/
}
.btn-poster-msg:hover {
	background-color: #f4f4f4!important;
}

/* Panels */
.panel {
    background-color: #f4f4f4!important;
    color: #555!important;
    box-shadow: 0px 1.5px 0.5px #ddd;
    border-top-right-radius: 10px!important;
    border-top-left-radius: 10px!important;
}
.panel-heading {
    background-color: rgb(235, 239, 241)!important;
    color: #393939!important;
    border-top-right-radius: 4px!important;
    border-top-left-radius: 4px!important;
}

/* Icon nb connectés */ .panel-jv-forum .nb-connect-fofo { background: url(https://i.imgur.com/qx6MBdK.png) left center no-repeat; }

.titre-info-fofo { color: #555!important; }

/* Enleve la couleur blanche */
.panel-jv-forum .bloc-info-forum .lien-jv, .panel-jv-forum .bloc-forums-preferes .lien-jv, .panel-jv-forum .bloc-sous-forums .lien-jv {
    color: #777;
}
.panel-jv-forum .bloc-info-forum .lien-jv:hover, .panel-jv-forum .bloc-forums-preferes .lien-jv:hover, .panel-jv-forum .bloc-sous-forums .lien-jv:hover {
    color: #333;
}

/* Icon paramètres */
.titre-info-fofo-link.icon-config { color: #555!important; }


/* Liste sujets topics */
/*.topic-list .topic-title:hover { color: #555!important; }
.topic-list .topic-title:visited { color: #555!important; }*/
.lien-jv.topic-title {
    color: #333!important;
    font-weight: bold;
    font-size:11px;
    font-family: Arial;
}

.topic-list .ads-middle ~ li:nth-of-type(odd) { background-color: #fff; }
ul.topic-list > li:not(.ads-middle) {
    background-color: #fff;
}
.topic-list > li:first-child {
    border-radius: 5px!important;
}
.topic-list li { border: 1px solid #ccc!important;border-radius: 5px; }
ul.topic-list > li:not(.ads-middle):hover {
    background-color: #f4f4f4;
}

/* Date topic */
.topic-list .topic-date>.lien-jv:hover {
    color: #4274a0;
}

/* Header */
/*div.jv-header-sticky, .jv-header-bottom { display: none; }*/
.jv-header-top {
    background-color: rgba(42, 90, 132, 0.9)!important;
    /*border-bottom: 2px solid #555;*/
    /*opacity: 0.9;*/
}

.jv-header-menu .jv-header-bottom, .jv-header-menu .jv-header-sticky { display: none!important; }

/*.jv-header-menu .jv-nav-primary .jv-nav-lvl1-item:last-child>.nav-link { display: none!important; }
.jv-header-menu .jv-nav-primary .jv-nav-toggler:before { background-color: transparent; }
.jv-header-menu .jv-nav-primary .jv-nav-toggler:before { border-top-color: #ccc; }
*/
.jv-header-menu .jv-header-bottom, .jv-header-menu .jv-header-sticky { background-color: rgba(42, 90, 132, 0.9)!important; }
.jv-header-menu #jv-header-search {
    background-color: rgba(42, 90, 132, 0.9)!important;
}

.jv-header-menu #jv-header-search .search input { border-bottom: 0.0625rem solid #ddd; }

.jv-header-menu #jv-header-search .jv-search-keyword-label { color: white!important; }
.jv-header-menu #jv-header-search .jv-search-keyword-link:hover { color: #ddd!important; }

.jv-header-menu #jv-header-search .search button {
    transition: 0.2s;
    border: 1px solid  transparent;
    border-radius: 3px;
    background-color: white;
    color: #4274a0;
}
.jv-header-menu #jv-header-search .search button:hover {
    color: white;
    border: 1px solid white;
    background-color: #4274a0;
}

.jv-header-menu .nav-link-search:active, .jv-header-menu .nav-link-search:focus {
    text-decoration: none!important;
}

.jv-header-menu .nav-link-search:after {
    background-color: rgba(255, 255, 255, 0.4);
}

/* Editor */
.conteneur-editor {
    background-color: #f7f7f7!important;
}
.jv-editor .btn-group {
    background-color: #f9f9f9;
    border: none;
    border-bottom: 1px solid #eee;
    border-color: #ddd;
    border-radius: 0px;
}
.jv-editor .btn-jv-editor-toolbar:hover {
    color: #4274a0;
    border-bottom: 1.5px solid #4274a0;
}
.jv-editor .btn-jv-editor-toolbar:active {
    color: #335a7c;
    box-shadow: none;
    background-color: #eee;
    border-bottom: 1.5px solid #4274a0;
}
.jv-editor .btn-jv-editor-toolbar:focus {
    color: #335a7c;
    box-shadow: none;
    background-color: #eee;
    border-bottom: 1.5px solid #4274a0;
}
.jv-editor .text-editor { border-top: 0.0625rem solid #ddd; }

/* Game top */
.game-top {
	background-color: #335a7c!important;
    /*opacity: 0.9;*/
}
.game-top-bar-support { background-color: rgba(0,67,101,0.75)!important; }

.navbar-game .nav-item.active .nav-link>span, .navbar-game .nav-item.label-support.active-mach-version .nav-link>span {
	border-bottom-color: #4274a0;
    color: #4274a0;
}

.label-support.active, .label-support.active-mach-version { background-color: #21405a; }
.label-support.active:hover, .label-support.active-mach-version:hover { color: #345b7c!important; }

.navbar-game .nav-item:hover .nav-link, .navbar-game .nav-item .nav-link:focus { color: #345b7c!important; }

/* Game right */
.menu-sommaire>ul .actif a, .menu-sommaire>ul .actif { color: #345b7c; }

/* Game autre */
.accr-item-large .mtag { background-color: #4397e0; }

/* Spoils */
.bloc-spoil-jv .txt-spoil { background-color: #4274a0; border-radius: 3px; }
.bloc-spoil-jv .aff-spoil, .bloc-spoil-jv .masq-spoil { color: rgba(42, 90, 132, 1); }
.bloc-spoil-jv.en-ligne .open-spoil:checked ~ .contenu-spoil, .bloc-spoil-jv .contenu-spoil {
    background-color: #e9f2ff;
    /*border: 0.0625rem solid #d5e1f2;*/
    border: none;
    border-top-right-radius: 2px;
    border-top-left-radius: 2px;
}

/* Sélection */
::selection {
  background: #4e99e0;
    color: white;
}
::-moz-selection {
  background: #4e99e0;
    color: white;
}

/* Sondage */
.bloc-sondage {
    background-color: #eee;
    border-radius: 3px;
}
.bloc-sondage .tab-choix .reponse a {
    color: #333!important;
    background: url(https://i.imgur.com/guueSpT.png) no-repeat left 0.125rem;
}
.bloc-sondage .tab-choix .reponse a:hover {
    color: #4274a0!important;
}
.bloc-sondage .intitule-sondage { color: #4274a0; }
.titre-head-bloc-sondage .titre-bloc { color: #4274a0; }
.titre-head-bloc-sondage { border-bottom: none; }
.bloc-sondage .pied-result {
    border-top: none;
    color: #335a7c;
    font-weight: bold!important;
}

.bloc-sondage .tab-choix .reponse { color: #333!important; }
.bloc-sondage .tab-choix .pourcent { background-color: #d9d9d9; border-top-right-radius: 2px; border-top-left-radius: 2px; }
.bloc-sondage .tab-choix .back-barre { background-color: #b4b4b4!important; }
.bloc-sondage .tab-choix .back-barre span { background-color: #2982ce!important; }

.bloc-sondage .tab-choix .pourcent { color: #555; }

/* Choix profils & MP's */
.jv-header-menu .dropdown .button-primary { background-color: #4274a0!important; }
.jv-header-menu .dropdown .button-primary:hover { background-color: rgba(42, 90, 132, 1)!important; color: white!important; }
.jv-header-menu .account .account-pseudo:hover { color: #4274a0!important; }

.jv-header-menu .account:hover, .jv-header-menu .jv-nav-dropdown-item:hover { background-color: rgba(42, 90, 132, 0.1)!important; }

.jv-header-menu .lien-jv:not(.button):active, .jv-header-menu .lien-jv:not(.button):focus, .jv-header-menu .lien-jv:not(.button):hover, .jv-header-menu a:not(.button):active, .jv-header-menu a:not(.button):focus, .jv-header-menu a:not(.button):hover { color: #4274a0!important; }

.jv-header-menu .jv-nav-dropdown-link { color: black; }
.jv-header-menu .jv-nav-dropdown-link:hover { color: #4274a0; }

/* Couleur pseudos modos */
.text-modo, .text-modo { color: #1e6fb7!important; }
.text-modo:hover, .text-modo:hover { color: #1a69af!important; }

/* MP's */
#discussion h1, #discussion .titre-discut, #mp-menus .nouveau-msg a {
    background-color: #4274a0!important;
    border-radius: 2px;
}
#mp-menus .active a { color: #1a69af!important;  }
.list-msg .row-head .sup-msg, .list-msg .row-head .sujet-msg, .list-msg .row-head .date-msg, .list-msg .row-head .exp-msg { background-color: #4274a0!important; border-radius: 2px; }
#mp-menus .nouveau-msg a:hover { color: white!important; }

.mp-pagination .nb-pages b, .sujet-msg a:hover { color: #4397e0; }

.sujet-msg a:focus, #destinataires .label a:hover, #destinataires .label a:focus { color: #4274a0; }

.mp-pagination a { border-radius: 50px; }

.list-msg .row-mp.selected { background-color: #e5f3ff!important; }

/* Créer nouveau mp -> forms */
#nouveau-msg #conv_titre, #destinataires .form-control-tag {
	border-radius: 3px;
    outline: none;
    border: none;
    border-left: 4px solid #e4e4e4;
    border-right: 0.5px solid #eee;
    border-top: 0.5px solid #eee;
    border-bottom: 0.5px solid #eee;
}

/* Liens dans topic (msg edité compris) */
.bloc-message-forum .info-edition-msg a, .bloc-message-forum .info-edition-msg .lien-jv { color: #4274a0!important; }
.text-enrichi-forum a:hover, .text-enrichi-forum a:focus, .text-enrichi-forum a:active, .text-enrichi-forum .lien-jv:hover, .text-enrichi-forum .lien-jv:focus, .text-enrichi-forum .lien-jv:active { color: #68acf1!important; }

/* Textarea message */
.jv-editor .conteneur-editor, .jv-editor .previsu-editor {
	border-radius: 3px;
    border-color: #ddd;
}

/* Profils */
.bloc-default-profil .header h2 { color: #4274a0; }
.menu-profil .list-menu-profil a:hover {  color: #4274a0; }
.menu-profil .list-menu-profil .actif { color: #4274a0; }
.menu-profil .list-menu-profil .actif:before { background-color: #4274a0; }

/* Bouton rédiger un article */
.link-rediger-article {
    border-radius: 3px;
    transition: 0.2s;
    background-color: #4397e0;
    border: 1px solid transparent;
}
.link-rediger-article:hover {
    box-shadow: 0px 1px 4px #ccc;
    color: #4397e0;
    background-color: transparent;
    border: 1px solid #ccc;
}
.link-rediger-article:active, .link-rediger-article:focus {
    color: #4397e0;
    background-color: transparent;
    border: 1px solid #4397e0;
    text-decoration: none;
}

/* Rediger article */
.required:before {
	color: #555;
}

/* Titre profil */
.titre-profil-general .titre-bloc, .table-profil-modif-default .titre-tab { color: #4397e0; }

/* Page header span */
.page-header span { color: #4397e0; }

/* Icônes modifier profil */
.table-profil-modif-forums .icon-dropdown:hover, .table-profil-modif-forums .icon-dropup:hover { color: #4397e0; } 

/* Icônes header */
.jv-header-menu .jv-header-top .nav-link-account:hover, .jv-header-menu .jv-header-top .nav-link-search:hover, .jv-header-menu .jv-account-number-mp:hover, .jv-header-menu .jv-account-number-notif:hover { color: #e9e9e9!important; }

/* Se connecter */
.bloc-erreur {
    color: #3078b7;
    border-bottom: 0.0625rem solid #90c2ef;
    border-top: 0.0625rem solid #90c2ef;
}

/* Page "tous les forums" */
.fs-body a:hover, .fs-body a:focus { color: #4397e0; }
.f-blabla li a:hover, .f-blabla li a:focus { background-color: #4397e0; }
.bloc-recherche .search input:hover, .forum-search .search input:hover, #form-recherche .search input:hover { border-color: #4397e0!important; }
.bloc-recherche .search input:focus, .forum-search .search input:focus, #form-recherche .search input:focus { border-color: #4397e0!important; }

/* Tests jeux */
.item-small-pres .a-titre-item { color: #4397e0; }
.accr-item-large .a-titre-item:hover { color: #4397e0; }
.bloc-avis-fiche-jeu .bloc-note-lecteur div:first-child, .bloc-avis-fiche-jeu .note-avis-loulou, .titre-head-bloc .bloc-on-right.bloc-link-plus .lien-chev { color: #4397e0; }

.hit-note-r { color: #4397e0; }
.hit-note-g { color: #1f5079; }

.label-pc, .hover-pc:hover, .hover-pc:focus { background-color: #1B486B!important; }
.label-support.active, .label-support.active-mach-version, .label-support.active:hover, .label-support.active-mach-version:hover { color: white!important; }

/* Paramètres pseudos */
.titre-profil-general .lien-plus, .bloc-lien-historique-sanction-profil a { color: #4397e0; }
.ajout-gestion-profil { background: url(https://i.imgur.com/AD3IhTk.png); }

/* DDB & modal noelshack */
.modal-generic-header {
	background-color: #4397e0;
    border-top-right-radius: 4.5px;
    border-top-left-radius: 4.5px;
}

.label-info { background-color: #4397e0; }

.modal-generic.active .modal-generic-main { border-radius: 5px; }

.modal-generic-close { color: #4274a0; transition: color 0.1s; }
.modal-generic-close:hover { color: white; }

.modal-generic-content .form-control {
    outline: none!important;
    box-shadow: none;
    border-top: 1px solid #eee!important;
    border-bottom: 1px solid #eee!important;
    border-right: 1px solid #eee!important;
    border-left: 1px solid #eee!important;
    border-radius: 1px!important;
}
.modal-generic-content .form-control:hover {
    border: none;
	/*border: 1px solid #ccc!important;*/
    border-top: 1px solid #ddd!important;
    border-bottom: 1px solid #ddd!important;
    border-right: 1px solid #ddd!important;
    border-left: 1px solid #ddd!important;
}
.modal-generic-content .form-control:focus {
    border: none;
	/*border: 1px solid #ccc!important;*/
    border-top: 1px solid #4397e0!important;
    border-bottom: 1px solid #4397e0!important;
    border-right: 1px solid #4397e0!important;
    border-left: 3px solid #4397e0!important;
}

#ns-webservice .ns-titre { color: #555; }

#jv-editor-video .modal-generic-tab {
	background-color: #4397e0;
}

/* Scroll bar */
::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-track {
    background: #eee;
    /*border-radius: 3px;*/
}
::-webkit-scrollbar-thumb {
    background: #999;
    /*border-radius: 3px;*/
}
::-webkit-scrollbar-thumb:active {
    background: rgba(42, 90, 132, 1); 
}

/* Panel droit jv.com */
.heading-pres1 { background-color: #4274a0!important; }
span.type { color: #4274a0!important; }

