css:
@-moz-document url-prefix("https://jsfiddle.net") {
/*
	Creator: Mark Cavalli
	Email: MarkCavalli@mail.ru

*/


#sidebar {
    top: 20px;
    width: 10px;
}

#sidebar:hover {
    width: 170px;
   z-index: 100;
}

#s-bottom {
 	display: none;   
}

#content {
    margin-left: 10px;
}

.CodeMirror {
    height: calc(100% - 20px);
    margin-top: 20px;
}

.windowLabel {
    padding: 1px 10px;
}

#actions .actionItem a.aiButton {
    line-height: 19px;
}

#actions .actionItem .aiButton, #actions .actionItem .aiButton span, #actions .dropdown .actionItem a.aiButton {
    height: 19px;
}

#actions #usermenu img {
    top: 2px;
    height: 15px;
    width: 15px;
}


header h1 {
    top: 2px;
    left: 30px;
}

header h1 svg {
    height: 15px;
    width: 20px;
}

header {
    height: 20px;
    position:absolute;
}

#actions {
    height: 20px;
}

#editor iframe, #editor textarea, body.editbox {
    min-width: 320px;
 
}

#editor {
    padding-top: 20px;
    height: calc(100vh - 20px) !important;
}

#editor .panel-h, #editor .panel-v {
    background-color: white;
}

.windowLabel {
    color: deepskyblue !important;   
    position: absolute;
    left: 0px;
    width: calc(100% - 20px);
    background: rgba(241,241,241,1);
    padding: 0px 10px !important;
}

.CodeMirror {
    height: calc(100% - 20px) !important;
    margin-top: 20px !important;
}

.resultsPanel .windowLabelCont .size {
    padding: 1px 6px;
    margin: 2px;
    z-index: 31;
    position: absolute;
    top: 0;
    left: 50px;
    opacity: 1 !important;
}

}
