@-moz-document domain("youtube.com") {
*
  {
    box-shadow: none!important;
  }

  #masthead
  {
    background-color: rgba(0,0,0,0.5);
    backdrop-filter: blur(30px) saturate(150%);
  }

  ytd-multi-page-menu-renderer
  {
    background: rgba(50,50,50,0.7);
  }

  #header
  {
    backdrop-filter: blur(30px);
  }

  ytd-multi-page-menu-renderer #container
  {
    backdrop-filter: blur(30px);
  }

  #contentContainer, #guide-content, #guide-inner-content, #guide-wraper, .style-scrope
  {
    background: rgba(0,0,0,0.1)!important;
  }

  #guide-inner-content
  {
    backdrop-filter: blur(30px);
  }

  #guide-content #header
  {
    opacity: 0;
    backdrop-filter: none;
  }

  #links-holder
  {
    backdrop-filter: blur(10px);
    background: rgba(0,0,0,0);
  }

  #primary-links.ytd-c4-tabbed-header-renderer, #secondary-links.ytd-c4-tabbed-header-renderer
  {
    background: rgba(30,30,30,0.1);
  }

  .gstl_50, .sbsb_a, .sbdd_b
  {
    background: rgba(255,255,255,0.0);
    border: none;
  }

  .sbsb_a
  {
    backdrop-filter: blur(30px);
    background: rgba(0,0,0,0.4);
    border-right: solid 0.5px rgba(255,255,255,0.2);
    border-left: solid 0.5px rgba(255,255,255,0.2);
    border-bottom: solid 0.5px rgba(255,255,255,0.2);
  }

  paper-listbox
  {
    background: rgba(50,50,50,0.6);
    backdrop-filter: blur(30px);
  }

  #playlists.scrollable.ytd-add-to-playlist-renderer, #endpoint
  {
    background: rgba(0,0,0,0.1);
    backdrop-filter: blur(30px);
  }

  ytd-add-to-playlist-renderer
  {
    background: rgba(0,0,0,0);
  }

  yt-sharing-renderer
  {
    background: rgba(40,40,40,0.4);
    box-shadow: none;
  }

  .dropdown-content
  {
    backdrop-filter: blur(15px);
  }

  ytd-thumbnail-overlay-hover-text-renderer
  {
    backdrop-filter: blur(0px);
    background: rgba(0,0,0,0);
    transition: all ease 0.2s;
  }

  ytd-thumbnail-overlay-hover-text-renderer:hover
  {
    backdrop-filter: blur(6px);
    background: rgba(0,0,0,0.1);
  }

  #thumbnail
  {
    transition: all ease 0.2s;
    transform: scale(1);
    filter: blur(0.5px);
    border-radius: 10px;
  }

  #thumbnail:hover
  {
    transform: scale(1.05);
    z-index: 500;
    filter: blur(0px);
    border-radius: 5px;
    box-shadow: 0px 0px 30px 5px rgba(0,0,0,1)!important;
  }

  .captions-text span
  {
    background: rgba(90,90,90,0.0)!important;
  }

  .captions-text
  {
    backdrop-filter: blur(30px);
    background: rgba(90,90,90,0.2)!important;
  }

  #iv-drawer
  {
    backdrop-filter: blur(30px);
    z-index: 37;
  }

  .iv-card
  {
    background: rgba(255,255,255,0.6);
  }

  #ytp-id-6
  {
    backdrop-filter: blur(15px);
  }

  .ytp-settings-menu
  {
    background: rgba(30,30,30,0.3);
    backdrop-filter: blur(30px);
  }

  .gsfs
  {
    transition: all ease 0.1s;
    border: none;
    color: white!important;
  }

  .gsfs:hover
  {
    background: rgba(0,0,0,0.1);
    border-left: solid white 5px;
  }

  .ytp-contextmenu
  {
    background: rgba(0,0,0,0.5)!important;
    backdrop-filter: blur(30px);
  }

  .ytp-menuitem-label
  {
    transition: all ease 0.2s;
    border-left: solid rgba(0,0,0,0) 10px!important;
  }

  .ytp-menuitem-label:hover
  {
    border-left: solid white 5px!important;
  }

  ytd-guide-entry-renderer
  {
    border-left: none;
    transition: all ease 0.2s;
  }

  ytd-guide-entry-renderer:hover
  {
    border-left: solid 4px white!important;
  }

  #endpoint
  {
    transition: all ease 0.2s;
  }

  #endpoint:hover
  {
    background: rgba(0,0,0,0.5);
    border-left: 5px solid white;
  }

  ytd-notification-renderer
  {
    transition: all ease 0.2s;
  }

  ytd-notification-renderer:hover
  {
    background: rgba(0,0,0,0.6)!important;
    border-left: 5px solid white;
  }

  .videoAdUiPreSkipContainer, .videoAdUiSkipContainer, #AdSense, .html5-video-info-panel
  {
    backdrop-filter: blur(10px);
    background: rgba(0,0,0,0.2)!important;
  }

  .ytp-ce-expanding-overlay-background
  {
    background: rgba(255,255,255,0.25);
    backdrop-filter: blur(30px);
  }

  .ytp-ce-channel-metadata
  {
    background: rgba(0,0,0,0);
    color: white;
  }

  ytd-menu-service-item-renderer
  {
    border-left: 10px solid transparent;
    transition: all ease 0.2s;
  }

  ytd-menu-service-item-renderer:hover
  {
    border-left: 5px solid white;
  }

    .ytp-expand-pause-overlay .ytp-pause-overlay, .ytp-pause-overlay .ytp-expand
{
    background: rgba(23,23,23,0.4);
    backdrop-filter: blur(15px);
}

.ytp-title-show-expanded .ytp-title-channel{
    background: rgba(23,23,23,0.4);
    
}

.ytp-videowall-still:hover{
    transform: scale(1.05);
    z-index: 2000;
}

.ytp-videowall-still{
    transition: transform ease 0.2s!important;
}

ytd-miniplayer[active]{
    background: rgba(23,23,23,0.4);
    backdrop-filter: blur(15px);
}

#card.ytd-miniplayer{
    background: rgba(0,0,0,0);
}

