css:
@-moz-document domain("justpaste.it")
{
    body {
        background: #2c3e50;
    }
    #mainTableContent {
        background: #2c3e50;
        color: #bdc3c7;
    }
    .noteFotter {
        color: #7f8c8d;
        display: block;
        border-top: 1px solid rgba(127, 140, 141, 0.2);
    }
    #footer {
        background: #21313d;
    }
    a,
    a:hover,
    a:focus {
        color: rgb(248, 223, 92);
    }

    /* iframe */
    .mce-panel {
        background: rgb(33, 49, 62);
        border:  0;
    }

    div.mce-edit-area {
        background: #34495e;
    }

    .mce-btn {
        background: transparent !important;
        border:  0;
    }
    body#tinymce {     background: #34495e;
    color: #bdc3c7; }

    .mce-btn button,
    .mce-btn button i,
    .mce-menubtn,
    .mce-menubtn button {
        color:  #bdc3c7 !important;
    }
    .mce-btn button:hover,
    .mce-btn button:hover i,
    .mce-menubtn button:hover {
        color:  #f8df5c !important;
    }

    .mce-btn-group:not(:first-child) {
        border-left-color: rgba(189, 195, 199, 0.27);
    }

    /* other pages */
    .alert.alert-warning {
        background: #34495e;
        border:  0;
    }

    .linkDesc,.linkDesc:hover, .linkDesc:focus {
        color: #bdc3c7;
    }
    p {
        background:  transparent !important;
    }
    p span {
        color: rgb(189, 195, 199) !important;
    }
    form > div > div {
        background: #34495e !important;
        border: 0 !important;
    }
}
