css:
@-moz-document domain("trello.com") {
  .list {
    background: #f1f1f1;
  }
  .u-fancy-scrollbar-webkit-scrollbar {
    height: 4px;
    width: 4px;
  }
  .u-fancy-scrollbar-webkit-scrollbar-track-piece {
    background: #ededed;
  }
  .open-card-composer {
    padding: 16px 18px 20px 18px !important;
    padding-left: 16px !important;
  }
  .list-wrapper {
    width: 280px;
  }
  .list-header {
    padding: 16px 16px 16px 16px;
  }
  .list-total {
    font-size: 12px;
    position: absolute;
    top: 15px;
    right: 40px;
  }
  .list-header-extras {
    position: absolute;
    right: 12px;
    top: 12px;
  }
  .list-card {
    border: 1px solid #ddd;
    margin-bottom: 4px;
  }
  .list-card-details {
    padding: 10px 8px;
  }
  .list-card-title {
    margin: 0px 6px;
    line-height: 24px;
  }
  .active-card {
    background-color: #f1f1f1 !important;
    opacity: 1;
  }
  .icon-sm.icon-edit.list-card-operation.dark-hover.js-open-quick-card-editor.js-card-menu {
    opacity: 0;
  }
  .list-card-labels {
    margin: 4px;
  }
  .card-label {
    border-radius: 2px;
    margin: 2px !important;
    padding: 2px 6px !important;
    line-height: 18px !important;
    height: auto !important;
  }
  .card-label-green {
    background-color: #badc58;
  }
  .card-label-yellow {
    background-color: #f9ca24;
  }
  .card-label-purple {
    background-color: #e056fd;
  }
  .card-label-orange {
    background-color: #f0932b;
  }
  .card-label-sky {
    background-color: #7ed6df;
  }
  .card-label-pink {
    background-color: #ff7979;
  }
  .window {
    background-color: #fff;
    width: 760px;
  }
  .card-detail-window {
    background: #fff;
  }
  .window-cover {
    box-shadow: none;
    border-bottom: 1px solid #ddd;
  }
  .window-header-icon {
    display: none;
  }
  .window-header {
    margin: 32px;
  }
  .card-detail-window .window-main-col {
    margin-left: 0px !important;
    padding: 8px 8px 8px 32px;
    width: calc(100% - 248px);
  }
  .mod-card-back-title.js-card-detail-title-input {
    margin: 2px 8px;
    width: 100%;
  }
  .window-sidebar {
    padding: 8px 32px 8px 8px;
  }
  .checklist-items-list {
    width: 500px;
    margin: 16px 0 16px 32px;
  }
  .checklist-progress-bar-current-complete {
    background: #badc58;
  }
  .checklist-progress-bar-current {
    background: #badc58;
  }
  .checklist-progress-bar {
    background: #f1f1f1;
  }
  .checklist-item-fade-out .checklist-item-checkbox,
  .checklist-item-state-complete .checklist-item-checkbox {
    background-color: #badc58 !important;
    border-color: #ddd;
    border-bottom-color: #ddd;
  }
  .checklist-item-state-complete .icon-lg,
  .checklist-item-state-complete .icon-sm {
    color: #fff;
  }
  .checklist-item-fade-out .checklist-item-details-text,
  .checklist-item-state-complete .checklist-item-details-text {
    color: inherit;
    font-style: inherit;
    text-decoration: none;
  }
  .js-badges {
    display: inline-block;
    margin: 4px;
    padding: 0;
  }
  .badges {
    padding: 0;
    display: flex;
    justify-content: flex-start;
    flex-wrap: wrap;
  }
  .badge {
    margin: 2px;
    padding: 2px 3px;
    background: #f1f1f1;
    border-radius: 2px;
  }
  .badge-text {
    padding-right: 3px;
  }
  .badge.badge-points.point-count {
    background-color: #22a6b3 !important;
    color: #fff !important;
    background-image: none !important;
    padding: 2px 6px !important;
    font-weight: bold;
    height: 18px;
    margin: 6px -2px 0 6px;
  }
  .badge.badge-points.point-count.consumed {
    background-color: #7ed6df !important;
    background-image: none !important;
    color: #fff !important;
    opacity: 1 !important;
    font-weight: 400;
    font-weight: bold;
    order: -1;
  }
  .list-total .points {
    color: #22a6b3 !important;
  }
  .list-total .cpoints {
    color: #7ed6df !important;
    opacity: 1 !important;
  }
  .card-detail-due-date-badge.is-clickable .card-detail-due-date-text {
    text-decoration: none;
  }
  .card-detail-badge.is-due-complete,
  .is-due-complete.card-detail-badge.is-clickablehover,
  .badge.is-due-complete {
    background-color: #f1f1f1;
    border-radius: 2px;
    color: #838c91;
  }
  .is-due-complete .icon-sm.icon-check.card-detail-badge-due-date-complete-icon,
  .badge.is-due-complete .badge-icon.icon-sm.icon-clock {
    color: #a3a3a3;
  }
  .is-due-complete .card-detail-badge-due-date-complete-box.js-card-detail-due-date-badge-complete.js-details-toggle-due-date-complete {
    border-color: #a3a3a3;
  }
  .is-due-future.card-detail-badge.is-clickablehover,
  .card-detail-badge.is-due-future,
  .badge.is-due-future {
    background-color: #f9ca24;
    color: #fff;
    border-radius: 2px;
  }
  .badge.is-due-future .badge-icon.icon-sm.icon-clock {
    color: #fff !important;
  }
  .is-due-past.card-detail-badge.is-clickablehover,
  .card-detail-badge.is-due-past,
  .badge.is-due-past {
    background-color: #eb4d4b;
    border-radius: 2px;
  }
  .is-due-soon.card-detail-badge.is-clickablehover,
  .card-detail-badge.is-due-soon,
  .badge.is-due-soon {
    background-color: #f0932b;
    border-radius: 2px;
  }
  .list-card-dropzone,
  .list-card-dropzone-limited,
  .list-card-membersempty {
    display: none !important;
  }
  .list-card-members {
    margin: 0 4px 4px 4px !important;
    float: left;
    width: 100%;
  }
  .member.js-member-on-card-menu {
    float: left;
    margin: 2px !important;
    width: 32px;
    height: 32px;
    background: transparent;
  }
  .member.js-member-on-card-menu .member-avatar {
    float: left;
    border: 1px solid #ddd;
    border-radius: 2px;
  }
}
