css:
@-moz-document domain("smarkets.com") {
#header {
    background: #313131;
}

#header {
    box-shadow: 0 1px 12px 0 #00b073;
}

.theme-dark #content-wrapper {
    color: #ffffff;
    background-color: #191919; 
}

.theme-dark #right-nav .right-nav-section {
    border-bottom: .3rem solid #1c1d1d;
}

.theme-dark .right-nav-section .right-nav-section-header {
    background-color: rgb(0, 176, 115);
}

.theme-dark .right-nav-section .right-nav-section-content {
    background-color: #1c1d1d !important;
}

.theme-dark #user-bets thead {
    background: #353535;
    padding: .5rem;
}

.theme-dark #user-bets .user-bet:nth-child(2n) {
    background-color: #1f1f1f;
}

.theme-dark #user-bets .user-bet:nth-child(odd) {
    background-color: #2b2b2b;
}


.theme-dark .contract-group .ui-collapsible>.header {
    background-color: #1a1a1a;
}

.theme-dark .contract-groups .contract-group {
    border-bottom: 2px solid #00b073;
}


.theme-dark .overround-info {
        background-color: #2a2b2b;   
}


.theme-dark .contract-wrapper:nth-child(odd) {
    background-color: #2a2b2b;
}

.theme-dark .contract-wrapper:nth-child(2n) {
    background-color: #1c1d1d;
}


.theme-dark #left-nav {
    background: #2a2b2b;
}

.theme-dark #right-nav {
    background: #2a2b2b;
    color: #ffffff;
}

.theme-dark #footer {
    background: #131313;
    color: #a6a6a6;
}

.theme-dark .listing-header {
    background-color: #1c1d1d;
}

.theme-dark .event-listing-item:nth-child(odd) {
    background-color: #2a2b2b;
}

.theme-dark .event-listing-item:nth-child(2n) {
    background-color: #1c1d1d;
}

.theme-dark .overround-info {
    background-color: #00b073;
    color: #fff;
}

.theme-dark .menu-item>.header {
    border-bottom: 1px solid hsla(159, 100%, 35%, 0.66);
    border-right: 1px solid hsla(159, 100%, 35%, 0.66);
}

.theme-dark #content-wrapper #main-content {
    background-color: #1c1d1d;
}

.theme-dark .search-input-panel.-desktop .input-wrapper, .theme-dark .search-input-panel.-hugeDesktop .input-wrapper, .theme-light .search-input-panel.-desktop .input-wrapper, .theme-light .search-input-panel.-hugeDesktop .input-wrapper {
    background-color: #1c1d1d;
}


.theme-dark #header .separator, .theme-light #header .separator {
    border-left: .25em solid #313131;
}


#main-content > main {
     border-right: 1px solid hsla(159, 100%, 35%, 0.66)!important;
}

.theme-dark .contract-wrapper:nth-child(odd) {
     border-top: 1px solid hsla(159, 100%, 35%, 0.66)!important;
     border-bottom: 1px solid hsla(159, 100%, 35%, 0.66)!important;
}

#popular-events > div.right-nav-section-content > ul > li {
     border-top: 1px solid hsla(159, 100%, 35%, 0.66)!important;
     border-bottom: 1px solid hsla(159, 100%, 35%, 0.66)!important;
}

#main-content > main > div > div.event-header.-horse-racing > div > div.event-info.-desktop > div.info.-upcoming > div.event-badges {
     border-top: 1px solid hsla(159, 100%, 35%, 0.66)!important;
}

#top-horse-navigation > div.top-navigation.long > ol {
    border-bottom: 1px solid hsla(159, 100%, 35%, 0.66)!important;
}

.live-chat .live-chat-link:hover {
    background-color: #212121 !important;
}

.theme-dark #right-nav {
    background: #1c1d1d!important;
    color: #ffffff!important;
}

#main-content > main > div > div.event-header.-horse-racing > div > div > div.info.-complete > div {
     border-top: 1px solid hsla(159, 100%, 35%, 0.66)!important;
}


/* Alternative Scroll Bar */
::-webkit-scrollbar
{
  width: 12px;  
  height: 12px; 
}


/* Alternative Scroll Bar Background Color*/
::-webkit-scrollbar-track
{
  background: #191919;
}


/* Alternative Scroll Bar Slider Color */
::-webkit-scrollbar-thumb
{
  background: #00b073;
}

}
