css:
@-moz-document domain(sysvis.dropforge.io)
{
/* Replacing #006ebe with #23527C */

.term__select--circle .selector--circle {
    border-color: #23527C !important;
}
/* Replacing #006ebe as universal link color */

body {
    background: #ebf0f5 !important;
}
a,
div {
    transition: all 0.2s ease-in;
}
a:hover,
div:hover {
    transition: all 0.2s ease-out;
}
span a,
p a,
ul.mega-menu h1.h6 a {
    color: #337ab7 !important;
}
.landing__call-to-action-one.bg-blue {
    background: -webkit-radial-gradient(center, ellipse cover, #0099cb 1%, #0c2646 100%);
    background: radial-gradient(ellipse at center, #0099cb 1%, #0c2646 100%);
}
div.landing__header.cell.bg-teal {
    background: -webkit-linear-gradient(right, #50bebe 0, #41aaaa 50%, #0099cb 100%) !important;
    background: linear-gradient(to left, #50bebe 0, #41aaaa 50%, #0099cb 100%) !important;
}
.views-view--featured-content h3 {
    color: #879baa;
}
h2.block-title {
    color: #2d373c;
}
div.landing__header.cell.bg-teal a.button.clear {
    color: white;
}
.block-electron-main-menu .top-bar-search .form-item-keywords input {
    border-bottom: 1px solid #879baa !important;
}
#edit-submit-search svg {
    fill: #879baa;
}
div.landing__header.cell.bg-teal a.button.warning.cta-button {
    background: #faa50a !important;
    box-shadow: none !important;
    transition: background-color 0.3s ease;
}
div.landing__header.cell.bg-teal a.button.warning.cta-button:hover {
    background: #eb780a !important;
    box-shadow: none !important;
    color: white !important;
    transition: background-color 0.3s ease;
}
.block-electron-main-menu .top-bar-left {
    background: #009999 !important;
}
.block-electron-main-menu .top-bar-left:hover {
    background: #41aaaa !important;
}
.block-electron-main-menu .top-bar-right>div {
    background: white !important;
}
.block-region-feature-secondary-one h1 span {
    transition: all 0.3s ease-in;
}
.block-region-feature-secondary-one h1 span.slick-current {
    transition: all 0.3s ease-out;
    text-shadow: none !important;
    background-color: #004669;
    color: #02baf9 !important;
}
.block-region-feature-secondary-one h1 span.slick-current:hover {
    background: #faa50a;
    color: #2d373c !important;
}
.landing__search.bg-prussian {
    background: #2d373c;
}
.landing__search input#edit-search-api-fulltext,
.landing__search input#edit-name {
    background: #697882;
}
.user-navigation--left,
nav.block-usernavigation li.active a {
    background: #2D3736 !important;
    box-shadow: none !important;
}
.user-navigation--right,
.views-view--my-user-nav nav {
    background: #004669 !important;
    box-shadow: none !important;
}
.button.hollow.skyblue,
.button.hollow.warning {
    border-color: #02baf9 !important;
    color: #02baf9 !important;
}
.button.hollow.success {
    border-color: #50bed7 !important;
    color: #50bed7 !important;
}
.views-view--my-user-nav .profile-completion em {
    color: #faa50a;
}
.views-view--my-user-nav div.stat a {
    background: #009999;
}
.label.skyblue {
    background: #02baf9;
}
.user-navigation--right .grid-x.cell.shrink {
    border-top: 2px solid #faa50a;
}
.user-navigation--right .cell.medium-5.subscription,
.user-navigation--right .cell.medium-5.membership {
    background: #2d373c;
}
.user-navigation--right .cell.medium-7 {
    background: #525658;
}
.user-navigation--right .field-name-field-subscription .skyblue.label {
    color: white;
    letter-spacing: 0.5px;
    text-transform: uppercase;
}
.close-icon {
    background: #525658;
}}
