    box-sizing: border-box;
    width: 317px;
    height: 324px;
    background-color: #1b1b1b;
    box-shadow: 0 2px 4px 0 rgba(12,0,51,.1);
    margin: 0 12px 24px;
    padding: 38px 18px 55px;
    text-align: center;
}

.community-section .community-section-title {
    margin: 0 0 18px;
    padding: 0;
    font-size: 36px;
    font-weight: 600;
    letter-spacing: -.04em;
    color: #ffffff;
    text-align: center;
    font-family: 'Montserrat',sans-serif;
}
}
