css:
@-moz-document domain("sdarot.tv"), domain("zira.online"), domain("sdarot.click"), domain("sdarot.pm"), domain("sdarot.wf"), domain("zira.ninja"), domain("sdarot.live"), domain("sdarot.ninja"), domain("sdarot.world"), domain("sdarot.rocks"), domain("sdarot.services"), domain("sdarot.life"), domain("sdarot.today"), domain("sdarot.pro"), domain("sdarot.dev"), domain("sdarot.website"), domain("sdarot.buzz") {
body, .form-control, #loginForm, .modal-content {
  background-color: #444;
  color: #eee;
}

.btn-default, .table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
  color: #eee;
  background-color: #666;
  border-color: #aaa;
}

#donate thead th:first-child {
  color: #ccc;
}

section.background .content, section.background:not(.rounded)::before {
  background-color: #3f3f3f;
}

#sysMsg.background .content, #sysMsg.background:before {
  background-color: #2f2f2f;
}

#sysMsg.background {
  background-color: inherit;
}

.box, .box a + span, .nav-pills > li > p, #sysMsg, #staff .box, #tracking .nav-pills > li > p {
  background-color: #a0a0a0;
  color: #fff;
}


#sysMsg ul:not(.carousel-indicators) li:nth-child(2n), .nav-pills > li.active > p, .nav-pills > li.active > p:focus, .nav-pills > li.active > p:hover, #tracking .nav-pills > li.active > p, #tracking .nav-pills > li.active > p:focus, #tracking .nav-pills > li.active > p:hover {
  background-color: #ccc;
}

#queue h4 > a, #queue h5 > a, #tracking h4 > a, #tracking h5 > a, label {
  color: #fff;
}

.help-block {
  color: #bcbcbc;
}

.btn.focus:not(.nontrans), .btn:focus:not(.nontrans), .btn:hover:not(.nontrans) {
  background: #388fdd;
}

.content h4, a {
  color: #a4ccf0;
}

a:focus, a:hover {
  color: #667481;
}

#donate table tr td:first-child {
  background-color: #888;
  color: #eee;
}

.dropdown-menu {
  background-color: #444;
}

.dropdown-menu > li > a {
  color: #eee;
}

.progress {
  background-color: #888;
}

.btn-white {
  background-color: #666;
  color: #fff;
}

.btn-text-red {
  color: red;
  border-color: #555;
}

.btn-text-green {
  color: #95cc00;
  border-color: #555;
}

.close {
  color: #fff;
  opacity: .4;
}

#loginForm .form-control {
  background-color: #444;
  color: #eee;
}
}
