css:
@-moz-document url-prefix("https://bato.to/") {
body,
._bGj,
#_V8i ._kkj,
._mBi,
._x4i,
._W8i,
._Iqg {
    background: #1d1d1d !important;
    color: #828589 !important;
}
pre {
    display: block;
    font-size: 87.5%;
    color: #828589;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    margin-bottom: 0.5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.2;
    color: #828589;
}
#series-page .title-set .item-title a {
    color: #DDDEDE;
}
.hairlines-fade-bottom, .line-b-f {
    position: relative;
    color: #DDDEDE;
}
b, strong {
    font-weight: bolder;
    color: #DDDEDE;
}
#series-page .chapter-list .main .item.is-new {
    background-color: #364835;
}
.alert-secondary {
    color: #383d41;
    background-color: #0E5463;
    border-color: #d6d8db;
}
}
