css:
@-moz-document domain("winterparadox.com") {
.nav-wrap {
    background-color: #2c2c36;
}
a:nth-child(n) {
    color: #d2dcf2
}
a:nth-child(n):hover {
    color: #b7c8ea
}
li.isotope-item:nth-child(n) > a:nth-child(2) > h2:nth-child(1) {
    font-size:18px;
    color: #fff
}
li.isotope-item:nth-child(n) > a:nth-child(2) > h2:nth-child(1):hover {
    color: #b7c8ea
}
.page-heading > div:nth-child(1) > h1:nth-child(1),
.wpb_text_column > div:nth-child(n) > div:nth-child(n) > h2:nth-child(n),
.ninja-forms-form-title{
    color: #fff
}
.breadcrumbs {
    color: #fff
}
h3:nth-child(1),
strong:nth-child(n){
    color: #fff
}
div.vc_row:nth-child(4),
p:nth-child(n),
.copyright-footer,
.normal,
ol:nth-child(n){
    color: #90949c
}
#menu-item-275 > a:nth-child(1),
#menu-item-278 > a:nth-child(1),
#menu-item-279 > a:nth-child(1),
#menu-item-280 > a:nth-child(1){
    color: #fff
}

#menu-item-275 > a:nth-child(1):hover,
#menu-item-278 > a:nth-child(1):hover,
#menu-item-279 > a:nth-child(1):hover,
#menu-item-280 > a:nth-child(1):hover {
    color: #b7c8ea
}
.site-logo > a:nth-child(1) > img:nth-child(1){
    filter: contrast(0) brightness(1.5);
}
.site-logo > a:nth-child(1) > img:nth-child(1):hover{
    filter: contrast(0) brightness(2);
    
}
.site-wrapper {
    background-color: #23232D;
}
.page-heading {
    background-color: #26262F;
}
.site-footer {
    background-color: #2c2c36;
}
a.vc_general.vc_btn3.vc_btn3-size-lg.vc_btn3-shape-rounded.vc_btn3-style-3d.vc_btn3-icon-left.vc_btn3-color-black {
    box-shadow: 0 5px 0 #2c2c36;
    background-color:#3b3b4b
}
a.vc_general.vc_btn3.vc_btn3-size-lg.vc_btn3-shape-rounded.vc_btn3-style-3d.vc_btn3-icon-left.vc_btn3-color-black:hover {
    box-shadow: 0 2px 0 #2c2c36;
    background-color:#3b3b4b
}

}
