@-moz-document url-prefix("https://schoolido.lu/") {
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #BBBBBB;
    background-color: #1d1d1d;
}
.panel-Smile > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #BBBBBB;
}
.panel-Pure > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #333;
}
.panel-Cool > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #333;
}
.panel-Smile > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #333;
}
