    background: #1d1d1d;
    overflow: hidden;
    color: #BBBBBB;
}
.panel-Pure > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #BBBBBB;
}
.panel-Cool > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #BBBBBB;
}
.panel-All > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #BBBBBB;
}
.panel-default > .panel-body {
    background: #1d1d1d;
    overflow: hidden;
    color: #BBBBBB;
}
.panel-All > .panel-footer {
    color: #8242c6;
    background-color: #E272F9;
    border-top-color: #c3a4e4;
}
.jumbotron {
    padding-top: 30px;
    padding-bottom: 30px;
    margin-bottom: 30px;
    color: inherit;
    background-color: #1a1a1a;
}
.modal-body {
    position: relative;
    padding: 15px;
    background: #1d1d1d;
}
.modal-footer {
    padding: 15px;
    text-align: right;
    border-top: 1px solid #000;
    background: #1d1d1d;
}
.modal-header {
    min-height: 16.43px;
    padding: 15px;
    border-bottom: 1px solid #000;
    background: #1d1d1d;
}
.close {
    float: right;
    font-size: 21px;
    font-weight: 700;
    line-height: 1;
    color: #BBB;
    text-shadow: 0 1px 0 #fff;
    filter: alpha(opacity=20);
    opacity: .2;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #555;
    cursor: default;
    background-color: #1a1a1a;
    border: 1px solid #ddd;
    border-bottom-color: transparent;
}
.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #1a1a1a;
    border: 1px solid #e3e3e3;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
}
input[type="text"], input[type="email"], input[type="number"], input[type="password"], select {
    background: #111;
    border: 3px solid #b00964;
    border-radius: 7px;
    padding: 5px;
    color: #b00964;
    width: 100%;
    margin: 10px 0;
}
.dark .textarea {
    border-color: #bbbbbb;
    border-radius: 4px;
    background: #111;
}
.dark .input--text {
    background: #111;
    box-shadow: none;
    border-color: #e7e9ee;
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #555;
    background-color: #111;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
#home #myactivities, #home #activities, #home #hotactivities, #home #newactivities {
    margin-top: 30px;
    background: #1d1d1d;
    padding: 10px;
    border-radius: 10px;
}
.padding10 {
    padding: 10px;
    background: #1d1d1d;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    text-align: left;
    list-style: none;
    background-color: #1d1d1d;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid #ccc;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: 4px;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,.175);
    box-shadow: 0 6px 12px rgba(0,0,0,.175);
}
.navbar-Smile .dropdown-menu > li > a {
    color: #BBB !important;
}
.navbar-Pure .dropdown-menu > li > a {
    color: #BBB !important;
}
.navbar-Cool .dropdown-menu > li > a {
    color: #BBB !important;
}
.navbar-All .dropdown-menu > li > a {
    color: #BBBBBB !important;
}
.navbar-default .dropdown-menu > li > a {
    color: #BBBBBB !important;
}
}
