css:
/* i really want this to be global */
.navbar-default {
    background-color: #f57c00;
    border-color: transparent;
}

.btn-default {
    color: #fff;
    background-color: #f57c00;
    border-color: #f57c00;
}

body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #6e6e6e;
    background-color: #fff;
}

html {
    background-color: #f5f5f5;
}

.btn-success {
    color: #fff;
    background-color: #03A9F4;
    border-color: #03A9F4;
}
.btn-success:hover {
    color: #fff;
    background-color: #03A9F4;
    border-color: #03A9F4;
}
.btn-success:focus {
    color: #fff;
    background-color: #03A9F4;
    border-color: #03A9F4;
}

.btn-danger {
    color: #fff;
    background-color: #9E9E9E;
    border-color: #9E9E9E;
}

.btn-danger:hover {
    color: #fff;
    background-color: #9E9E9E;
    border-color: #9E9E9E;
}
.btn-danger:focus {
    color: #fff;
    background-color: #9E9E9E;
    border-color: #9E9E9E;
}
