css:
@-moz-document domain("discogs.com") {
#header_logo {
    width: 122px;
}
#header_logo img {
    width: 122px;
    content: url('https://i.imgur.com/W5SH74J.png') !important;
}
html, button, input, select, textarea {
    font-family: "Segoe UI","Helvetica Neue",Helvetica,"Nimbus Sans",Arial,sans-serif;
}
#site_headers_super_wrap {
    font-family: "Segoe UI","Helvetica Neue",Helvetica,"Nimbus Sans",Arial,sans-serif;
    font-weight: bold;
}
#site_search {
    background: #555;
    border: 1px solid #484848;
    border-radius: 3px;
}
#site_search:hover, .search_focused #site_search {
    background: #666;
}
#site_search #do_site_search i {
    color: #BBB;
}
#site_search #do_site_search:hover i {
    color: #FFF;
}
#site_search #search_q {
    color: #FFF;
    font-family: "Segoe UI","Helvetica Neue",Helvetica,"Nimbus Sans",Arial,sans-serif;
}
#site_footer {
    font-family: "Segoe UI","Helvetica Neue",Helvetica,"Nimbus Sans",Arial,sans-serif;
}
#site_footer_legal {
    font-family: "Tahoma","Helvetica Neue",Helvetica,"Nimbus Sans",Arial,sans-serif;
}
.site_nav_group.opened {
    background: #555;
}
.site_nav_submenu, .site_nav_tray {
    background: #555;
}
.site_nav_submenu a, .site_nav_tray a, .site_nav_submenu a:visited, .site_nav_tray a:visited {
    color: #BBB;
}
.site_nav_submenu a i, .site_nav_tray a i, .site_nav_group.opened i {
    color: #BBB;
}
.site_nav_submenu a:hover i, .site_nav_tray a:hover i {
    color: #FFF;
}
.site_nav_submenu a:hover, .site_nav_tray a:hover {
    color: #FFF;
}
.site_nav_tray h4 {
    color: #FFF;
}
.muted, .broadcast_table .gutter .icon_wrapper, .facet_count, table.table_block td.empty_row {
    color: #888;
}
.facet-bar {
    background-color: #EEE;
}
}
