css:
@-moz-document domain("gotoshop.net.ua") {
html,
body,
#wrapper,
#content {
    background-color: black;
    color: white;
}
.shops-slider .shops-slider-inner {
    background-color: #b3b3b3;
}
.shops-slider-wrapper .container {
    background-color: #111f5f;
}
.item .info,
.product .info {
    background-color: #adf;
}
.discounts .item {
    background-color: #adc;
}
header.navbar,
header.navbar .container,
header.navbar .container .collapse.navbar-collapse,
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav,
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li,
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li > a {
    min-height: 20px !important;
    max-height: 20px !important;
    line-height: 20px !important;
}
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li > a {
    margin: 0 10px;
    padding: 0;
}
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li > a > span {
    margin: 0 5px;
}
#header {
    margin: 0;
    padding: 0;
    width: 100%;
}
#header,
#header .container,
#header .container .selectors-wrapper,
#header .container .selectors-wrapper .selectors,
#header .container .selectors-wrapper .selectors .selector,
#header .container .selectors-wrapper .selectors .selector .dropdown a.dropdown-toggle {
    min-height: 28px;
    max-height: 28px;
}
#header .container .selectors-wrapper .selectors .selector,
#header .container .selectors-wrapper .selectors .selector .dropdown,
#header .container .selectors-wrapper .selectors .selector .dropdown .btn-group-search,
#header .container .selectors-wrapper .selectors .selector .dropdown .btn.dropdown-toggle {
    margin: 0;
    padding: 0;
    min-height: 20px;
    max-height: 20px;
}
#header .container .selectors-wrapper .selectors .selector {
    margin-right: 20px;
}
#header .container .selectors-wrapper .selectors .selector .dropdown a.dropdown-toggle span {
    vertical-align: inherit;
    min-height: 20px;
    max-height: 20px;
}
#header .logo {
    display: none;
}
#header .selectors-wrapper {
    margin: 0;
    padding: 0;
}
.shops-slider-wrapper .container {
    width: 100%;
}
#content .main-title {
    display: none;
}
#content .container {
    width: 95% !important;
}
#content .form-filters {
    padding: 5px 0;
    min-height: 30px;
    max-height: 30px;
}
#content .form-filters .btn.btn-default,
#content .form-filters .btn.btn-link {
    min-height: 20px;
    max-height: 20px;
    padding: 0 5px;
}
.form-filters .pull-left,
.form-filters .pull-right {
    padding: 0;
}
#discounts .item {
    position: relative;
    transition: none;
}
#discounts .item .info {
    position: absolute;
    top: 20px;
    left: 0;
    width: 100%;
}
#discounts .item .info,
#discounts .item .info .shops,
#discounts .item .info .shops a {
    min-height: 0px;
    max-height: 0px;
    padding: 0;
    margin: 0;
}
#discounts .item .info .discount-link {
    display: none;
}
#discounts .item .image-link {
    padding-top: 20px;
}
#discounts .item .info .shops a img {
    position: absolute;
    top: 0;
    left: 0;
    height: 50px;
    width: 50px;
    margin: 5px;
    background: white;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
    border-radius: 50px;
    z-index: 5;
    opacity: 0.2;
    transition: all 0.2s ease-in-out 0.2s;
}
#discounts .item .info .shops a img:hover {
    opacity: 1;
    border-radius: 5px;
    width: 70px;
    height: 70px;
}
#discounts .item .info .btn-add-note {
    position: absolute;
    top: 0;
    right: 0;
    z-index: 5;
    margin: 0;
    padding: 5px;
}
#discounts .item .image-link .over {
    display: none;
}
#discounts .item .product-actions {
    padding: 0;
    border-top: 1px solid #777;
    background: rgba(0, 0, 0, 0.05);
}
#discounts .item .discount-days {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 20px;
    line-height: 20px;
    background-size: 15px 15px;
    background-position: 5px 2px;
    font-size: 12px;
    padding-left: 0;
    text-align: center;
}
.hidden-print.hidden-xs,
.share-item {
    display: none;
}
html,
body,
#wrapper,
#content {
    height: 100%;
}
.badge {
    color: #e5ff0e;
    background-color: #aaddff;
}
.comments-list li.list-group-item:hover {
    background-color: rgb(0, 255, 8);
    color: black;
}
.comments-list li.list-group-item {
    color: red;
}
.product h3 {
    background-color: #bbbaba;
}
.comments-list li.list-group-item {
    background-color: #c1c1c1;
}
*:before,
*:after {
    background-color: #d41414;
}
.form-control {
    background-color: #38ffda !important;
    ;
    color: black !important;
}
div.selected-shop.clearfix {
    background-color: #0a6b1d !important;
}
div.discount-item a {
    color: #ffffff !important;
}
div.discount-item small {
    color: #00ffff !important;
}
div.discount-item strong {
    color: #ffd500 !important;
}
a.btn-loginnewwindow.btn-link2.add-shop-to-favorites {
    color: #ff0000 !important;
}
.fixed-tabs > ul > li {
    display: none !important;
}
}
