css:
@-moz-document domain("toot.si"), domain("mastodon.social"), domain("pawoo.net"), domain("mstdn.jp"), domain("mastodon.cloud"), domain("friends.nico")  {
/* fixes for mastodon web low contrast text 2018-04-12 */

body.app-body {
    background: black;
}
.ui {
    background: transparent;
}
.column > .scrollable {
    background: #191b22;
}
.search__input {
    color: white;
    background-color: #191b22;
    opacity: 1;
}
.search__icon .fa {
    color: white;
}
.search__icon .fa.active {
    opacity: 0.5;
}
.drawer__tab {
    color: white;
}
.status__display-name {
    color: #9baec8;
}
.status__relative-time {
    color: #9baec8;
}
.account__disclaimer {
    color: #9baec8;
}
.muted .status__content a,
.muted .status__content p,
.muted .status__display-name strong {
    color: #9baec8;
}
.status__prepend {
    color: #9baec8;
}
.status__prepend .status__display-name strong {
    color: #9baec8;
}
.column-subheading {
    color: #9baec8;
}
.getting-started a {
    color: #9baec8;
}
.icon-button {
    color: #9baec8;
}
}
