@-moz-document regexp("https://boardgamegeek.com/(?!advsearch|geeklist|image|geekmarket|geekquestions|geekaccount).*") {
body,
html {
    background: #92969d;
    font-family: helvetica, arial;
}
.profile_block {
    background: #fff;
}
table.innermoduletable {
    background: white;
}
html,
body,
table,
td,
tr,
div,
span,
a,
b,
strong,
li,
ul,
ol,
i {
    font-size: 14px;
}
a:link,
a:visited {
    color: #000;
}
a:hover {
    color: #292e62;
}
a {
    font-weight: 100;
}
div#header {
    min-width: 1070px !important;
    background: #ededed;
    border: none;
    color: #095f72;
    height: 50px;
}
#simpleCarousel,
.bggstore_widget_controls {
    width: auto;
}
.bggstore_widget_slide {
    position: absolute;
    width: 210px;
}
#simpleCarousel {
    background: #fff;
}
.bggstore_widget_controls a:link {
    color: #ffffff;
}
.searchmenu,
.menucontainer,
.mainmenu {
    background: transparent !important;
    float: left;
    width: auto;
}
#header_top {
    height: auto;
}
.searchmenu {
    float: right;
}
.mainmenu {
    margin-top: -30px;
    width: 100%;
}
.mainmenutabs li a {
    font-weight: 100;
}
.mainmenutabs li {
    width: 23%;
}
#logout_label {
    text-align: right;
}
.mainmenutabs,
.mainmenutabs li a.selected {
    background: transparent;
}
li#bggcon_label {
    display: none;
}
.fl {
    color: #292e62;
}
.geekmodule tr {
    border-bottom: 1px solid #ccc;
}
.geekmodule tr:nth-child(odd) {
    background: #fff;
}
.menu_quickbar {
    background: #292e62;
    width: 280px;
    color: #fff;
    border: none;
    font-size: 10px;
    padding: 5px;
    margin-left: 20px;
}
.menu_quickbar a {
    color: #fff;
}
.quickbar_pager a {
    margin-right: 5px;
}
.quickbar_pager a:last-child {
    color: #ff0000;
}
.menu_login {
    background: #000328;
    border: none;
}
.menu_login table a,
.menu_login div.mt5 a {
    color: #ddd;
}
.searchmenutabs input[type="submit"] {
    background-color: #292e62;
    color: white;
    height: 28px;
    width: 50px;
    font-size: 15px;
    border: none;
}
.searchmenutabs {
    margin-top: 10px;
}
input#sitesearch {
    height: 28px;
    font-size: 12px;
    line-height: 15px;
}
.submenutabs {
    margin-top: 10px;
}
a#geek-logout {
    color: #ff0000;
    float: right;
}
#quickbar_label {
    display: none;
}
div#container {
    margin: auto;
    width: 1300px;
}
.sf.ml10 .ng-binding a {
    font-weight: normal;
    font-size: 12px;
}
.innermoduletable td {
    padding: 10px 5px;
}
.recentitems.moduletable .innermoduletable td {
    padding: 0px;
}
.recentitems.moduletable .innermoduletable td a {
    padding: 10px;
    float: left;
    width: 190px;
}
.moduletable th a {
    color: lightblue;
    font-weight: bold;
}
.hotitems,
.recentitems {
    width: 210px;
}
.recentitems.moduletable > tbody > tr:nth-of-type(2) > td > .innermoduletable > tbody > tr > td,
.hotitems .innermoduletable td {
    background: #fff !important;
    border-bottom: #ccc 1px solid;
}
div#maincontent table:nth-child(1) {
    margin: 0 !important;
}
div#maincontent div.menu_login table {
    margin: auto !important;
}
div#maincontent table:nth-child(1) > td:nth-child(1) {
    min-width: 200px;
}
div#maincontent table:nth-child(2) > td:nth-child(1) {
    min-width: 200px;
}
div#dfp-leaderboard {
    margin: 0 !important;
}
.recentitems {
    width: 210px;
}
.recentitems .innermoduletable {
    width: 210px;
}
.recentitems .innermoduletable div {
    width: 210px;
}
.recentitems .innermoduletable div a {
    font-size: 13px;
}
.pt5.ng-scope {
    background: #fff;
}
.geekcentral_rightcol div.geekcentral_module:first-child {
    margin: 0px !important;
}
.quickbar_link a {
    font-size: 12px;
}
div.quickbar_pager {
    margin-bottom: 0;
}
td:nth-of-type(2) > table > tbody > tr > td:nth-of-type(2) {
    display: none;
}
table.collection_table {
    background: #fff;
}
.geekcollection_pager {
    background-image: none;
    background-position: 0px;
    background-repeat: repeat-x;
    color: #282828;
    background-color: #f9f9f9 !important;
    width: 30%;
    display: inline-block;
    float: right;
}
#maincontent>table:first-child td {
    align-self: flex-end;
}
#quickbar {
    display: none;
}
.searchmenutabs li a,
.searchmenutabs li a:visited {
    color: #292e62;
}
.searchmenutabs li a,
.searchmenutabs li a:hover {
    color: #292e62;
}
#maincontent>table:first-child>tbody>tr {
    display: inline-flex;
    flex-direction: row-reverse;
    background-color: #fafafa;
}
.menutab-open {
    background: #f3f3f3;
}
.menutab-open li a {
    font-weight: 100;
}
.submenutabs li a.open {
    background: #e5e5e5;
}
#collection .sf {
    font-size: 12px;
}
#collection {
    padding-top: 10px;
    background-color: #f9f9f9;
}
#collection .sf:not(.fl) {
    text-align: left;
    margin-bottom: 5px;
    width: 60% !important;
    display: inline;
    margin-left: 30px;
}
.forum_table td {
    background-color: #f5F5F5;
}
.forum_table {
    margin-top: 5px;
}
.forum_table>tbody>tr:first-child {
    display: inline-flex;
}
.forum_table>tbody>tr {
    align-self: center;
}
.forum_table>tbody>tr>td:nth-child(2) {
    background-color: #82d212;
    order: 1;
    margin-right: 40px;
    margin-left: 15px;
}
.forum_table>tbody>tr>td:nth-child(1) {
    order: 3;
}
.forum_table>tbody>tr>td:nth-child(3) {
    order: 2;
    margin-right: 360px;
}
.forum_table>tbody>tr>td:nth-child(2) a img {
    display: none;
}
.forum_table>tbody>tr>td:nth-child(2) a {
    padding: 0 10px;
    color: white;
    font-weight: 700;
}
.forum_table>tbody:nth-child(1) {
    display: flex;
    background-color: #f5F5F5;
    justify-content: space-between;
    height: 50px;
}
.editfield:hover {
    background-color: #e7f3f7;
}
.collection_handle,
.item_handle,
.copy_handle,
.collectionfilter_commandbar {
    background: #b4d9e7;
}
.searchbox_results {
    background-color: #f9f9f9;
    border: 1px solid #ebebeb;
}
.searchbox_results > #instantsearchresults5acf3316e1fca> div {
    color: red !important;
    font-size: 12px;
}
.collection_filters {
    position: absolute;
}
#views,
#filters {
    right: 0;
}
.collection .geekcollection_pager:nth-child(2) {
    width: 100%;
}
.moduletable th {
    height: 40px;
}
.infobox,
.messagebox,
.popupbox {
    border: 2px solid #ffffff;
    color: #514721;
    margin-bottom: 0px;
    background: white;
}
.hotitems th {
    background-image: none;
    background-color: #882721;
}
.recentitems th {
    background-image: none;
    background-color: #31a80a;
}
.tabOn,
.tabOff {
    height: 40px;
    line-height: 40px;
    background-image: none;
}
#main_content>.simplebox>table>tbody>tr .menu_table:first-child .tabOn,
#main_content>.simplebox>table>tbody>tr .menu_table:first-child .tabOff {
    width: 12.4% !important;
}
.tabOn {
    background: #292e62;
    font-weight: 700;
}
.tabOff {
    background: #fff;
    font-weight: 700;
}
.tabOff A:link,
.tabOff A:hover,
.tabOff A:visited {
    color: #1c3b47 !important;
}
.tabOn A:link,
.tabOn A:hover,
.tabOn A:visited {
    color: white !important;
}
.profile_title {
    color: #292e62;
}
.profile_table th {
    background-image: none;
    background-color: #292e62;
}
.menu_table {
    background-image: none;
    background-color: #fff;
}
.geekmodule a.ng-binding {
    font-weight: 400;
}
.geekmodule .forumlink {
    float: right;
}
.moduletable th a.selected,
.moduletable th a:visited.selected {
    color: white;
}
.moduletable th {
    background-image: none;
    background-color: #292e62;
}

.spotlight_red th{
    
     background-image: none;
    background-color: #a90329;
}

.spotlight_orange th{
    
     background-image: none;
    background-color: #dc7404;
}


.geekinput {
    padding: 8px 15px;
    font-size: 12px;
    margin: 5px 5px 5px 0px;
}



}

