/* =Structure
-------------------------------------------------------------- */

html,
body,
address,
blockquote,
div,
dl,
form,
h1,
h2,
h3,
h4,
h5,
h6,
ol,
p,
pre,
table,
ul,
dd,
dt,
li,
tbody,
td,
tfoot,
th,
thead,
tr,
button,
del,
ins,
map,
object,
a,
abbr,
acronym,
b,
bdo,
big,
br,
cite,
code,
dfn,
em,
i,
img,
kbd,
q,
samp,
small,
span,
strong,
sub,
sup,
tt,
var,
legend,
fieldset {
    margin: 0;
    padding: 0;
}
html {
    -webkit-text-size-adjust: 100%;
}
.site {
    background: #6a6a6a;
    border: solid 7px #6a6a6a;
    display: block;
    margin: 0 auto;
    width: 1060px;
    max-width: 94%;
}
.site-header {
    border-bottom: solid 4px #6a6a6a;
    margin: 0 3.35%;
    min-height: 190px;
    position: relative;
    z-index: 100;
}
.site-header hgroup {
    overflow: hidden;
    padding-bottom: 0;
    margin-top: 20px;
}
#main {
    padding: 0 35px 40px;
}
#main .content-area {
    float: left;
    margin: 40px -31.6% 60px 0;
    width: 100%;
}
#main .widget-area {
    float: right;
    margin: 40px 0 60px;
    overflow: hidden;
    width: 31.6%;
}
.site-content {
    margin: 0 37.3% 0 0;
}
.site-footer {
    clear: both;
    display: block;
    width: 100%;
}
