css:
@-moz-document domain("9anime.to"),
domain("9anime.is")
{
.container {
    max-width: 80%!important;
}



body {
	font-size: 1.5rem!important;
    font-weight: 500!important;
    color: #00e0ff;
	background:url(http://images.all-free-download.com/images/graphicthumb/dark_ornate_floral_seamless_pattern_vector_542457.jpg)!important;

}

body.dark .widget.schedule .widget-body .slider .items .item.active {
    background: #f17f7f;
    color: #ddd;
}

body.dark .film-list .item .inner .name {
    color: #00fffb;
}

.alert-primary {
display:none;
}



.film-list .item
{
	width:25%!important;
	
}

.film-list .item .inner .poster {
    height: 30%!important;
}
.film-list .item .inner .name .widget.genres ul li a{
  
    font-size: 1.5rem;
    font-weight: 500;

}




.widget.slider .item {
   
    width: 100%;
    height: 600px;
    background-size: 100%!important;
}


.swiper-slide-active
{
	width: 100%!important;
}#header .head #search .inner input {
    width: 100%;
    height: 100%;
    border: 3px solid #d5d5d5;

}


#sidebar {
    float: left;
    width:100%!important;
    position: relative;
    z-index: 1;
}

#main {
    float: right;
    width: 100%!important;
    position: relative;
    z-index: 1;

}


#body #main {
    width: 100%;
    margin-right: 0;
    padding-right: 0;
    float: left;
}

.widget.genres ul li a {
    color: #555;
    font-size: 1.5rem;
    margin: 4px 10px;
    display: block;
	width:150px!important;

	
}

.widget.genres ul li {
    width:150px!important;
    float: left;
    border-right: 1px solid #eee;
}


.widget.ranking .item-top {
    height: 5%!important;
    position: relative;
}


.this-message-does-not-harm-to-you-dont-remove-it
{
	display:none;
}


/*animepage splash img*/
.widget.player #player {
    height: 600px!important;
}
/*Episode button size*/

/*Best color #a14b54 */
.widget.servers ul.episodes li a {
    display: block;
    height: 50px;
	width: 50px;
    line-height: 28px;
    min-width: 38px;
    padding: 0 8px;
	padding-top: 10px;
    text-align: center;
    font-weight: 900;

    font-size:2rem!important;
}


body.dark .widget.servers ul.episodes li a.active, body.dark .widget.servers ul.episodes li a:hover {
    background: #a14b54;
    color: #ddd;
}


body.dark .widget .widget-title .title ,.widget.info .info .head .title,.widget.info .info dl.meta dd a{
    color: #a14b54;
}

.widget .widget-title .tabs .tab.active {
    border-bottom:4px solid #a14b54;
}

/*Hide Comments on videos*/
#disqus_thread
{
display:none;
}

body.dark #header .head #logo
{
filter: hue-rotate(70deg);
}

#header .head #logo a
{
	  color: #a14b54;
	filter: hue-rotate(-70deg);
}


.quickfilter
{
width:750px;
	margin:0 auto;
	 font-size: 2rem;
    font-weight: 900;
	
}


.btn-group-sm>.btn, .btn-sm {
    padding: 3px 6px;
    font-size: 2rem;
    line-height: 1.5;
    border-radius: 2px;
}

.title
{
text-align:center!important;
}



.film-list .item .inner .name {

    color: white !important;
    font-size: 2rem;
  
}

.film-list .item:hover {
    background:#a14b54!important;
	
	
	
}


body.dark .btn-primary:hover {
    color: #fff;
    background-color: #a14b54;
    border-color: #a14b54;
}



body.dark .btn-primary {
    color: #fff;
    background-color: #ff9aa4;
    border-color:#a04b00!important;
}


/*Details on hover above anime*/
.tooltipster-box
{
	display:none!important;
	
	
}

/*HIDE TOOLTIP*/
.tooltipster-sidetip.anime .tooltipster-box .tooltipster-content .inner .title h1 , body.dark a{

    color: #a14b54;
  
}

}
