html:after {
    padding: 5px;
    text-align: center;
    color: #ff0000;
    content: "© 2018 Youtube Purple & Blue (-Tristen-)";
    font-size: 11px;
    font-weight: bold;
    display: block;
    text-transform: uppercase;
    position: fixed;
    margin: 0 auto;
    width: 100%;
    left: 0;
    bottom: 0;
    right: 0;
    opacity: 0.2;
}
body {
    background: transparent !important;
}
/*FOR USERSTYLES ONLY*/
/* Wallpaper */

.ytd-page-manager, body.site-center-aligned {
    background: #000000 url(https://scontent-atl3-1.xx.fbcdn.net/v/t1.0-9/42576655_703315980034134_1579344506501726208_n.jpg?_nc_cat=109&_nc_ht=scontent-atl3-1.xx&oh=d464365e045fac99cd158090f727bbeb&oe=5D359DE9) repeat fixed!important;
}
/* Wallpaper*/
/*FOR EDITING ONLY*/
/* Wallpaper */
