    text-shadow: 0 0 0 !important;
}
/*Box Shadow*/

::before, h3, header, i, button, footer, label, td, table, div, body, html, a, li, ui, ul, span {
    -webkit-box-shadow: 0 0 0 !important;
    box-shadow: 0 0 0 !important;
}
/*Transparent Gradient Remover*/

.yt-live-chat-text-message-renderer {
    background-color: transparent !important;
    background-image: -moz-linear-gradient(top, #08c, #0077b3) !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(rgba(0, 0, 0, 0)), to(rgba(0, 0, 0, 0))) !important;
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0)) !important;
    background-image: -o-linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0)) !important;
    background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0)) !important;
}
/*Border Remover*/

.translation-editor-original-input, .translation-editor-original-input:hover, .translation-editor-original-input {
    border: 0 !important;
    border-left: 0 !important;
    border-right: 0 !important;
    border-top: 0 !important;
