.ytcp-navigation-drawer-0 paper-icon-item.original-studio-link.ytcp-navigation-drawer iron-icon.ytcp-navigation-drawer, #creator-classic-item, .ytcp-navigation-drawer-0 paper-icon-item.bottom-section-entry.ytcp-navigation-drawer:hover, .iron-selected:hover, #dismiss-buttom, .badge.ytd-badge-supported-renderer:hover, .yt-video-picker-grid .selected-video-picker-item, ytd-subscribe-button-renderer:not([button-style]) paper-button:not([subscribed]), #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover, .yt-next-continuation:hover {
