@-moz-document url-prefix("https://www.youtube.com/analytics"), url-prefix("http://www.youtube.com/analytics"), url-prefix("http://youtube.com/analytics"), url-prefix("https://youtube.com/analytics") {
/*Analytics Override*/

div{color: #0099ff!important;}

/*Border Remover*/
button{border:0 !important; border-left:0 !important; border-right:0 !important; border-top:0 !important; border-bottom:0 !important;}

/*Transparent Remover*/
li,ul,input,gwt-debug-bragBar,span,table,tbody,tr,div,td{background-color: transparent !important;}

/*Transparent Gradient Remover*/
div{background-image: linear-gradient(to right bottom, rgba(0, 153, 255, 0) 0%, rgba(0, 153, 255, 0) 100%) !important;}

/*Text-Shadow Remover*/
button,div,h1,h2{text-shadow: 0 0 0 !important;}

/*Boarder Grey*/
::after,:after,::before,h3,header,i,button,footer,label,td,table,div,body,html,a,li,ui,ul,span{border-color: #0099ff #0099ff #0099ff #0099ff!important;}

/*Button*/
.C2KYWWB-K-h .datePickerDayIsValue,button{outline: none!important; background-image: linear-gradient(to right bottom, #4B4B4B 0%, #000000 100%) !important;background-color: transparent !important;border-radius: 43px 8px 43px 8px !important;}

/*Button Hover*/
.C2KYWWB-K-h,button:hover{outline: none!important; background-image: linear-gradient(to right bottom, #141414 0%, #000000 100%) !important;background-color: transparent !important;border-radius: 43px 8px 43px 8px !important;box-shadow: 0 0 1px #000 !important;}
}

