 * File:    pypi_index.css
 */
html{
        background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat;
}
body, div#content-body {
    background: #222;
    box-shadow: 3px 3px 10px #000;
}

div.body
{
    background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat;
    color: #aaa;
}

div.document 
{
    background: #012028;
}

/*div#content-body div#content,*/
.skiptonav{
        background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat;
}

#skiptonav{
        background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat;
}
div#content-body{
    color: #3776A3;
}
h1#logoheader, a#logolink {
    background: #111 url("data:image/png;base64,") repeat !important;
}
h1#logoheader div {
    color: #4983AB !important;
}
div.section h1,
div.body h1,
div.body h2,
div.body h3,
div.body h4,
div.body h5,
div.body h6
{
    background: none;
    color: #3776A3;
}

html body div#left-hand-navigation div#menu ul[class^="level"] li a {
    background: none;
    background: #111;
    color: rgba(0,0,0,0);
    border: #333 solid 1px;
    margin: auto;
    overflow: hidden;
    text-decoration: none;
    white-space: normal;
}
html body div#left-hand-navigation div#menu ul[class^="level"] li {
    background: #111;
    color: rgba(0,0,0,0);
    margin: auto;
    text-decoration: none;
}
div#document-navigation {
    background: none;
    background-color: #323;
    box-shadow: 2px 2px 5px #000;
    border: #222 solid 1px;
    -moz-appearance: none;
}
div#document-navigation h4{
    color: rgba(255,255,255,.75);
    text-shadow: 1px 1px 8px #000;
}
div#document-navigation ul,
div#document-navigation ul li,
div#document-navigation ul li a {
    background: none;
    border: none;
    color: rgba(200,100,100,.5);
    text-decoration: none;
    white-space: normal;
}
/*html body div#left-hand-navigation,*/
div.related,
div.sphinxsidebar 
{
    background-color: #111;
}

div.sphinxsidebar input {
    border: 1px solid #333;
}

div.note {
    background-color: #111;
    border-color: #000;
}

div.related:first-child {
    border-color: #AAA;
}

div#sidebarbutton {
    background-color: #000 !important;
}

div#left-hand-navigation div#menu ul[class^="level"],
div#left-hand-navigation div#menu ul[class^="level"] li.selected,
div#left-hand-navigation div#menu ul[class^="level"] li.selected a.selected {
    background-color: #444 !important;
}
div.section ul.nodot li {
    background: #222;
    color: #AAA;
}

div#left-hand-navigation div#menu ul[class^="level"] {
    background-color: #322 !important;
    color: #4983AB !important;
    border: rgba(100,100,100,0) dotted 1px;
}
a {
    color: #4983AB !important;
    border: none;
    text-decoration: none;
    white-space: normal;
    padding: 0px;
    font-family: "Menlo", Menlo, Lucidia;
}
:root {
    --background-color: 180,180,180;
    --alpha-odd: 0.6;
    --alpha-even: 0.8;
}
div#breadcrumb {
    text-shadow: 2px 2px 8px #000;
}
div.section table {
    width: 90%;
    box-shadow: 2px 2px 8px #000;
}
div.section table
div.section table body,
div.section table th,
div.section table tr,
div.section table td {
    background-color: #111;
    color: rgba(85,100,100,1);
}
div.section table tr.odd td,
div.section table tr.odd{
    /*background: none;*/
    background-color: rgba(var(--background-color), var(--alpha-odd));
}
div.section table tr.even td,
div.section table tr.even {
    background-color: rgba(var(--background-color), var(--alpha-even));
}

}
