css:
@-moz-document domain("ameblo.jp") {
/* Ameblo AD Blocker ver.2020.04.20  ************************** */
/* ブログページ ADブロック *********************************** */
/* ヘッダー部 ドロップダウン記事リスト 動作の修正 ********* */
._2VAk1gaA {
    display: none; }

._2VAk1gaA:hover {
    display: block; }

._226P45V8:hover + ._2VAk1gaA {
    display: block; }

/* ヘッダー部 ドロップダウンAD */
._1E2vzS02 {
    display: none; }

/* 本文下部 AD（新スキン）*************************** */
.skin-entryAd {
    display: none; }

div[id^="div-gpt-ad"] {
    display: none; }

/* サイドメニュー部（新スキン）*********************** */
/* ADエリアのスクロールフィックスを解除 */
#subAInner {
    position: static !important;
    margin-top: 0 !important; }

/* メニュー上部AD */
#sideTopBanner {
    display: none; }

/* メニュー下部AD */
#sideBottomBanner {
    display: none; }

/* ブログページ ADブロック（旧スキン対応） ************* */
/* 本文上部 AD（旧スキン）********* */
._5DRokKfQ {
    display: none; }

/* 本文下部 AD（旧スキン）********* */
.subAdBannerHeader,
.gpt-frame {
    display: none; }

/* サイドメニュー部（旧スキン） ***** */
/* メニュー上部AD */
.subAdBannerArea {
    display: none; }

/* メニュー下部AD */
.subAdBannerHeader,
.gpt-frame {
    display: none; }

/* フッター部  ************************************ */
/* ページ下部 ジャンルランキング・Amebaトピックス を非表示 */
#js-bigfooter {
    display: none; }

/* 2019.07 下部 旧#js-bigfooter を非表示 */
#app ._Q47mEGqf {
    display: none; }

/* 2018.07 ブログを始める を非表示 */
#app aside._iI3GpNIa {
    display: none; }

/* アメブロフッター  ******************************** */
/* アメブロフッターを表示したい場合は 以下の1項を削除 */
#app footer {
    display: none; }
}

@-moz-document regexp("ameblo.jp") {
/* アメブロヘッダー　スクロールアウト オプション  ****************** */
body {
    padding-top: 0 !important; }

._1NHfCU0F {
    position: absolute; }

/* ************************************************
アメブロヘッダーを「ウインドウ上部に固定」のデザイン」を変更し、
スクロールで上方にスクロール移動する様に変更します。

このコードを有効にするには、以下の操作を行ってください
　　● コードの適用先を「ドメイン上のURL」に変更
　　● このスタイル「Ameblo AD Blocker」を保存
************************************************ */
}

@-moz-document domain("ameblo.jp") {
/* 記事一覧 ADブロック  ************************************** */
/* 旧スキン3列タイプ 記事下部 */
.subAdBannerHeader,
.subAdBannerHeader + div {
    display: none; }

ins.adsbygoogle {
    display: none !important; }

.category-alliance-wrapper {
    display: none; }
}

@-moz-document domain("ameblo.jp") {
/* 画像一覧 ADブロック  ************************************** */
/* 画像一覧ページ */
#officialBox {
    display: none; }

/* 画像一覧 個別画像ページ */
#adBox {
    display: none; }
}

@-moz-document domain("profile.ameba.jp") {
/* プロフィール ********************************************* */
/* ページ右 AD */
.profile__google-ad {
    display: none; }

div[id^="div-gpt"] {
    display: none; }

/* 下部 人気記事など */
.big-footer {
    display: none; }
}

@-moz-document domain("blog.ameba.jp") {
/* 読者登録（登録終了）***************************************** */
#monsterBnr {
    display: none; }
}

@-moz-document domain("blog.ameba.jp") {
/* ブログ管理 ADブロック ************************************* */
#ucsMainRight div:not(#sideHowToUse) {
    display: none; }

/* 下部AD */
#footerAd {
    display: none; }

/* フッター */
#globalFooter {
    display: none; }

/* 管理トップ アメブロ告知バナー */
.ucsTop .adcross_loader {
    display: none; }
}

@-moz-document domain("peta.ameba.jp"), domain("msg.ameba.jp") {
/* 各種管理ページ（左メニュー小型タイプ）ADブロック *************** */
/* 下部AD */
#footerAd {
    display: none; }

/* フッター */
#globalFooter {
    display: none; }

/* ぺタ管理ページのみ 右列AD */
.adsenseAreaSide {
    display: none; }

/* メッセージ管理ページ ごみ箱 下部AD */
div[id^="div-gpt-ad"] {
    display: none; }
}

@-moz-document domain("search.ameba.jp") {
/* Ameba検索 ADブロック  ************************************ */
/* 右サイド AD  *********************************** */
.PcSideBar_AdArea {
    display: none; }

div[id^="div-gpt-ad"] {
    display: none; }

/* 検索結果リスト 上下のADブロック ***** */
#listingAdA,
#listingAdB {
    display: none; }

/* アメブロフッター ******************************** */
.PcGlobalFooter {
    display: none; }
}

@-moz-document domain("blogtag.ameba.jp") {
/* ハッシュタグ一覧  ***************************************** */
/* 右サイドAD */
div[id^="div-gpt-ad"] {
    display: none; }

/* ブログを始める */
.sideRegister {
    display: none; }

/* トップブロガー登録 */
.button--whiteGreen.sideTopBloggerButton {
    display: none; }

/* フッター amebloサービス一覧  ********************* */
#gFooterInner {
    display: none; }

/* アメブロフッター ******************************* */
#ambFooter {
    display: none; }
}

@-moz-document domain("www.ameba.jp") {
/* ホーム ADブロック **************************************** */
/* 左列AD */
.HomeLeftAd {
    display: none; }

/* Amebaトピックス内 AD */
.HomeBlogFeed_Article_Ad {
    display: none; }

/* フッター部 非表示 */
.FooterAmeba {
    display: none; }

.PcFooter {
    display: none; }

/* ログアウト時 */
div[id^="div-gpt-ad"] {
    display: none; }
}
