css:
@-moz-document domain("play.typeracer.com") {
.container,
body,
.main {
    background: #222;
    color: #555;
}
.footer,
.themeHeader,
.statsView,
.mainMenuAdUnit,
#hostpageBannerAds,
.navControls,
#gwtLoadingMessage {
    /*"hide"*/
    
    position: fixed;
    top: 200vh;
}
.mainUserInfoBox {
    position: fixed;
    bottom: 0;
    right: 0;
    background: #111;
    border-radius: 3px;
}
.mainMenu > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(5) {
    display: none;
}
.mainMenu {
    position: fixed;
    top: 100%;
    left: 100%;
}
.mainMenu::after {
    content: "welcome to typeracer.";
    position: fixed;
    top: 50% !important;
    left: 50% !important;
    transform: translate(-50%, -50%);
    color: #fff;
    font-size: 300%;
    animation-name: example;
    animation-duration: 10s;
    animation-iteration-count: infinite;
}
/* Standard syntax */

@keyframes example {
    0% {
        letter-spacing: 0;
    }
    50% {
        letter-spacing: 0.1em;
    }
    100% {
        letter-spacing: 0;
    }
}
.mainMenu::before {
    content: "enter a typing race: ctrl+alt+i, practice: ctrl+alt+o, create a multiplayer room: ctrl+alt+p";
    position: fixed;
    top: 100% !important;
    left: 50% !important;
    transform: translate(-50%, -100%);
}
.room > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > div:nth-child(1) > div:nth-child(1),
.chrome_tr,
.chrome_tl,
.chrome_r,
.chrome_l,
.chrome_br,
.chrome_bl,
.chrome_t,
.chrome_b {
    display: none;
}
.roomSection,
.chrome_m {
    background: transparent;
}
.gameStatusLabel {
    font-size: 1500% !important;
    position: fixed;
    width: 100vw;
    left: 0;
    bottom: 0;
    text-align: center;
    pointer-events: none;
    color: rgba(255, 255, 255, 0.05) !important;
}
.textInfoView > tbody:nth-child(1) > tr:nth-child(3),
.textInfoView > tbody:nth-child(1) > tr:nth-child(4),
.textInfoView > tbody:nth-child(1) > tr:nth-child(5),
.textInfoView > tbody:nth-child(1) > tr:nth-child(6),
#dUI > table > tbody > tr:nth-child(2) > td:nth-child(3),
#tr_textHeader,
#dUI > table > tbody > tr:nth-child(2) > td:nth-child(1) {
    display: none;
}
.textInfoView {
    position: fixed;
    bottom: 2em !important;
    left: 2em !important;
}
.textInfoView > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > table:nth-child(1) {
    padding: 1em;
    background: #111;
    border-radius: 5px;
}
.raceAgainLink::before {
    content: "retry: ctrl+alt+k, or click here";
    position: fixed;
    top: 100% !important;
    left: 50% !important;
    transform: translate(-50%, -100%);
    color: #333;
}
#gwtLoadingMessage::after {
    content: "please wait, typeracer is loading.";
    position: fixed;
    top: 50% !important;
    left: 50% !important;
    transform: translate(-50%, -50%);
    color: #fff;
    font-size: 300%;
    animation-name: example;
    animation-duration: 10s;
    animation-iteration-count: infinite;
}
.gameView > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 960px;
}
.scoreboard > tbody:nth-child(1) > tr > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) > div:nth-child(1)::after {
    content: ">";
    font-size: 450%;
}
.scoreboard > tbody:nth-child(1) > tr > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) > div:nth-child(1) > img {
    display: none;
}
.txtInput {
    z-index: 1000000 !important;
}
.inputPanel > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(1) {
    color: #fff;
}
}
