css:
@-moz-document url-prefix("https://novelplanet.com/") {
#main {
    background-color: #1C1D21;
}
.customMobileBtn {
    color: #EEEFF7 !important;
}
#navPanel {
    background: #31353df9;
}
#navPanel > nav > div > ul > li a {
    color: #EEEFF7 !important;
}
.fillFloatBar {
    background: #1C1D21;
}
input[type="submit"],
input[type="reset"],
input[type="button"],
.button {
    background-color: #92CDCF;
    color: #EEEFF7;
}
.infoLabel {
    color: #EEEFF7;
}
body {
    color: #EEEFF7;
}
.divLoading  img {
	content:url("https://svgshare.com/i/6Fw.svg");
}

#footer {
    background-color: #31353D;
}
#footer > div > section > div > section > h3 {
    color: #92CDCF;
}
#footer > div > section > div > section > ul li a {
    color: #EEEFF7 !important;
}
.result_box {
    background: #31353D;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
}
select {
    background-color: #25262a;
}
input[type="text"],
input[type="password"],
input[type="email"],
select,
textarea {
    background: #25262a;
}
#nav ul li a {
    color: #EEEFF7 !important;
}
.result_box a span {
    color: #92CDCF;
}
#tab-1,
#tab-2,
#tab-3 {
    background-color: #1C1D21;
}
#header {
    background-color: #1C1D21;
    color: #EEEFF7;
}
.title {
    color: #92CDCF !important;
}
a[title] {
    color: #445878 !important;
    font-weight: bold;
    font-style: italic;
}
a {
    color: #445878 !important;
    font-weight: bold;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #EEEFF7;
}
.r-tabs-anchor {
    background-color: #1C1D21;
}
.r-tabs .r-tabs-nav .r-tabs-state-active .r-tabs-anchor {
    color: #EEEFF7 !important;
    background-color: #31353D;
    border-top-right-radius: 0;
    border-top-left-radius: 0;
}
.r-tabs .r-tabs-nav .r-tabs-tab {
    background-color: #1C1D21;
}
.r-tabs .r-tabs-panel {
    border-bottom: 0px;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
}
.r-tabs {
    background-color: #1C1D21;
    border-top: 0px !important;
    border-right: 0px !important;
    border-left: 0px !important;
    border-bottom: 0px !important;
    border-radius: 0px !important;
}
::-webkit-scrollbar-button {
    display: none;
    height: 13px;
    border-radius: 0px;
    background-color: #AAA;
}
::-webkit-scrollbar-button:hover {
    background-color: #AAA;
}
::-webkit-scrollbar-thumb {
    background-color: #445878;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #445878;
}
::-webkit-scrollbar-track {
    background-color: #1c1d21;
}
::-webkit-scrollbar-track:hover {
    background-color: #1c1d21;
}
::-webkit-scrollbar {
    width: 13px;
}
}
