css:
@-moz-document domain("cookpad.com") {
.main-header {
    background-color: #4c4736;
    border-bottom: 1px solid #1e303a;
}.link-unstyled {
    color: #d8d458;
}.main-container {
    background-color: #0a212d;
        box-shadow: 0 0 0px 20px #0a222e;

}.recipe-list>* {
    border-top: 1px solid #4c4736;
}.splash {
    background: #0a212d;
}.action-bar {
    background-color: #0a212d;
    border-top: 1px solid #4c4736;
}.form-control--alternate, .form-alternate .form-control, .form-alternate .btn {
    background-color: #4c4736;
}html {
    background-color: #0a212d;
}.page-footer {
    color: #d8d458;
}.cooksnaps-container {
    background: #19160d;
}.document__panel__section {
    background-color: #19160d;
}div.editor {
    background-color: #19160d;
}.recipe-show__story p:last-child {
    color: #7d765b;
}.ingredient__details {
    color: #908f7b;
}.step__description {
    color: #908f7b;
}.numbered-list__item:not(.ui-sortable-placeholder)::before {
    color: #d8d458;
    background: #4c4736;
    box-shadow: 0 0 3px #19160d;
}.modal-open .modal {
    background-color: #4c4736;
}.padded-content--large {
    background-color: #4c4736;
}.card {
    border: 1px solid #4c4736;
    color: #ff9933;
    background-color: #4c4736;
}.tab-content.active {
    background-color: #4c4736;
}.sidebar-controls {
    background: #4c4736;
}.tabs__item.active .tabs__link {
    background-color: #4c4736;
}.tabs__link {
    border-bottom: 1px solid #0a212d;
    color: #353222;
    background-color: #d68633;
}.user-background::before {
    background-color: #0a212d;
}.offcanvas--open .sidebar {
    background-color: #4c4736;
}.tab-list__item--active a {
    border-bottom: 3px solid #059f4d;
    color: #d68633;
}.feed__content {
    border-top: 1px solid #4c4736;
    background-color: #4c4736;
}hr {
    border-top: 1px solid #4c4736;
}.settings-list {
    background: #4c4736;
    border: 1px solid #4c4736;
}.settings-list li a {
    color: #d8d458;
}.tab-list__count {
    color: #a3a090;
}.tab-list__unit {
    color: #d8d458;
}.user-header__follows-count a {
    color: #779f52;
}.btn-default {
    color: #e7e5df;
    background-color: #4c4736;
}.header-search__action__button {
    color: #0a212d;
    background-color: #d68633;
    border: 1px solid #d68633;
}.form-control {
    border: 1px solid #d68633;
}.form-alternate .form-control {
    color: #b5b490;
}.autocomplete-suggestion {
    border-bottom: 1px solid #4c4736;
    border-left: 1px solid #4c4736;
    border-right: 1px solid #4c4736;
    background-color: #4c4736;
}.highlighted-word {
    color: #d68633;
}.comment-card__footer {
    background: #b97025;
}.thread__form {
    background-color: #b97025;
}.thread__entry {
    border-bottom: 1px solid #d68633;
}.card-sm {

    background-color: #1a170d;
    border: 1px solid #1a170d;
    }.cookie-dialog--top-overlay ~ .offcanvas .sidebar, .cookie-dialog--top-overlay ~ .offcanvas .main-header {
    top: 1px;
}.cookie-dialog--top-overlay ~ .offcanvas {
    padding-top: 0px;
}
}
