css:
@-moz-document url-prefix("https://www.gaccom.jp/safety/new/"), regexp("https?:\\/\\/www\\.gaccom\\.jp\\/safety\\/area\\/[a-z]\\d+(\\/[a-z]\\d+|)") {
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: inherit !important; font-weight: inherit !important;
 line-height: 1.4em !important;
 }


/*--- header ---*/
.nav-header {max-width: none; height: auto; margin: 1px;}
.nav-header ul.menu li.menu-item {height: auto;}
.nav-header .logo-area {height: 20px;}
.nav-header .logo-area img {height: 20px; margin: 0;}
.nav-header ul.menu {height: auto;}
.menu-item .cng-lang {margin: 0;}
.menu-item .cng-lang-img {width: 40px !important; height: 20px !important; margin: 0;}

ul.breadcrumb-list {max-width: none; padding: 0;}

/*--- footer ---*/
ul, menu, dir {margin: 0; padding: 0;}
.sns-area ul {padding: 0;}
.sns-area li {margin: 0;}


/*--- main contents ---*/
.main-body {max-width: none;}

.page-title-area {margin: 0;}
.widget-page-title {padding: 0;}

.main-row {width: auto;}
.mdl-card.widget-area {margin: 0; padding: 0;}
.widget-area .widget-title {padding: 0;}
.doughnut-chart,
.doughnut-chart-area,
.google-ad-area,
.header-msg,
.header-msg .message-close,
.line-chart {text-align: inherit; float: left;}
.doughnut-chart {margin: 0;}
.doughnut-chart .title-area {z-index: -99;}
.chart-legend.safety-kind-chart-legend {display: block;}
.chart-legend.safety-kind-chart-legend>p {display: inline-block !important; margin: 0;}
.main-body.ui-content.ui-body-c .main-row h2+div {padding: 0 !important;}
table.features-model * {display: inline-block;}
table.features-model td, table.features-model th {width: auto; padding: 0;}
table.features-model td {text-indent: 0.5em;}
table.features-model td br {width: 0.5em; content: "";}
.mdl-button.btn-main {margin: 0; padding: 0;}

.safety_info-list li {padding: 0 0.1em 0.1em; border-bottom: 1px gray;}
.safety_info-list .safety_info-img-outer {}
.safety_info-list .safety_info-img-outer .safety_info-img-item {margin: 0;}
.safety_info-list .safety_info-img-outer {width: 100px;}
.safety_info-list .safety_info-img-outer .safety_info-img-inner {border-radius: 17px; width: 35px; height: 35px;}
.safety_info-list .safety_info-img-outer .safety_info-img-inner img {width: 25px;}
.safety_info-list .safety_info-img-outer .safety_info-img-inner img.avatar {width: 35px;}
.safety_info-list .safety_info-text-outer {min-height: inherit;}
.safety_info-list .safety_info-text-outer .info-place {position: static; float: left; width: auto;}
.safety_info-list .safety_info-text-outer .info_date {position: static; float: right; width: auto;}
.safety_info-list .safety_info-text-outer .info_title,
.safety_info-list .safety_info-text-outer .info_description {margin: 0;}




}
