css:
@-moz-document domain('trello.com') {
  
a, a:hover {
	text-decoration: none !important;
}
body, html {
	font-family: "IBM Plex Sans", "ibm_plex_sansregular", "Helvetica", "Arial", sans-serif;
}
#surface {
    background: #fefefe !important;
    background-size: cover !important;
}
#board .list {
    box-shadow: 0 0px 0px 1px black;
}
#surface .board-header .board-header-btn-text {
    color: #666 !important;
    text-decoration: none;
}
#surface .board-header .board-header-btn-filter-indicator {
    background: #eee !important;
}
#surface .board-header .board-header-btn-filter-indicator .board-header-btn-icon-close {
    background: #ddd !important;
}
#board .list {
    margin-top: 1px;
    box-shadow: none;
}
#board .list .js-badges >div:first-child {
	display: none;
}
/* header */
#header .header-logo {
    display: none;
}
#header {
    background: #f1f1f1 !important;
    border-bottom: 1px solid #ddd;
    box-shadow: none;
}
#header .header-search {
    float: right;
    margin-right: 160px;
}
#header .header-btn-icon {
	color: #666 !important;
}
#header .header-btn {
    background: none !important;
    color: #666 !important;
}
#header .header-avatar  {
	display: flex;
	justify-content: center;
	align-items: center
}
#header .js-back-menu {
	background: #666 !important;
}
#header .header-btn:hover {
    color: #444 !important;
}
#header .header-search-input {
	background: #fff;
}
#header .header-search-icon {
	color: #666 !important;
}
#content .org-label {
	display: none;
}
/*list*/
.list-wrapper.mod-add.is-idle {
    background: transparent;
    color: #aaa;
    border: 1px dashed #ccc;
}
.list-wrapper.mod-add.is-idle:hover {
  	background: transparent;
    color: #999;
    border: 1px dashed #aaa;
}
.list-wrapper.mod-add .placeholder {
    color: inherit;
}
/* right menu */
.board-wrapper.is-show-menu .board-menu {
    box-shadow: none;
    background: #f1f1f1;
    border-left: 1px solid #ddd;
}
/*.drawer*/
.boards-drawer-pinned {
    box-shadow: none !important;
    background: #f2f2f2 !important;
}
.board-header-btn-icon {
    color: #aaa !important;
}
.boards-drawer-header {
    background: #6D6666 url(https://d78fikflryjgj.cloudfront.net/images/01ef898811a879595cea8ac3cd77a155/header-logo-2x.png) 3px 4px no-repeat !important;
    background-size: 30% !important;
}
.boards-drawer-header h3 {
    display: none;
}
.js-recent-boards {
    display: none;
}
.js-search-boards {
    border: none !important;
    background: #E2E4E6 !important;
    outline: none;
    box-shadow: none !important;
}
#boards-drawer .compact-board-tile-link-thumbnail {
    background: #aaa !important;
    border-radius: 999em;
    border: 10px solid #ddd;
    box-sizing: border-box;
    height: 30px;
    width: 30px;
    opacity: 1;
    justify-content: center;
    align-self: center;
}
#boards-drawer .compact-board-tile-background {
    background: #ddd !important;
}
#boards-drawer .js-starred-boards {
    display: none;
}
/*
#boards-drawer .js-board-drawer-buttons .js-unpin {
    display: none; 
}
*/
#boards-drawer .compact-board-tile-fade {
    display: none;
}
.list-card {
    box-shadow: 0 0 1px rgba(0, 0, 0, 0.2) !important;
    border: none !important;
}
.icon-star {
    color: orange;
}
.list-header-num-cards {
    display: none !important;
}
.member {
	height: 24px;
	width: 24px;
}
.member-avatar {
    border-radius: 999em;
	height: 24px;
	width: 24px;
}
.list-card-members .member {
    border-radius: 999em;
}
.list-header-num-cards:not(.hide) {
    display: none;
}
.list-card .list-card-labels .card-label {
	min-width: 10px;
	width: 10px;
	height: 10px;
	border-radius: 99em
}
.list-card .list-card-title {
	font-size: 12px;
}
.list-wrapper .open-card-composer {
	margin: 6px 8px;
	border-radius: 4px;
	text-align: center;
	border: 1px #bbb dashed;
	font-size: 12px;
	padding: 4px 0;
}
.list-wrapper .open-card-composer:hover {
	background: none;
	color: #333;
}
.list-wrapper .list-card {
	cursor: default;
	transition: all 0.2s ease;
}
}
