css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://home.myspace.com/index.cfm?fuseaction=user"), url("home.myspace.com/Modules/HomeDisplay/Pages/Home.aspx") {

table, tr, td, div , #home_infoBar span, .cols, .section, .content, .buttons, h1,h2,h3,h4,h5, 
.daySection, .blacktextnb10, #betterb th, #bulletin_inbox th, #messages th, #footerCell th, 
.fieldTitle, .messageTable, .row, th, .blacktext10, #confirm p, .popup_title, #subnav
{color:#ffffff !important; background-color: transparent !important;border: 0px !important;}

 .messageMediumFont, input, textarea  {
	font-size: 11px !important;
	color: #eeeeee !important;
	border: 1px solid #222222 !important;
        BACKGROUND-COLOR: #0D0D0D !IMPORTANT;
	background-repeat: repeat !important;
	font-family:Verdana, Arial, Helvetica, sans-serif !important;
        -moz-appearance: none !important;
}

a:link, a:active, a:visited
{color:#cccccc !important;}

a:hover
{color:#333333 !important; text-decoration:none !important;}

#home_bulletins th, #home_bulletins .cols, #home_bulletins tr, td, table, tr, th, 
form, input, textarea, .blogContent, .blogContentInfo, p 
{background: transparent !important; border: 0px !important; color: #696969 !important;}

.bottom, .top {display:none !important;}

/* Paint backgrounds black, cuz I don't like white*/
#mainContent {background-color: #000 !important}
th {background-color: #000 !important;}
td {background-color: #000 !important;}
#box-mtSmHistory {background-color: #000 !important; color: #bbb !important;}
#hsmMain {background-color: #000 !important; color: #bbb !important;}
#hsmUserName {color: #cfffff !important;}
#hsmStatus, #hsmMoodName, #hsmTimestamp {color: #bbb !important;}
#friendsearch {background-color: #000 !important;}
.clear {background-color: #000 !important;}
#friends {background-color: #000 !important;}
/* Remove the add this add that suggestion box, toms bs announcements, people you may know, apps box, ads, and amber alerts */
#suggestion {display:none !important}
#tomannouncement {display:none !important}
#pymkcol3 {display:none !important}
#appnavigation {display:none !important}
#marketingbox {display:none !important}
#marketingcontent {display:none !important}
#aalert{
display:none!important;
}
#addressbook,
#mtFriendSpaceSuggestion,
#mainContent #col2 div[style*="rgb(252, 246, 207);"],
#grayboxrounded,
#appnavigation,
#col3,
#googlebar,
.top .title span[id*="icon"],
.top .title span[id*="friendsupdate"],
#wrapperTopMenu,
#mobileunit,
#footer,
#tblbulletins th,
#findFriendsLinks,
#home_coolNewVideos,
#splash_profile,
#home_setHomePage,
#home_featured_comedy
{display:none !important;
visibility:collapse !important;}
/* Scrolling Bulletins */
#bulletins .datagrid{
height:120px!important;
overflow:auto!important;
padding:0!important; background-color:#000 !important;
}
/* Scrolling Friend Updates */
#friendUpdate .middle{
height:320px!important;
overflow:auto!important; 
padding:0!important; 
margin:0!important; background-color: #000 !important;
}
#friendUpdate .result{
margin-left:-2px!important;
padding-right:10px!important;
}
#col3, #grayboxrounded, #leaderboard, #googleadtest {display:none !important;}
#col2 {width:750px !important;}
#col2 .module {float:left !important;}
#userstatus {width:370px !important; margin-right:10px !important; background-color:#000 !important;}
#friendUpdate {width:370px !important;}
#friendUpdate li, #friendUpdate div, #friendUpdate ul {width:auto !important;}
span.controlbox {width:100px !important;}
span.title {width:170px !important;}
#friends .friend {padding:0 !important; margin: 0 1px !important; height:80px; white-space:nowrap; overflow:hidden; position:relative; background-color: #000 !important;}
#friends {padding:0 2px !important; background-color: #000 !important;}
#friendspace div .clear {clear:none !important;}
#findFriendsLinks {display:none;}
.ImgOnlineNow {position:absolute; bottom:0; left:5px;}
#friendspace, #bulletins {width:100% !important; background-color: #000 !important;}
#friendslinks br {display:none;}
#friendslinks {clear:both !Important;}
#tblCol2 {width:10% !important;}
}
