css:
@-moz-document url-prefix("https://mypads.framapad.org/mypads/?/mypads") {
div.form-group label {
    display: none;
}
.list-group-item {
    border: none;
    transition: background-color 0.3s ease-in;
}
.list-group-item:hover {
    background-color: #ededed;
}
.ombre {
    background-color: transparent;
    box-shadow: none;
}
.panel {
    border: none;
    box-shadow: 0 2px 42px #0003, 0 2px 8px #0003;
    margin-bottom: 50px;
}
header div.lang {
    position: absolute;
}
.btn-default,
.btn-default:hover,
.btn-default:focus,
.btn-default:active,
.btn-default.active,
.open > .dropdown-toggle.btn-default {
    border: none;
}
.glyphicon {
    color: #7ca0b4;
}
.btn-default {
    color: #8aa2b6;
    transition: all 0.1s ease-in;
}
.form-control {
    height: 27px;
    padding: 0 14px;
    background-color: #efefef;
    border: none;
    margin-top: 3px;
}
.btn-default:hover,
.btn-default:focus,
.btn-default:active,
.btn-default.active,
.open > .dropdown-toggle.btn-default {
    color: #788a9f;
    background-color: #e6e6e6;
}
.panel-title {
    text-transform: uppercase;
    letter-spacing: 1.1px;
    color: #ffffffb3;
}
.btn i.glyphicon-star-empty {
    color: #d2d2d2;
    text-shadow: none;
}
.btn i.glyphicon-star {
    color: #FFC833;
    text-shadow: none;
}
aside {
    display: none;
}
.col-md-9 {
    width: 100%;
}
.btn-group > .btn {
    background-color: transparent;
    text-shadow: none;
    border: none;
}
.btn-group > .btn > span {
    display: none;
}
.btn.cancel {
    color: #b06a6a;
}
section.pads {
    width: 60%
}
section.props {
    width: calc(40% - 40px);
    right: 0;
    position: absolute;
}
section.users {
    width: 100%;
}
.ombre > h1 {
    display: none;
}
.nav-tabs > li.active > a {
    background-color: transparent;
    border: none;
    border-bottom: 2px solid red;
}
.nav-tabs > li.active > a:hover {
    background-color: transparent;
    border: none;
    border-bottom: 2px solid #EB7239;
}
h2 {
    font-family: 'MS Sans Serif', sans-serif;
    font-weight: lighter !important;
    color: #aaa;
}
.glyphicon-info-sign {
    display: none;
}
}
