@-moz-document url-prefix("http://apps.tire-hq.com"), url-prefix("http://www.tire-hq.com"), domain("tire-hq.com"), domain("goodyearwholesale.com") {
/*Login Stuff*/

.login_container {
    background: none;
}
.login_controls {
    background: none;
    width: 40%;
    padding-left: 5%;
    margin-left: 0;
    height: auto;
    color: white;
}
.login_content {
    background: none;
    width: 70%;
    margin: auto;
    z-index: 9;
}
.login_container {
    background: #1e468d;
}
.login_tagline {
    float: right;
    margin: 0;
    width: 40%;
    padding-top: 50px;
    padding-right: 10%;
}
.login_controls ul {
    color: white;
}
.login_controls input[type=submit] {
    background: linear-gradient(-45deg, #e3d046 0, #e3d046 8px, #fd0 8px, #fd0 100%) !important;
    border: 5px solid #fd0;
    color: black;
    width: 100%;
    font-weight: bold;
    font-size: 18px;
    padding: 5px 0 5px 0;
    height: auto;
    margin: 0;
}
.login_controls input[type=text],
.login_controls input[type=password] {
    width: 100%;
    height: auto;
    margin: 0;
    padding: 5px 3px 5px 3px;
    box-shadow: none;
    font-size: .95em;
}
.login_controls label {
    margin: 10px 0 2px 1px;
    font-size: .95em;
}
.login_controls ul li a {
    color: white;
    margin: 0;
    padding: 0;
    text-align: left;
}
.login_text {
    float: right;
    width: auto;
    margin: 0;
}
}

@-moz-document url-prefix("http://apps.tire-hq.com"), url-prefix("http://www.tire-hq.com"), domain("tire-hq.com"), domain("goodyearwholesale.com") {
/*Mostly home stuff, ribbons etc.*/

.thq_shadow_bkg {
    margin: auto;
    width: 70%;
    background: none;
    background: linear-gradient(to bottom, grey, #eee 5%);
}
.ribbon_top {
    display: none;
}
.ribbon_bottom {
    display: block;
    color: white;
}
.ribbon_container {
    display: inline-block;
    background: none;
    width: 100%;
    height: auto;
    margin: 0;
    overflow: hidden;
    background-color: #eee;
}
.ribbon_txt {
    width: auto;
    height: auto;
    text-align: left;
    padding: 0;
    font-size: 1em;
    text-indent: 0px;
}
#Ribbon1 {
    margin: 20px 2% 15px 1%;
    background-color: #a1a9b7;
}
#Ribbon2 {
    margin: 20px 1% 15px 2%;
    background-color: #a1a9b7;
}
#Ribbon3 {
    margin: 20px 2% 15px 1%;
    background-color: #a1a9b7;
}
#Ribbon4 {
    margin: 20px 1% 15px 2%;
    background-color: #a1a9b7;
}
#Ribbon1 .ribbon_bottom,
#Ribbon2 .ribbon_bottom,
#Ribbon3 .ribbon_bottom,
#Ribbon4 .ribbon_bottom {
    background: none;
    height: auto;
    position: relative;
    top: auto;
    left: auto;
    width: auto;
}
#Ribbon1 .ribbon_top,
#Ribbon2 .ribbon_top,
#Ribbon3 .ribbon_top,
#Ribbon4 .ribbon_top {
    display: block;
    position: relative;
    float: left;
    top: auto;
    left: auto;
}
#Ribbon1,
#Ribbon2,
#Ribbon3,
#Ribbon4 {
    border: 5px solid #fff;
    border-radius: 5px;
    padding: 15px;
    width: 47%;
    height: 200px;
    position: relative;
    float: left;
}
.thq_logo_footer {
    height: 60px;
    width: 100%;
    background-color: #0d2273;
    margin: auto;
    padding-left: 15%;
    padding-right: 15%;
    color: white;
    clear: both;
}
#thq_goodyear_footer_logo {
    position: relative;
    float: left;
    top: 5px;
    max-height: 60px;
    height: auto;
    width: auto;
    margin: auto;
    padding: 10px;
}
#thq_dunlop_footer_logo {
    position: relative;
    float: left;
    top: 1px;
    max-height: 60px;
    height: auto;
    width: auto;
    margin: 0;
    padding-left: 5%;
    padding-top: 10px;
}
#thq_kelly_footer_logo {
    position: relative;
    float: left;
    max-height: 60px;
    height: auto;
    width: auto;
    margin: auto;
    padding: 10px 10px 10px 5%;
}
#thq_footer_contact {
    position: relative;
    float: right;
    border: none;
    width: auto;
    margin: auto;
    padding-left: 15px;
    padding-top: 5px;
}
#thq_auth_footer {
    background-color: #1e468d;
}
}

@-moz-document url-prefix("http://www.tire-hq.com"), url-prefix("http://apps.tire-hq.com"), domain("tire-hq.com"), domain("goodyearwholesale.com") {
/*Mostly global stuff*/

* {
    font-family: helvetica;
    box-sizing: border-box;
}
body {
    height: auto;
    background: none;
    background-color: #eee;
    min-width: 0;
}
.thq_content_wrapper {
    background: none;
}
.thq_page_title {
    width: 100%;
}
.thq_titlebar .thq_logocontainer {
    margin-left: 0;
}
.thq_titlebar_nav .thq_sitenav_btn {
    margin-left: 0;
}
.thq_primary_nav {
    width: 70%;
    height: 40px;
    margin: auto;
    background: none;
    background-color: #444;
}
.thq_primary_nav ul {
    float: none;
    margin-left: 0px;
}
.thq_primary_nav ul li a {
    color: #ffffff;
    text-decoration: none;
    font-size: 1em;
}
#thq_auth_header {
    background: none;
    background: #1e468d;
}
#home_content {
    width: 70%;
    margin: auto;
    background: none;
}
.thq_primary_nav ul li .thq_secondary_nav {
    background: none;
    background-color: #777;
    cursor: pointer;
    margin-top: 12px;
}
.thq_primary_nav ul li {
    padding: 10px;
    font-size: 1em;
}
.thq_titlebar_nav {
    background: none;
    background-color: #444;
    width: 100%;
    padding: 0 15% 0 15%;
    min-width: 900px;
}
#thq_SearchBox #thq_SearchForm #thq_SearchText {
    background: none;
    background-color: #eee;
    height: 80%;
    padding: 0;
    font-size: .95em;
    width: 75%;
    float: none;
}
#thq_SearchBox {
    margin-right: 0;
}
.thq_site_container {
    width: 100%;
    background: none;
    background: url('https://www.g3xpress.com/g3x/images/diagonal.png');
}
.thq_titlebar {
    background: none;
    width: 100%;
    padding: 0 15% 0 15%;
    margin: auto;
    min-width: 900px;
}
#thq_SiteNav_Btn {
    color: #fff;
    font-size: 1em;
}
.thq_titlebar_nav .thq_links li a {
    color: #fff;
    font-size: 1em;
}
.thq_primary_nav ul .thq_active {
    background-color: #777;
}
.thq_primary_nav {
    width: 100%;
    padding: 0 15% 0 15%;
    min-width: 900px;
}
.thq_primary_nav ul li .thq_secondary_nav .thq_menuwrapper ul .thq_header a {
    color: #ccc;
}
.thq_primary_nav ul li .thq_secondary_nav .thq_menuwrapper ul li a {
    color: #fff;
}
.thq_sitenav {
    background: none;
    background-color: #777;
    margin: auto;
    width: 70%;
}
.thq_sitenav .thq_sitenav_content .thq_menu_container ul .thq_list_header {
    color: #fff;
    font-weight: bold;
    font-size: 15px;
}
.thq_sitenav .thq_sitenav_content .thq_menu_container ul li a {
    color: #eee
}
#News {
    display: block;
    width: auto;
    background: none;
    margin: auto;
}
.thq_black_footer {
    background: url('https://www.g3xpress.com/g3x/images/diagonal-tight-darker.png');
    padding-left: 15%;
    padding-right: 15%;
    margin: auto;
    width: 100%;
    height: 100%;
}
.thq_black_footer .thq_policies {
    top: 0;
}
.thq_black_footer div ul li a {
    font-size: 1.2em;
    height: 100%;
}
.thq_black_footer div {
    float: left;
}
.message0 {
    color: black;
}
}

@-moz-document url-prefix("http://apps.tire-hq.com"), url-prefix("http://www.tire-hq.com"), domain("tire-hq.com") {
/*<meta name="viewport" content="width=device-width, initial-scale=1.0">*/

@media only screen and (max-width: 1000px) {
    .thq_page_title {
        width: 100%;
    }
    .thq_icon_container {
        display: none;
    }
    .thq_shadow_bkg {
        width: 95%;
    }
    #Ribbon1,
    #Ribbon2,
    #Ribbon3,
    #Ribbon4 {
        width: 100%;
        margin: 0;
    }
    #home_content {
        width: 99%
    }
    .thq_primary_nav {
        width: 100%;
        display: inline-block;
        min-width: 300px;
        padding: 0;
        margin: 0;
        overflow-x: visible;
    }
    .thq_primary_nav ul li {
        float: none;
        background-color: #444;
    }
    .thq_primary_nav ul {
        margin: 0;
        padding: 0;
    }
    .thq_primary_nav ul li .thq_secondary_nav .thq_menuwrapper ul li {
        font-size: 1em;
        background-color: #777;
    }
    .thq_primary_nav ul li .thq_secondary_nav .thq_menuwrapper ul li a {
        font-size: 1em;
    }
    .thq_primary_nav ul li .thq_secondary_nav .thq_menuwrapper ul .thq_header {
        font-size: 1.2em;
    }
    #thq_SearchBox {
        background-color: #444;
        float: none;
        padding: 5px;
    }
    #News {
        display: none;
    }
    .thq_titlebar {
        display: block;
        min-width: 0;
    }
    .thq_userinfo {
        display: none;
    }
    .thq_titlebar_nav {
        display: block;
        overflow: hidden;
    }
    .thq_primary_nav ul li .thq_secondary_nav .thq_menudivider {
        display: none;
    }
    .thq_primary_nav ul li .thq_secondary_nav {
        position: relative;
        top: 0;
        min-height: 100px;
        -webkit-box-shadow: none;
        box-shadow: none;
        -webkit-border-radius: none;
        float: none;
        margin: 0;
    }
    .thq_primary_nav ul li .thq_secondary_nav .thq_menuwrapper {
        background-color: #777;
        float: none;
        width: 100%;
    }
    li #301030 {
        display: none;
    }
    /*Login Stuff*/
    
    .login_container {
        background: none;
    }
    .login_controls {
        background: none;
        width: 90%;
        padding-left: 5%;
        margin-left: 0;
        height: auto;
        color: white;
    }
    .login_content {
        background: none;
        width: 95%;
        margin: auto;
        z-index: 9;
    }
    .login_tagline {
        float: right;
        margin: 0;
        width: 90%;
        padding-top: 50px;
        padding-right: 10%;
    }
    .login_controls ul {
        color: white;
    }
    .login_controls input[type=submit] {
        background: linear-gradient(-45deg, #e3d046 0, #e3d046 8px, #fd0 8px, #fd0 100%) !important;
        border: 5px solid #fd0;
        color: black;
        width: 100%;
        font-weight: bold;
        font-size: 18px;
        padding: 5px 0 5px 0;
        height: auto;
        margin: 0;
    }
    .login_controls input[type=text],
    .login_controls input[type=password] {
        width: 100%;
        height: auto;
        margin: 0;
        padding: 5px 3px 5px 3px;
        box-shadow: none;
        font-size: .95em;
    }
    .login_controls label {
        margin: 10px 0 2px 1px;
        font-size: .95em;
    }
    .login_controls ul li a {
        color: white;
        margin: 0;
        padding: 0;
        text-align: left;
    }
    .login_text {
        padding-top: 30px;
        float: right;
        width: auto;
        margin: 0;
    }
    #thq_noauth_footer {
        background: none;
        background-color: black;
        height: 100%;
    }
}
}
