css:
@-moz-document url-prefix(http://www.moviesubtitles.org/)
{


body {
    color: #CCC !important;
    background-color: #101010 !important; }

/*------------- Main Page -------------*/

.title, #logo h1 a, #logo p {
    color: #CCC !important;
    background: #101010 !important; }
#logo h1 a:hover, #header a:hover {
    color: white !important; }
#header a {
    color: #CCC !important; }


#tabs, #search {
    border: 1px solid #050505 !important;
    background: #202020 !important; }
#tabs li a {
    border: 1px solid #050505 !important;
    color: #CCC !important;
    background: #303030 !important; }
#tabs li a.current {
    background: #505050 !important; }
#tabs li a:hover {
    color: white !important;
    background: #505050 !important; }


.box, .boxtop, .left_articles {
    border: 1px solid #050505 !important;
    color: #CCC !important;
    background: #202020 !important; }
.calendar, td.stat {
    background-color: #202020 !important; }


.smallbox, .smallboxtop {
    background: #202020 !important; }
.smallboxtitle {
    color: #CCC !important; }
.smallbox div {
    background-color: #202020 !important; }
.smallbox a {
    color: #CCC !important; }
.smallbox a:hover {
    color: white !important;
    text-decoration: underline !important; }


/*------------- Search Results -------------*/

.left_articles a {
    color: #CCC !important; }
.left_articles a:hover {
    color: white !important;
    text-decoration: underline !important; }


/*------------- Subtitle Results -------------*/

th {
    border: 1px solid #050505 !important;
    background: #707070 !important; }
th span {
    color: white !important; }

.subtitle {
    text-decoration: underline;
    border: 1px solid #050505 !important;
    background: #303030 !important; }
.subtitle:hover {
    background: #505050 !important; }
.subtitle span:nth-child(even) {
    color: #00ff00 !important; }
.subtitle a div {
    color: #CCC !important; }


/*------------- Specific Subtitle Page -------------*/

.subtitle1, .subsdownload {
    border: none !important;
    background: #202020 !important; }

.filename {
    color: white !important; }

b#love {
    color: #00ff00 !important; }


/*------------- "Movies" Tab -------------*/

.ma {
    margin-bottom: -2px !important; }
.ma:nth-child(even) {
    border-color:  #050505 !important;
    background-color: #202020 !important; }
.ma:nth-child(odd) {
    border-color:  #050505 !important;
    background-color: #303030 !important; }
.ma:hover {
    background-color: #505050 !important; }

.alfa {
    padding: 4px !important; }
.alfa b {
    color: #CCC !important; }
.alfa b:hover {
    color: white !important; }
.alfa1 {
    text-decoration: underline !important;
    color: white !important; }


/*------------- "New Tab", "Top Tab" -------------*/

td.td1, table#table5 tbody tr td:nth-child(even) {
    border-color: #050505 !important;
    background: #303030 !important; }
table#table5 tbody tr td:nth-child(odd) {
    background: #404040 !important; }
#table5 {
    background: #050505 !important; }


ol li div, ol li div a {
    background-color: #202020 !important; }

}
