@-moz-document domain("workflowy.com")
{

/* TAB COLORS W/ UNICODE TAGS */
 :root {
	--circle-opacity: rgba(0,0,0,0.0);
    --circle-hover-color: rgba(0,0,0, 0.5);
	--content: "â—¯";
	--font-family: AvenirNext-UltraLight;
}


span.contentTag[title="Filter #t-indian-red" ] span {
	display: none;
}
.contentTag[title="Filter #t-indian-red" ]:before {
	content: var(--content);
	display: inline !important;
	visibility: visible !important;
	color: var(--circle-opacity) !important;
	font-family: var(--font-family) !important;
}
.contentTag[title="Filter #t-indian-red" ]:hover:before {
	color: var(--circle-hover-color) !important;
}
span.contentTag[title="Filter #t-indian-red" ] {
	visibility: hidden;
}
.t-indian-red-proj > .name > .content {
	border-left: 10px solid indianred;
	border-radius: 0 !important;
	padding-left: 8px;
	margin: 3px 0 0 4px;
}







