/* License:    https://creativecommons.org/publicdomain/zero/1.0/deed/ */

@-moz-document domain("kernel.org") {
body,
tr.dark,
div.listingblock div.content,
#releases tr:nth-child(odd) {
    background-color: black !important;
    color: lightgrey !important;
}
h1,
h2,
th,
a:hover,
a:hover img,
.qbox,
div.page_header,
.title,
.header,
div.page_footer,
tr.light,
div.extended_header,
div.diff.chunk_header span.chunk_info,
.body,
.eolkernel,
div#cgit,
div#cgit table.list tr:nth-child(odd) {
    background-color: #232323 !important;
    color: lightgrey !important;
}
a:link,
dt {
    color: #FF8000 !important;
}
a:visited,
div.page_header a:visited,
a.header {
    color: #808060 !important;
}
.kver {
    color: grey !important;
}
td {
    border-bottom-color: grey !important;
}
.pvkey,
em,
tt {
    color: green !important;
}
a.rss_logo,
div.listingblock div.content tt {
    color: lightgrey !important;
}
#releases tr:nth-child(even),
div#cgit table.list tr:nth-child(even),
.literal-block,
div#cgit div.cgit-panel table,
div#cgit table.diffstat,
div#cgit table.diff {
    background-color: #343434 !important;
}
div#cgit table.list tr:hover > *,
#releases tr:hover > * {
    background-color: hsla(0, 0%, 10%, 1);
}
a.deco,
a.branch-deco {
    color: black !important;
}
a.deco:hover,
a.branch-deco:hover {
    color: white !important;
}
span.age-days {
    color: #88ff88 !important;
}
div#cgit table.diff td div.add {
    color: #88ff88 !important;
}
div#cgit table.diff td div.hunk {
    color: #6bbfff !important;
}
div#cgit span.insertions {
    color: #00e03e !important;
}
div#cgit span.deletions {
    color: #ff3e3e !important;
}
#extras > div {
    background: #232323 !important;
}
.pvkey, em, tt {
    color: #00e03e !important;
}
div#cgit span.age-weeks {
    color: #ccc;
}

}
