css:
@-moz-document domain("developer.wordpress.org"),domain("codex.wordpress.org")
{
    /*
     *
	 * Main page sections (backgrounds)
	 *
	 */
	
  #wpadminbar
  {
    background-color: #1A1A1A !important;
  }

  #wporg-header
  {
    background-color: #1A1A1A !important;
  }
  
  body#wordpress-org.wporg-codex #wporg-header-menu div.uparrow {
    border-bottom: 9px solid #0073aa !important;
  }
  
  
  ul.nav-menu
  {
    background-color: #1A1A1A !important;
  }

  #page, #wordpress-org
  {
    background: #1a1a1a !important;
  }
  
  #headline /*codex*/ { 
    background: #0073aa !important;
  }

  main#primary, div#secondary
  {
    background-color: #1a1a1a !important;
  }

  div#secondary .expandable button
  {
    background-color: #1a1a1a !important;
  }

  div#secondary a.active
  {
    background-color: #d6d6d6 !important;
  }

  .table-of-contents, #toc
  {
    background: #202020 !important;
  }

  #wporg-footer
  {
    background-color: #1A1A1A !important;
  }
	
    /*
	 *
	 * Headline (codex)
	 *
	 */
  #headline {
    border-bottom: none !important;
  }
  
  #headline h2
  {
    text-shadow: none !important;
  }
  
  #headline h2 a {
    color: white !important;  
  }
  
  #headline p.login {
    color: white !important;  
  }
  
  #headline p.login a {
    color: white !important;
    text-decoration: underline !important;  
  }
    
	/*
	 *
	 * Breadcrumbs
	 *
	 */
	
  .breadcrumb-trail .trail-browse
  {
    color: #B6C0C0 !important;
  }

  .breadcrumb-trail span.sep
  {
    color: white !important;
  }

  .breadcrumb-trail .trail-end
  {
    color: white !important;
  }
	
	/*
	 *
	 * General Fonts
	 *
	 */
	
  h1, h2, h3, h4, h5
  {
    color: white !important;
  }
  
  table th, table td {
    color: #C0C0C0 !important;
  }

  p, li, #bodyContent dd, #bodyContent dt /*codex*/
  {
    color: #C0C0C0 !important;
  }
	
	/*
	 *
	 * Callout info box
	 *
	 */
	
  div.callout, #localNotice div /*codex*/
  {
    background: #d6d6d6 !important;
  }

  div.callout p
  {
    color: black !important;
  }
  
  #localNotice div a/*codex*/ {
    color: #21759b !important
  }
	/*
	 *
	 * User comments and voting
	 *
	 */
	
  header.comment-meta, .comment-body
  {
    background-color: #181818 !important;
  }

  #user-note-voting a span
  {
    color: white !important;
  }
	
	/*
	 *
	 * Code
	 *
	 */
	
  .syntaxhighlighter.php
  {
    margin-top: 0 !important;
    padding-bottom: 0 !important;
    margin-bottom: 25px !important;
    border: 1px solid #343434 !important;
  }

  .syntaxhighlighter td.gutter div.line
  {
    background: #1a1a1a !important;
    border-right: none !important;
  }

  .syntaxhighlighter td.code div.line, pre
  {
    background: #0f0f0f !important;
  }

  .syntaxhighlighter td.code div.line:hover
  {
    background: #262626 !important;
  }

  code
  {
    color: #006699 !important;
  }

  code.php.plain
  {
    color: #B6C0C0 !important;
  }

  code.php.variable, pre
  {
    color: #aa7700 !important;
  }

  code.php.string
  {
    color: #388e3c !important;
  }
	
	/*
	 *
	 * Unorganized
	 *
	 */
	
  .arg-default
  {
    color: #B6C0C0 !important;
  }

  .changelog table
  {
    color: #C0C0C0 !important;
  }

  .changelog table code
  {
    color: #aa7700 !important;
  }

  .comment-meta span
  {
    color: #C0C0C0 !important;
  }

  section.parameters dl dt
  {
    color: #7a7a7a !important;
  }

  span.arg-name
  {
    color: #0073AA !important;
  }

  span.trail-inner a
  {
    color: #0073AA !important;
  }

  th.changelog-desc
  {
    background-color: #202020 !important;
  }

  th.changelog-version
  {
    background-color: #202020 !important;
  }
  
  .widefat:hover tbody tr {
    background-color: #1a1a1a !important;  
  }
  
  .widefat:hover tbody th {
    background-color: #464646 !important;  
  }
}
