css:
@-moz-document domain("rtd.moe") {
article p {
	font-size: 1.1em;
	letter-spacing: .06em;
	line-height: 2em;
	text-rendering: optimizelegibility;
}
}

@-moz-document domain("rtd.moe") {
body {
	background-color: #333;
	color: #8a755c;
}
iframe[id*="dsq"] {
	background-color: #e5dacc !important;
	padding: 15px;
}
html {
	filter: hue-rotate(180deg) invert(95%) !important;
}
img,
video,
audio,
iframe {
	filter: brightness(80%) invert(100%) hue-rotate(180deg) !important;
}
}
