css:
/*Updated version*/

/* i really want this to be global */
 #docs-chrome {
    background: #303237 none repeat scroll 0% 0% !important;
    color: #DDD !important;
}
/* Account email in top right corner */
 .docs-presence-plus-widget-status {
    color: #DDD !important;
}
/* Document title box */
 .docs-title-input {
    background-color: #303237 !important;
    font-style: normal !important;
}
.docs-title-input:focus {
    color: white !important;
    background-color: #303237 !important;
    border: 1px solid #171717 !important;
    border-top-color: #0f0f0f !important;
}
.docs-title-untitled {
    display: none !important;
}
/* Fix for menu bar text when hover */
 .goog-control-hover {
    color: black !important;
}
/* Main toolbar */
 #docs-toolbar-wrapper {
    border-top: 1px solid #151516 !important;
    border-bottom: 1px solid #040404 !important;
    background-image: linear-gradient(#404143, #333436) !important;
    box-shadow: inset 0 1px rgba(255, 255, 255, 0.15) !important;
}
/* Toolbar's vertical dividers */
 .goog-toolbar-separator.goog-inline-block {
    border-left: 1px solid #222 !important;
}
/* Color of items in the dropdown menus */
 .goog-menuitem-content {
    color: black;
}
/* Color of non-selectable items in the dropdown menus */
 .docs-gm .goog-menuitem-disabled, .docs-gm .goog-menuitem-disabled .goog-menuitem-content {
    color: #eee !important;
}
/* Background of the rulers */
 .docs-ruler {
    background: #575757 !important;
}
.docs-ruler-background-inner {
    background-color: #8C8C8C !important;
}
.docs-gm .docs-horizontal-ruler {
    border-bottom: 1px solid #303237;
}
.docs-gm .docs-vertical-ruler {
    border-right: 1px solid #303237;
}
#sketchy-horizontal-ruler {
    border-left: 1px solid #303237;
}
.docs-gm #sketchy-vertical-ruler {
    border-top: 1px solid #303237;
}
.docs-explore-widget-button {
    -webkit-filter: invert(100%);
    filter: invert(100%);
    background: #CFCDC8;
    border-top: 1px solid white;
}
/* Toolbars*/
 .goog-toolbar-button, .goog-toolbar-toggle-button, .goog-toolbar-menu-button, .goog-toolbar-combo-button, .docs-icon-folder-solid {
    -webkit-filter: invert(1);
    filter: invert(1);
    color: black !important;
    /* font icons color*/
    background-color: #CFCDC7 !important;
    /*lists backgound color*/
}
.docs-titlebar-buttons.docs-material {
    border-bottom: 1px solid black;
}
.docs-chrome.docs-material-appbar {
    -webkit-filter: invert(1);
    filter: invert(1);
}
.goog-toolbar-menu-button-caption.goog-inline-block, .docs-toolbar-mode-switcher-menu-label, .docs-toolbar-mode-switcher-menu-description, .goog-toolbar-combo-button-input.jfk-textinput {
    color: black;
}
/* Location folder*/
 .docs-parent-collections-container-folder-name.goog-inline-block {
    color: white;
}
.docs-parent-collections-container-outer.goog-inline-block.goog-control.goog-control-hover {
    background: blue;
}
/* Share buttom, User Icon */
 .docs-title-input, .docs-titlebar-buttons {
    background-color: #303237 !important;
    border-bottom: 1px solid #303237 !important;
    border-radius: 2px !important;
    border-top-color: #303237 !important;
    box-shadow: 0 0px rgba(255, 255, 255, 0.15) !important;
    font-style: normal !important;
}
/*Menu color background*/
 .goog-menu.goog-menu-vertical, .goog-menu.goog-menu-vertical.docs-material.docs-menu-hide-mnemonics, .goog-menu.goog-menu-vertical.docs-fontmenu.goog-menu.goog-menu-vertical.goog-menu-noicon.goog-menu-noaccel, .menu.menu-button.goog-control.goog-inline-block.goog-control-hover, .menu-button.goog-control.goog-inline-block.goog-control-hover.goog-control-open, .menu-button.goog-control.goog-inline-block.goog-control-hover.goog-control-active.goog-control-closed, .menu-button.goog-control.goog-inline-block.goog-control-hover, #docs-menu-shield {
    background-color: #989faa;
}
/*Menu buttons: file, tools, etc*/
 .menu-button.goog-control.goog-inline-block {
    background-color: #303237;
    color: white;
    border: transparent;
}
.docs-parent-collections-container-outer.docs-parent-collections-container-outer.goog-inline-block.goog-control.goog-control-hover {
    color: white;
}
span.docs-parent-collections-container-text {
    color: white;
}
/* Highlighted selection */
 .goog-menuitem.goog-menuitem-highlight, .goog-menu.goog-menu-vertical.goog-menu-noicon.goog-menu-noaccel.goog-menuitem-highlight, .goog-menuitem.goog-option.goog-option-selected.goog-menuitem-highlight, .goog-menuitem.goog-option.goog-option-selected.goog-menuitem-selected, .goog-menuitem.goog-option.goog-option-selected.goog-menuitem-hoover, .goog-color-menu-button-indicator, .docs-icon-img-container.docs-icon-img.docs-icon-text-color, .docs-icon-img-container.docs-icon-img.docs-icon-text-bgcolor {
    -webkit-filter: invert(1);
    filter: invert(1);
}
/*Saved changes text color*/
 div.docs-title-save-label-text.docs-title-save-label-text-link {
    color: white;
}
/*Version controls display*/
 .docs-parent-collections-container-outer.docs-parent-collections-container-outer.goog-inline-block.goog-control.goog-control-hover {
    color: black;
    background-color: #989faa;
}
.docs-revisions-chromecover-content.docs-revisions-chromecover-content-color-default.docs-revisions-chromecover-content-exclude-sidebar-width {
    color: white;
    background-color: #303237;
}
/* Fixes for suggestion mode when editing own doc, viewing mode when editing others' docs */
 #docs-toolbar-mode-switcher, #docs-access-level-indicator {
    background-color: transparent !important;
    color: black !important;
}
.docs-icon-mode-review-white, .docs-icon-mode-view-white {
    filter: invert(1) !important;
}
.companion-guest-app-switcher, .companion-app-switcher-container {
    background-color: #303237;
    border: 1px solid #303237;
}
.app-switcher-button-icon-container, .companion-collapser-button.app-switcher-button-checked.app-switcher-button-hover .app-switcher-button-icon-container {
    background-color: #6e787e !important;
}
.app-switcher-button-icon {
    fill: black;
    filter: invert(1) !important;
}
.app-switcher-button .companion-collapser-button .app-switcher-button-hover {
    fill: white;
    filter: invert(1) !important;
}
.docs-explore-widget-button .goog-inline-block .goog-flat-button {
    filter: invert(1) !important;
}
/* ----- DOCS ----- */

/*Document page color, overwrites any color*/

/* 
.kix-page,
.kix-page-content-wrapper {
    background: #454545 !important;
}
.kix-page,
.kix-page-content-wrapper {
    background: default;
}
 */

/* Background behind the document */
 #docs-editor {
    background: #303237 none repeat scroll 50% 10% !important;
}
/* Document drop-shadow removal */
 .kix-page-paginated {
    box-shadow: none !important;
}
/* ----- SLIDES ----- */

/* Lower sections - Speaker Notes */
 #speakernotes {
    color: rgb(201, 76, 76);
    background: #989faa;
}
#speakernotes-dragger {
    background-color: #303237;
    border: 1px solid #303237 !important;
}
.speakernotes-dragger-thumb .dragger-thumb {
    -webkit-filter: invert(1);
    filter: invert(1);
}
/* Background behind slides */
 #workspace-container {
    background: #303237 none repeat scroll 50% 10% !important;
}
.canvas {
    box-shadow: 0 0 2px #000 !important;
    border: 1px solid #303237 !important;
}
/* Left Side Pannel*/
 .filmstrip {
    background-color: #303237 !important;
}
.punch-filmstrip-scroll {
    border: 1px solid #303237 !important;
    background-color: #242424 !IMPORTANT;
}
.punch-filmstrip-thumbnail-pagenumber {
    fill: white !important;
}
.punch-filmstrip-thumbnail-background[fill='#fef6e0'] {
    fill: #9ea9c5 !important;
}
.punch-filmstrip-thumbnail-border {
    filter: invert(1) !important;
}
.docs-gm .punch-filmstrip-cursor, .punch-filmstrip-cursor {
    stroke: #9ea9c5 !important;
    fill: #9ea9c5 !important;
}
.punch-filmstrip-thumbnail-pagenumber .punch-filmstrip-selected-thumbnail-pagenumber[fill='#202124'] {
    fill: white !important;
}
.docs-gm #filmstrip-controls {
    background: #CFCDC8;
    filter: invert(1) !important;
}
/* Themes and Transitions Menus*/
 .docs-gm-sidebars .punch-theme-sidebar, .docs-gm-sidebars .punch-animation-sidebar {
    background-color: #303237;
}
.docs-gm-sidebars .punch-theme-sidebar .docs-thumbnailcontrol-title, .docs-gm-sidebars .punch-theme-sidebar .docs-thumbnailcontrol-title {
    color: white;
}
.docs-gm-sidebars .punch-theme-sidebar-in-presentation-header {
    background-color: #303237 !important;
    border-bottom: 1px solid #303237;
    color: white;
}
.punch-theme-sidebar-in-presentation-content {
    border: 3px solid white;
    background: #303237 !important;
}
.docs-gm-sidebars .punch-theme-sidebar-in-presentation-header.goog-zippy-expanded {
    border-color: #303237;
}
/* Themes header*/
 .docs-gm-sidebars .punch-theme-sidebar-header {
    background-color: #303237;
    border-bottom: 1px solid #303237;
    color: white;
}
/* Themes in this presentation drop down menu*/
 .docs-gm-sidebars .punch-theme-sidebar-in-presentation-content {
    border-top: 1px solid #303237;
    color: white;
}
.punch-theme-sidebar-import-theme {
    border-top: 1px solid #303237;
}
/* Transition */
 .docs-gm-sidebars .punch-animation-sidebar-header, .punch-animation-sidebar-controls-float .punch-animation-sidebar-controls {
    background-color: #303237 !important;
    border-bottom: 1px solid #303237;
    color: white;
}
.punch-sidebar-tile-header, .docs-gm-sidebars .punch-transition-tile .punch-sidebar-tile-options {
    background-color: #303237 !important;
    color: white;
}
.docs-gm-sidebars .punch-animation-sidebar-add {
    border-bottom-color: #303237;
}
.docs-gm-sidebars .punch-animation-sidebar-controls, .docs-gm-sidebars .punch-sidebar-tile-header, .docs-gm-sidebars .punch-sidebar-tile.punch-animation-tile-drop-top:first-child {
    border-top-color: #303237;
}
.punch-sidebar-tile:first-child .punch-sidebar-tile-header {
    border-top-color: #303237;
}
.punch-animation-sidebar-controls {
    background: #303237;
}
.docs-gm-sidebars .punch-animation-sidebar-add-text, .docs-gm-sidebars .goog-flat-button-focused .punch-animation-sidebar-add-text {
    color: white;
}
.docs-gm-sidebars .punch-sidebar-tile-header-text, .docs-gm-sidebars .punch-sidebar-tile-header-subtext {
    color: white;
}
.punch-animation-sidebar-controls, .punch-sidebar-tile, .punch-sidebar-tile-header, .punch-animation-sidebar-add, .punch-sidebar-tile-drop-target-top, .punch-sidebar-tile-drop-target-bottom {
    background-color: #303237 !important;
}
/* ----- SHEETS ----- */
 .docs-gm .grid-bottom-bar {
    -webkit-filter: invert(1);
    filter: invert(1);
    background: #CFCDC8;
}
