css:
@-moz-document url-prefix("https://catbox.moe/"), url-prefix("https://qt.catbox.moe/") {
/* Name:    BlackCatMoe
 * Author:  QChaoz
 * Version: v0.0.1
 */


body {
	background-image: none;
    background-color: #16151D;
    margin: 0;
    padding: 0;
	font-family: "Helvetica";
}

.welcome {

	color: #CCC;
	text-align: center;
	font-size: 64px;
	padding: 15px;
    background: #1D1B26;
	
}

.welcomesub {

	padding: 10px;
	color: #57a1b7;
	text-align: center;
	font-size: 22px;
	color: #b2b2b2;
	margin-bottom: 0.5em;

}

.greeting {

	font-size: 24px; 
	margin-top: -25px;
	color: #b2b2b2;

}

.greeting > a.linkbutton {
	color: #694BA1;
}

.linkrow {

	font-size: 14px;
	margin-top: 25px;
	color: #CCC;

}

.linkrow > a.linkbutton {
	color: #694BA1;
}

.loginstatus {

	font-size: 12px; 
	margin-top: 5px; 
	margin-bottom: 20px;
	color: white;

}

.hometab {
    width: 10%;
    height: 22px;
    padding: 2px;
    border: none;
    background: #694BA1;
    border-radius: 5px;
    text-align: center;
    position: absolute;
    background-image: none;
    font-size: 18px;
    margin: 0.9em 0 0.9em 1em;
}

.hometab:nth-of-type(2) {

	top: 46px;

}

.footer {

	position: relative;
	bottom: 0px;
	width: 100%;
	margin-top: 1em;
	z-index: 1;
	font-size: 18px; 
	text-align: center;
	color: white;
}

.urlUploads {

	text-align: center;

}

form.genericform {

	width: 70%;
	padding: 35px;
	border: none;
	border-radius: 25px;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background: #1D1B26;
	background-image: none;
	font-size: 16px;
	overflow: hidden;
	color: #b2b2b2;
	
}

form.urlUploadForm {

	width: 45%;
	margin-left: auto;
	margin-right: auto;
	text-align: center;
	padding: 5px 5px 5px 5px;
}

form a {

	color: #3F8694;
	text-decoration: none;

}

input[type=text] { 

	width: 45%;
	height: 25px;
	font-size: 16px;

}

input[type=text].stylized { 

	background-color: #17151B;
	border: 1px solid #23202A;
    padding: 2px;
    color: white;
    border-radius: 2px;
    margin-bottom: 3px;

}

input[type=text]:focus { 

	outline: 0;

}

textarea {

	background-color: #17151B;
	border: 2px solid #23202A;
	border-radius: 0.2em;
	width: 45%;
	color: white;
	font-size: 14px;
	font-family: "Helvetica";
	margin-bottom: 0.5em;

}

input[type=password] { 

	background-color: #CCE8ED;
	border: 2px solid #142c3c;
	width: 35%;
	height: 25px;
	font-size: 18px;
}

input[name=url] { 

	width: 50%;
	padding: 15px;
	background-color: #17151B;
	border: 2px solid #23202A;
	text-align: center;
	color: #b2b2b2;

}

input[name=url]::placeholder {
	color: #b2b2b2;
	opacity: 0.25;
}

::-webkit-input-placeholder {
	text-align: center;
	color: #000000;
}

::-moz-placeholder {
	text-align: center;  
}

:-ms-input-placeholder {  
	text-align: center; 
}

input[type="checkbox"] {

	width: 20px;
	height: 20px;
	background: #fcfff4;
}

input[type=button] {

	border: none;
	padding: 5px;
	border-radius: 0.2em;
	width: 100%;
	text-align: center;
	background-color: #694BA1;
	font-size: 22px;
	cursor: pointer;
	
}

input[type=button]:hover {

	background-color: #694BA1;
	color: white;
	transition: background-color 150ms linear;
}

input[type=submit] {

	border: none;
	margin-top: 0.5em;
	padding: 5px;
	border-radius: 0.2em;
	text-align: center;
	background-color: #694BA1;
	font-size: 22px;
	color: #17151B;
	cursor: pointer;

}

input[type=submit]:hover {
	border: none;
	background-color: #694BA1;
	transition: background-color 150ms linear;
	color: white;

}

select {

	background-color: #17151B;
	border: none;
	border-radius: 0.2em;
	color: #b2b2b2;
	height: 35px;

}

.notesmall ::placeholder {
	color: #b2b2b2;
    opacity: 0.25; /* Firefox */
}

a.homebutton {

	color: #17151B;
	text-decoration: none;

}

a.homebutton:hover {

	color: white;
	text-decoration: none;

}

a.linkbutton {

	color: #b2b2b2;
	text-decoration: none;

}

a.linkbutton:hover {

	color: white;
	text-decoration: none;

}

a.linkbutton#showURLUp {
	cursor: pointer;
}

a.stats {

	color: #694BA1;
	text-decoration: none;

}

a.stats:hover {

	color: white;
	text-decoration: none;

}

a.filelist {

	width: 20%;
    padding: 5px;
    border: 2px solid #3F8694;
	border-radius: 25px;
	text-align: center;
	margin: 20px;
	margin-top: -5px;
	background-image: none;
	font-family: "Helvetica";
    font-size: 14px;
	display: inline-block;

}

a#disabled {
	color: gray;
	cursor: text;
	text-decoration: none;
}

p.albumlink {

	font-size: 12px;
	cursor: pointer;
	margin-left: auto;
	margin-right: auto;
	border: none;
	border-radius: 0;
	padding-top: 0;
	padding-bottom: 0;
	background-color: transparent;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap; 
	width: 70%;
	color: #694BA1;
	
}

p.albumlink:hover {
	color: white;
}

span.urlResponse {

	font-size: 20px;
	cursor: pointer;
	margin-left: auto;
	margin-right: auto;
	padding-top: 2px;
	padding-bottom: 2px;
	display: inherit;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;		/* firefox compatibility. wants to break into 2 lines. */

}

button.urlUpSubmit {

	width: 25%;
	margin-top: 10px;
	display: block;
	margin: 5px auto 0 auto;
	border: 2px solid #142c3c;
	padding: 5px;
	border-radius: 5px;
	text-align: center;
	background-color: #CCE8ED;
	font-size: 22px;
	cursor: pointer;

}

button.urlUpSubmit:hover {

	background-color: #B2E7F0;
	transition: background-color 150ms linear;

}


.notenormal {

	width: 55%;
	padding: 25px;
	border: none;
    color: #b2b2b2;
	border-radius: 0.2em;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: none;
    background: #1D1B26;
	font-size: 35px;
	overflow: hidden;

}

.notesmall {

	width: 50%;
    color: #b2b2b2;
	padding: 15px;
	border: none;
	border-radius: .2em;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: none;
    background: #1D1B26;
	font-size: 22px;
	

}

.notesmall p:nth-of-type(4) {
    background: #694BA1;
    border-radius: .2em;
    display: block;
    margin: 0 100px;
    padding: 10px;
	color: white;
}

.notesmall > p > a.linkbutton {
	color: #694BA1;
}

.notesmall > p > a.linkbutton:hover {
	color: white;
}

.notesmall > button {
	background: #694BA1;
	padding: 10px !important;
	border-radius: 0.2em;
	border: none;
	color: #1D1B26 !important;
	font-weight: bold;
	margin: 0 !important;
}

.notesmall > button:hover {
	color: white !important;
	cursor: pointer;
}

.notesmall > a.linkbutton {
    color: #694BA1;
}

.notesmall > a.linkbutton:hover {
    color: white;
}

.notetiny {

	width: 25%;
	padding: 5px;
	border: none;
	border-radius: .2em;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-image: none;
	font-size: 16px;
    background: #1D1B26;
    color: #b2b2b2;

}

.notetiny > a {
    color: #694BA1;
}

.notetinywarning {

	width: 35%;
	padding: 15px;
	border: none;
	border-radius: 0.2em;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background: #1D1B26;
	background-image: none;
	font-size: 16px;
	color: #b2b2b2;

}

table {

	width: 55%;
	background-image: none;
	border: none;
	border-collapse: separate;
	background: #1D1B26;
	color: #CCC;
	text-align: center;
	font-size: 18px;
	margin: auto;
	border-spacing: 5px;
}

th {
	padding: 0.5em;
}

td {
	border: none;
	padding: 0.2em;
	max-width: 3em;
	word-wrap:break-word;
	width: 50% !important;
}

ul {
	padding: 0;
}

ul li.home{

	width: 55%;
	padding: 25px;
	border: none;
	border-radius: 0.2em;
	text-align: center;
	margin: 10px;
	margin-top: 0;
    color: #CCC;
	background-image: none;
    background: #1D1B26;
	font-size: 26px;
	display: block;

}

ul li.torrent {

	width: 20%;
	padding: 6px;
	border: none;
	border-radius: 25px;
	text-align: center;
	margin: 15px;
	margin-top: -5px;
	background-image: none;
	background: #1D1B26;
	font-size: 14px;
	display: inline-block;
	overflow: hidden;
}

ul#reorder-bounds {
	list-style-type: none;
	display: grid;
	grid-template-columns: auto auto auto auto;
    grid-template-rows: auto;
    justify-content: center;
}

ul li#reorderable {
	display: inline-block;
	
}

li div {
	border:solid 1px black; 
}

ul#reorder-bounds > li#reorderable > div {
	width: 185px;
    height: 185px;
	min-width: 185px;
	margin-top: 0;
    margin: 1em;
	padding: 10px;
    background: #1D1B26;
    border-radius: 5px;
}

ul#reorder-bounds > li#reorderable > div > img {
	display: inline-block;
	width: 100%;
	height: 100%;
	vertical-align: middle;
}

dl {
	padding: 0;	
	width: 60%;
}

dl.faq {

	display:table; 
	margin-left: auto; 
	margin-right: auto; 
	margin-bottom: 1em;
	color: #CCC;

}

dl.faq > h1 {
	color: #694BA1;
	margin: 0.5em 0;
}

dl dt.question {

	font-size: 20px;
	font-weight: 600;
	margin-top: 5px;
	color: #CCC;

}

dl dd.answer {

	font-size: 18px;
	margin-top: 5px;
	color: #b2b2b2;

}

code {
	font-size: 16px;
}

div.image {
	position: fixed;
	right: 0px;
	bottom: -5px;
	z-index: -1;
}

div.seeders {

	color: #3da738;
	width: 35%;
	padding: 15px;
	border: none;
	border-radius: 0.2em;
	text-align: center;
	margin: 15px;	
	background-image: none;
	background: #1D1B26;
	font-size: 24px;
	display: inline-block;

}

div.leechers {

	color: #b53c45;
	width: 35%;
	padding: 15px;
	border: none;
	border-radius: 0.2em;
	text-align: center;
	margin: 15px;	
	background-image: none;
	background: #1D1B26;
	font-size: 24px;
	display: inline-block;

}

div.completed {

	color: #7dd3d4;
	width: 35%;
	padding: 15px;
	border: none;
	border-radius: 0.2em;
	text-align: center;
	margin: 15px;	
	background-image: none;
	background: #1D1B26;
	font-size: 24px;
	display: inline-block;

}

.grid-bounds {
    width: 75%;
	text-align: center;
	font-family: "Helvetica";
	margin: 0 auto 40px auto;
    display: grid;
    grid-template-columns: auto auto auto auto;
    grid-template-rows: auto;
    justify-content: center;
}

.col-1-8 {
	width: 185px;
    height: 185px;
	min-width: 185px;
	display: grid;
	justify-items: center;
	grid-template-rows: 1fr;
	margin-top: 0;
    margin: 0.75em;
	padding: 10px;
    background: #1D1B26;
    border-radius: 5px;
	color: #565656;
}

.col-1-8 > p {
	font-size: 12px;
	margin: 0 0;
}


img.previewimg, video.previewimg {

	max-width: 100%;
	max-height: 100%;
	margin-top: 0;
	margin-bottom: 0;


}

img.reorderable {

	max-width: 100%;
	max-height: 100%;

}


.placeHolder div {

	height: 100%;
	background-color:white;
	border:dashed 1px gray;
	
}


form.dz-clickable {

	width: 650px;
	border: none;
	border-radius: .2em;
	background-color: #1D1B26;
	cursor: pointer;
	padding: 25px;
	box-shadow: 0 0 20px black;

}

form.dz-clickable:hover {

	background-color: #694BA1;
	text-decoration: none;
	outline: none;
	transition: background-color 150ms linear;

}

div.dz-default {

	color: white;
	cursor: pointer;
	font-size: 24px;
	font-weight: 400;
	margin-top: 0;

}

div.dz-preview:first-of-type {
	margin-top: 45px;
}


div.dz-preview {

	width: 85%;
	background-image: none;
	border: 2px solid #3F8694;
	border-radius: 15px;
	padding-top: 20px;
	margin-top: 25px;
	box-shadow: 1px 1px 5px grey;
	overflow: hidden;
	cursor: default;

}

div.dz-error {
	border: 2px solid #FF5C5C;
}

div.dz-error-message {

	font-size: 20px;
	margin: 5px;
	cursor: default;
}

div.dz-success {
	border: 2px solid #FF5C5C;
}

div.dz-details {

	text-align: left;
	margin-left: 25px;
}

.dz-upload {
	display: block;
	background-color: #205f60;
	height: 10px;
	width: 0%;
	max-width: 75%;
	margin-left: 20%;
	margin-top: -16px;
	border-radius: 15px;
	float: left;
}

div.dz-filename {
	margin-bottom: 5px;
}

div.responseText {

	font-size: 20px;
	margin-top: 5px;
	margin-bottom: 7px;
	cursor: pointer;
	width: 340px;
	margin-left: auto;
	margin-right: auto;
	border: 1px solid #396B84;
	border-radius: 4px;
	padding-top: 2px;
	padding-bottom: 2px;
	display: inherit;
	background-color: #CCE8ED;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;		/* firefox compatibility. wants to break into 2 lines. */

}

div.responseText:hover {

	background-color: #B2E7F0;
	transition: background-color 150ms linear;

}

div.dz-fallback input {

	width: 30%;
	display: block;

}

div.dz-fallback input:hover {

	width: 30%;
	display: block;

}

div.dz-message {

	font-size: 24px;

}

div.noscriptalert {

	color: #F50012;
	font-size: 18px;
	margin-left: auto;
	margin-right: auto;
	text-align: center;
}

div.contributorsContainer {

	width: 60%;
	margin: 0 auto;

}

div.contributor {

	padding: 1em;
	border: none;
	border-radius: 25px;
	background-image: none;
	background: #1D1B26; 
	font-size: 18px;
	margin-top: 0;
	margin-bottom: 0.5em;
	color: #b2b2b2;


}

div.contributor > p > span {
	color: #694BA1;
}

div.contributor > p > a.linkbutton {
	color: #694BA1;
}

div.contributor > p > a.linkbutton:hover {
	color: white;
}

@media screen and (max-device-width: 640px) {

	form.dz-clickable {

		width: 450px;
		min-height: 250px;

	}

	form.urlUploadForm {
		width: 450px;
	}

	div.dz-message {

		font-size: 40px;
		font-weight: 400;
		margin-top: 110px;

	}

	div.urlUploads a {
		font-size: 32px;
	}

	.greeting {

		width: 85%;
		font-size: 38px;
	}

	.linkrow {
		
		margin-top: 55px;
		font-size: 30px;

	}

	.footer .innertext {

		font-size: 24px; 
		text-align: center;

	}

	.loginstatus { 

		font-size: 20px;
		margin-bottom: 20px;

	}

	form.genericform {

		width: 85%;
		margin-bottom: 290px;

	}

	dl.faq {

		font-size: 28px;
		width: 85%;

	}

	.hometab {

		width: 20%;
		height: auto;
	}

	.hometab:nth-of-type(2) {

		top: 54px;
		color: red;
	}
	
	a.homebutton{

		font-size: 24px;

	}

	.image {
		display: none;
	}	
	
	.notetiny {
		width: 70%;
	}

	.notesmall {
		width: 0;
	}

	.notetiny a {
		font-size: 32px;
	}	
	
	table {

		max-width: 75%;
		
	}

	td {
		max-width: 15em;
	}

}
}

@-moz-document url("https://catbox.moe/sharexcode.txt") {
body {
	color: #b2b2b2;
	padding: 1em;
}
}
