css:
@-moz-document url-prefix("https://i18n.brainout.org") {
body {
    background-color: #1e272e;
}

a {
    color: #ffa801;
}

a:hover {
	color: #f1c40f;
}

.breadcrumb {
    border-radius: 0;
    background-color: #27343e;
}

.sort {
    color: white;
}

.table-condensed{
    color: white;
}

.stripe-odd{
	background-color:rgba(0, 0, 255, 0);
}


.strip-odd {
	background-color:#33414d;
}

.btn-xs {
	background-color: #ecf0f1;
	 background-image:none;
}

.progress-bar-success {
    background-image:none;
    background-color:#2ecc71;
}

.progress-bar-info {
    background-image:none;
    background-color:#2980b9;
}


.progress-bar-danger{
    background-image:none;
    background-color:#c0392b;
}


.progress-checks{
    background-image:none;
    background-color:#f39c12;
}


.progress{
    background-image:none;
	background-color:#ecf0f1;
    border-radius:50px;
    border: 1px solid white;
}

.legend {
    color: white;
}

footer li {
    color: #95a5a6;
}

footer a {
    color:#7f8c8d;
}

.alert-info {
	color: white;
	background-image:none;
    border: none;
	background-color:#575fcf;
}

.alert{
	border:none;
}

.alert-warning {
    background-color:#ff3f34;
    background-image:none;
    color: white;
    border: none;
}


label {
	color: #ecf0f1;
}

.list-group-item {
	background-color:#1e272e;
    color:#ecf0f1;
}



a.list-group-item-success {
	 color: #2ecc71;
}

a.list-group-item-danger {
	 color: #e74c3c;
}


a.list-group-item-warning {
	 color: #f39c12;
}

a.list-group-item-info {
	 color:#3498db ;
}

.panel-body {
    background-color:#33414d;
}

.panel-footer {
	background-color:#33414d;
}

.panel {
 	background-image:none;
    border: none;
	background-color:#33414d;
	color: white;
	box-shadow:0 0 10px rgba(0, 0, 0, 0.55);
}

.panel-primary > .panel-heading{
	background-image:none;
	border-bottom: 1px solid white;
	background-color:#34495e;
}

.panel-success > .panel-heading {
	background-image:none;
	border-bottom: 1px solid white;
	background-color:#2ecc71;
}

.translation-editor {
	background-color:#1e272e;
    color: #ecf0f1;
}

.btn-success{
    	background-image:none;
	background-color:#27ae60;
}

.btn-warning{
    	background-image:none;
	background-color:#f39c12;
}

.btn-info{
    	background-image:none;
	background-color:#3498db;
}

.btn {
	border: none;
}

.table-striped {
    color:black;
}


.dropdown-menu{
    background-color: #34495e;
}
}
