/* NavBar Button */
.meter,body input[disabled], body select[disabled],body input,input.button,.submitFooter .button,#menuMainList li a{background-color: #000;border: 1px solid #00ff00 !important;border-radius: 0px;box-shadow: inset  0px 1px 10px #007F00;text-shadow: 0px 0px 0px #000;color:#00ff00;}

body #content{box-shadow: inset 0px 1px 60px #007F00;}

#help h2, h2, fieldset legend,#help dt.term,body fieldset legend,table tr th,.warning,#menuMainList li span,input.button:hover,.submitFooter .button:hover,#menuMainList li a:hover{border: 1px solid #00ff00 !important;background-color: #00ff00;color:#000;border-radius: 0px;}

#html{width: 100%;height: 100%;}

.meter .text{text-shadow: 0px 0px 5px #000;}
.meter .bar{background: linear-gradient(to bottom, #bfd255 0%,#8eb92a 50%,#72aa00 51%,#9ecb2d 100%);border-radius: 0px 7px 7px 0px;}

.submitFooter{border: 0px solid #00ff00;}

#wrapper {width: 940px;}
#main #contents {width: 610px;}

#statusInfo{padding-right: 15px;color: #00ff00;}
#statusInfo a{color: #00ff00;}

#content.infopage #main {
    background-color: rgba(0, 0, 0, 0);;
    border-radius: 5px;
    border: 0px solid #111111;
}

#content.infopage{padding-bottom: 15px;}

/* LOGO */
#header .logo,#header #logo {background: url(https://i.imgur.com/MsBeW3a.png) left center no-repeat;}
body #content,table tr:hover,#header{background: rgba(0, 0, 0, 0);}
#content::after{top:10px;padding:15px;content: "DD-WRT CONTROL PANEL :: THEME: TERMINAL v1 BY REKORED_";}
#content::after{padding:15px;}

/* HEADERS */
#help h2,h2,fieldset legend{color:#000;background-color: #00ff00;border-radius: 0px;padding:7.5px;}

/* Background stuff */
body #content.infopage #main,#menuSub,.submitFooter{background-color: rgba(0, 0, 0, 0);}
body.gui{background-color:#000;}
body{background-color:#000 !important;}

/* Font */
*{font-family: Verdana, Geneva, sans-serif}
#help a {color: #000;font-weight: 600;}
#content::after,body #help dd.definition,.setting{color:#00ff00;}

.off,input.num,input,select {
    box-shadow:  0px 0px 10px #007F00;
    color: #000;
    background-color: #00ff00;
    background-image: none;
    border: 0px solid #CCC;
    border-radius: 4px;
}

body{background-color: #3b0031;}

/* The CSS to edit this box, BOX-INCEPTION!!! */
body fieldset,textarea,fieldset,#content{/*position: absolute;*/border-radius: 0px;left: 0px;background-color: #000;border: 1px solid #00ff00;color: #00ff00 ;box-shadow: inset  0px 1px 10px #007F00;}
