css:
@-moz-document url-prefix("http://chiaseit.vn/forum/"), url-prefix("https://chiaseit.vn/forum/"), domain("chiaseit.vn") {
/* Set variables */

html {
 --bright-text: #fff;
 --input-background: #555;
 --in-page-background: #202020;
 --in-page-background-tone-2: #333;
 --in-page-text: #D3D3D3;
 --in-page-link: rgb(137, 203, 238);
 --in-page-visted-link: rgb(174, 114, 174);
 --in-page-unread-visted-link: rgb(69, 147, 187);
 --in-page-accent-tone-1: rgb(0, 115, 177);
 --sticky-item-background: #343434;
 --header-background: #3d485b;
 --header-hover-background: #333;
 --header-selected-background: rgb(66, 100, 130);
 --header-text: #fff;
 --sub-header-background: #3d485b;
 --black-box-gradient: linear-gradient(#333, #444);
 --normal-border-tone: rgb(0, 120, 159);
 --normal-border: 1px solid var(--normal-border-tone);
 --time-color: #b2b983;
}

/* End of setting variables */
html, body {
    background-image: none !important;
    background-color: var(--in-page-background);
    color: var(--in-page-text);
}

a:link, a:visited {
    color: var(--in-page-link);
}

a:hover {
    text-decoration: underline;
}

.BRMSContainer .brmsTabContent ol.brmsContentList a[href$='unread'],
.BRMSContainer .brmsTabContent ol.brmsContentList a.username,
.discussionListItem .lastPostInfo .username,
.discussionListItems .unread .title a {
    color: var(--in-page-link)!important;
}

.discussionListItem .title a,
.BRMSContainer .brmsTabContent ol.brmsContentList a {
    color: var(--in-page-visted-link);
}

.BRMSContainer .brmsTabContent ol.brmsContentList a.paint {
    color: var(--time-color);
}

#header,#headerMover #headerProxy {
    background-image: none !important;
    background-color: var(--in-page-background) !important;
}

#content,
.conversation_view .messageList,
.conversation_view .quickReply {
    background-color: var(--in-page-background);
}

.navTabs .navTab.selected .navLink {
    color: var(--header-text);
    background-color: var(--header-selected-background);
}

.topHeader, .tabLinks, .navTabs .navTab.selected .tabLinks a {
    color: var(--header-text);
    background-color: var(--header-background) !important;
    border: none!important;
}

.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    color: var(--header-text);
    background-color: var(--header-hover-background) !important;
}

.BRMSContainer .brmsTabContent ol.brmsContentList li:hover .itemContent .counter .countNumber {
    background-color: rgb(69, 147, 187)!important;
    color: var(--bright-text);
}

.BRMSContainer .itemContent:hover .counter .arrow {
    border-left-color: rgb(69, 147, 187)!important;
}

.BRMSContainer ul.brmsTabNav > li.current {
    color: var(--bright-text);
    background: #2c2c2c !important;
    box-shadow: inset 0 0 35px 5px #132b5b;
}

.BRMSContainer ul.brmsTabNav > li {
    text-shadow: 0 0 0 transparent, 0px 1px 1px #444;
}
.BRMSContainer ul.brmsTabNav > li a {
    color: var(--bright-text);
}

.BRMSContainer.brmsTopTabs ul.brmsTabNav > li {
    border-color: #060606!important;
}
.BRMSContainer ul.brmsTabNav > li {
    color: var(--bright-text);
    background: #666 !important;
    box-shadow: inset 0 0 35px 5px #132b5b;
}

.BRMSContainer .brmsConfigList .brmsConfigBtn {
    background-color: #666!important;
    background-image: none!important;
}

.BRMSContainer .brmsTabContent {
    border: var(--normal-border)!important;
    background-color: #444!important;
    box-shadow: inset 0 0 35px 5px var(--header-background);
}

.BRMSContainer .brmsTabContent ol.brmsContentList li {
    border-top: 1px none white!important;
}

.BRMSContainer .brmsSticky .itemContent {
    background: var(--sticky-item-background) !important;
}

.BRMSContainer .brmsNewItem .itemContent,
.BRMSContainer .itemContent {
    background: #202020!important;
}

.BRMSContainer .itemContent:hover {
    background: #444!important;
}

.BRMSContainer .itemContent .itemDetail a {
    margin-right: 0.5em!important;
}

li.itemUser:nth-child(odd){
    background: #333 !important;
}
li.itemUser:nth-child(even){
    background: #555 !important;
}

.brmsDropdownMenu li {
    background: #444 !important;
    border: 1px solid #5d5d5d!important;
    border-top-color: #222;
    border-right-color: #222;
    border-bottom-color:#222;
    border-left-color: #222;
    box-shadow: 0 2px 2px -2px #282828;
}

.cse .gsc-control-cse, .gsc-control-cse, form.gsc-search-box {
    background: #202020;
    border: var(--normal-border);
}

.breadcrumb {
    background-color: #202020;
    border: var(--normal-border);
}
.breadcrumb .crust a.crumb {
    color: #808080;
}
.breadcrumb .crust a.crumb:hover {
    background-color: #020202;
}

.breadcrumb .crust:hover .arrow span {
    border-left-color: #cccaca;
}
.breadcrumb .crust .arrow span {
    border-left-color: #808080;
}

.secondaryContent {
    background-color: #202020;
    border-bottom: 1px solid black;
}

.pairsJustified {
    color: #808080;
}

div.BRMSContainer:nth-child(3) > div:nth-child(2) {
    background-color: #202020!important;
    box-shadow: none;
}
li.itemUser {
    color: #808080;
}

.nodeList .categoryStrip, .xenOverlay .section .heading {
    background-color: var(--sub-header-background)!important;
    border: var(--normal-border);
}
.tabs {
    border-bottom: none;
}
.tabs li.active a, .tabs.noLinks li.active {
    background-color: var(--sub-header-background)!important;
}
.tabs li a, .tabs.noLinks li {
    color: var(--bright-text) !important;
    background: var(--black-box-gradient);
    border: var(--normal-border);
}

.profilePage .tabs.mainTabs {
    background: var(--sub-header-background)!important;
}

.member_notable .mainContent > .section, .online_list .mainContent > .section, .news_feed_page_global .eventList {
    border: var(--normal-border);
}

.Popup .PopupControl:hover {
    background-color: #333 !important;
}
.PopupOpen .arrowWidget {
    filter: invert(100%);
}

.txtarea, .textCtrl {
    background-color: var(--input-background) !important;
    color: var(--bright-text)!important;
    border: 2px inset #393939;
}
.textCtrl:focus, .textCtrl.Focus {
    background-image: none!important;
    background-color: #393939!important;
    color: var(--bright-text)!important;
    border: 2px inset #393939;
}
input.textCtrl::placeholder {
    color: var(--bright-text)!important;
}

input.button.disabled,
a.button.disabled,
input.button.primary.disabled,
a.button.primary.disabled,
html .buttonProxy .button.disabled {
    color: var(--bright-text);
    background: linear-gradient(#555, #aaa);
    border-color: var(--normal-border-tone);
    opacity: 0.5; 
}

.explain .CurrentStatus {
    color: rgb(219, 141, 141);
}

.redactor_toolbar {
    border-bottom: 1px solid rgb(0, 0, 0);
    background: linear-gradient(#555, #aaa);
}

.redactor_toolbar li {
    filter: drop-shadow(0 0 0.05em black);
}

.nodeList.sectionMain .primaryContent,
.discussionList .sectionHeaders {
    background: var(--black-box-gradient);
}

.discussionListItem .stats,
.discussionListItem,
.mif_chatbox iframe {
    background: #333;
}

.sectionFooter {
    color: rgb(0, 129, 198);
    background-color: #222;
}

.nodeStats {
    color: #808080!important;
}
.nodeLastPost {
    color: #fb5555;
}

#DiscussionListOptionsHandle a,
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo,
.node .nodeLastPost,
.nodeList .node.level_1,
.BRMSContainer .brmsConfigList .brmsConfigBtn,
.brmsDropdownMenu,
.discussionListItem .stats,
.discussionListItem,
.mif_chatbox iframe,
.quickReply,
.profilePage .mast .section.infoBlock, .profilePage .mainProfileColumn,
.profilePage .mast,
.navigationSideBar a, .navigationSideBar a.LogOut,
.pollBlock .pollOptions, .pollBlock .pollResults {
    border-color: var(--normal-border-tone)!important;
}

#DiscussionListOptionsHandle a,
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo,
.subHeading, .navigationSideBar .heading,
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    background: var(--black-box-gradient);
}

#DiscussionListOptionsHandle a,
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    color: var(--bright-text);
}

.navigationSideBar a:hover {
    background: rgb(166, 165, 165);
    color: #000000;
    text-decoration: none;
}

.button:hover, .button[href]:hover, .buttonProxy:hover .button, .button.primary:hover {
    color: var(--bright-text);
    background-color: var(--in-page-accent-tone-1);
    text-decoration: none;
    border-top-color: #FFFFFF;
}
.button, .button[href], .buttonProxy .button, .button.primary {
    color: var(--bright-text);
    background-color: rgb(72,75,78);
}

.Menu .secondaryContent {
    background-color: #333;
}
form.AutoValidator label {
    color: #808080;
}

.Menu .sectionFooter {
    background-color: #202020;
}

.xenOverlay.memberCard {
    color: var(--bright-text);
    background-color: #333;
}

.textHeading, .xenForm .sectionHeader {
    color: #aeadad;
    text-shadow: #2c2c2c 1px 1px;
}

.searchResult .snippet a {
    color: rgb(156, 177, 246);
}

.sidebar .secondaryContent {
    border: var(--normal-border);
}

#jumpMenu {
    border: var(--normal-border);
}
#jumpMenu .blockLinksList ul, #jumpMenu .nodeList li.d0 {
    border-top: var(--normal-border);
}
.xenOverlay #jumpMenu .nodeList {
    border-left: var(--normal-border);
}
/*---*/

#content .pageContent {
    background-color: var(--in-page-background);
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5),
 -1px -1px 2px rgba(0, 0, 0, 0.5);
}
.messageList .message {
    border-top: var(--normal-border);
}

.message .signature {
    border-top: 1px dashed var(--normal-border-tone);
}

.primaryContent {
    background-color: #202020;
    padding: 10px;
    border-bottom: var(--normal-border);
}

.vietxf_MoreThread .secondaryContent {
    background-color: #202020;
    border-bottom: var(--normal-border);
}
.advslides {
    background-color: #D3D3D3;
    border: 2px double darkgrey;
}
.bbCodeBlock {
    border: var(--normal-border);
}
.bbCodeBlock .type {
    color: var(--bright-text);
    text-shadow: none!important;
    border-bottom: var(--normal-border);
    background: linear-gradient(#333, #777);
}
.bbCodeBlock pre, .bbCodeBlock .code {
    background: #555;
}

.emCtrl:hover, .emCtrl:focus, .ugc a:hover, .ugc a:focus {
    text-decoration: none;
    color: #032a6d;
    background: #a1bcc3;
    box-shadow: none !important;
}

.csitmt1, .xenForm .formHeader {
    color: var(--bright-text);
    background-color: #333;
}
.message .publicControls .item, body .muted, body a.muted, body .muted a {
    color: var(--bright-text);
}

.bbCodeQuote .quoteContainer {
    background-color: #444;
}
.PageNav a,.PageNav a.text {
    color: var(--bright-text);
}

#notices1 {
    display:none!important;
}

.Menu .primaryContent {
    background-color: #202020;
}
.Menu .blockLinksList {
    background-color: #333;
}
.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: var(--bright-text);
}

.sectionMain {
    background-color: #202020;
    border: var(--normal-border);
}
.bbCodeQuote .quoteContainer .quoteExpand {
    color: #f8ddbb;
    cursor: pointer;
    background: -webkit-linear-gradient(top, rgba(252, 252, 255, 0) 0%, rgb(113, 113, 114) 80%);
    background: -moz-linear-gradient(top, rgba(252, 252, 255, 0) 0%, rgb(113, 113, 114) 80%);
    background: -o-linear-gradient(top, rgba(252, 252, 255, 0) 0%, rgb(113, 113, 114) 80%);
    background: linear-gradient(to bottom, rgba(252, 252, 255, 0) 0%, rgb(113, 113, 114) 80%);
}

.PageNav a,
.PageNav a.text {
    color: var(--bright-text)!important;
}
.discussionListItem .itemPageNav {
    visibility: visible;
}
.itemPageNav a {
    background: #3d3d3d;
    border-color: #6db1f9;
}

.textWithCount,
.profilePage .mast .followBlocks .primaryContent.avatarHeap,
.messageSimple .comment, .messageSimple .secondaryContent,
.message .likesSummary {
    border: var(--normal-border);
}
.textWithCount.subHeading .text {
    color: rgb(225, 223, 223);
}

.profilePage .mast .sectionFooter {
    border-left: var(--normal-border);
    border-right: var(--normal-border);
    border-bottom: var(--normal-border);
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
}

.profilePage .primaryUserBlock .lastActivity,
.NewsFeedEnd .sectionFooter,
.textHeading, .xenForm .sectionHeader,
.xenForm fieldset, .xenForm .formGroup,
.eventList {
    border-color: var(--normal-border-tone);
}

.privateControls .item.control, .message .publicControls .item {
    color: var(--in-page-accent-tone-1);
}
body .muted, body a.muted, body .muted a {
    color: var(--time-color);
}

.xenForm fieldset + .ctrlUnit, .xenForm .formGroup + .ctrlUnit, .xenForm .submitUnit {
    border-color: var(--normal-border-tone);
}

.hasJs .bbCodeSpoilerText {
    background-color: #444;
    border-color: var(--normal-border-tone);
}

.navPopup .PopupItemLinkActive:hover {
    background-color: var(--input-background);
}

hr {
    background-color: var(--normal-border-tone)!important;
}

}

@-moz-document url-prefix("http://chiaseit.vn/forum/"), url-prefix("https://chiaseit.vn/forum/"), domain("chiaseit.vn") {
/* CHAT BOX */

#messresult {
    padding: 1em 1em 1em 1em;
    border: 1px solid black;
    background: var(--in-page-background-tone-2);
    color: var(--bright-text);
}

.mif_chatbox .txtarea {
    background-color: var(--input-background) !important;
    color: var(--bright-text)!important;
    border: 2px inset #393939;
    height: 24px;
}
.mif_chatbox .txtarea:focus {
    background-image: none!important;
    background-color: #393939!important;
    color: var(--bright-text)!important;
    border: 2px inset #393939;
}

.time {
    color: var(--time-color);
}

.alt {
    background: var(--in-page-background-tone-2);
    color: var(--bright-text);
}

/* Redirect */

#GFNContainer {
    background-color: var(--in-page-background-tone-2);
    border: var(--normal-border);
}
#GFNContainer h3 {
    border-color: var(--normal-border-tone);
}

#GFNContainer .GFNfooter {
    background-color: var(--in-page-background-tone-2);
}
#GFNContainer p {
    color: var(--bright-text);
}

ul.GFNbuttons > li > a.forward:hover {
    background-color: var(--in-page-accent-tone-1);
}
ul.GFNbuttons > li > a.cancel {
    color: var(--bright-text);
    background-color: rgb(72,75,78);
}
ul.GFNbuttons > li > a.cancel:hover {
    color: var(--bright-text);
    background-color: rgb(47, 47, 48);
}

/* Non-member */

.PanelScroller .scrollContainer, .PanelScrollerOff .panel {
    background-color: var(--in-page-background);
    border: var(--normal-border);
}
.PanelScroller .panel {
    background-color: var(--in-page-background);
}

.loginAndRegister a.noOutline {
    background: var(--black-box-gradient);
}
.visitorTabs .concealed {
    color: var(--in-page-link)!important;
}

/* Get link */

.getlinkfshare .box-left .panel-heading {
    background-color: var(--in-page-background-tone-2);
}
.getlinkfshare .panel-body {
    border: var(--normal-border);
    background-color: var(--in-page-background);
}
.getlinkfshare .form-control {
    color: var(--bright-text);
    background-color: #444;
    background-image: none;
    border: var(--normal-border);
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.075) inset;
    transition: border-color 0.15s ease-in-out 0s, box-shadow 0.15s ease-in-out 0s;
}
.getlinkfshare .form-control:focus {
    background-color: var(--in-page-background-tone-2);
}

/*Invert Icons */

.brmsIcoRefresh, .brmsIcoConfig, .brmsIcoList, .brmsIcoLayout {
    background-image: url('data:image/png;base64,');
 }

.brmsConfigBtn:hover .brmsIcoRefresh {
    background: url('data:image/gif;base64,') no-repeat center center;
}
}

@-moz-document url-prefix("http://chiaseit.vn/forum/"), url-prefix("https://chiaseit.vn/forum/"), domain("chiaseit.vn") {
/* SOME CUSTOM TWEAKS */

/* Chat box auto img wrapper */

#messresult div img {
    max-width:100%; 
    max-height:100%;
}

/* Username dark theme fix*/
.username .style4 {
    color: #0000CD;
    text-shadow: 0.07em 0.07em 0.05em lightblue;
}

/* Different color for visted with unread content links */

.discussionListItems .unread .title a {
    color: var(--in-page-link)!important;
}

.discussionListItem .title a:visited {
    color: var(--in-page-visted-link)!important;
}

.BRMSContainer .brmsTabContent ol.brmsContentList a.username:visited {
    color: var(--in-page-link)!important;
}

.BRMSContainer .brmsTabContent ol.brmsContentList a:not([class^='username']):visited {
    color: var(--in-page-visted-link)!important;
}

.BRMSContainer .brmsTabContent ol.brmsContentList a[href$='unread']:visited {
    color: var(--in-page-unread-visted-link)!important;
}

.BRMSContainer .brmsTabContent ol.brmsContentList a.paint,
.BRMSContainer .brmsTabContent ol.brmsContentList a.paint:visited {
    color: var(--time-color)!important;
}

.discussionListItem .lastPostInfo .username {
    color: var(--in-page-link)!important;
}

.discussionListItems .unread .title a:visited {
    color: var(--in-page-unread-visted-link)!important;
}

.discussionListItems .unread .title a {
    color: var(--in-page-link)!important;
}

.discussionListItem .title a:visited {
    color: var(--in-page-visted-link)!important;
}

/* Bright color for post contents */
.message .messageContent,
.message .signature {
    background-color: var(--in-page-background);
    color: var(--bright-text);
}

.message .messageContent span[style^="color"],
.message .signature span[style^="color"],
.primaryContent .baseHtml span[style^="color"] {
    text-shadow: 0.07em 0.07em 0.05em lightblue;
}

/*--Hide Google search bar--*/
#___gcse_0 {
    display:none!important;
}

/*--Hide facebook and youtube sidebars, not enable by default--*//*

#widget-15, #widget-21 {
    display:none !important;
}

#widget-12 {
    margin-top: -3em;
}

/*---Hide ads---*/

#content > div > div > center > ins > a > img,
.sidebar > a > img,
.sidebar > center > a > img {
    display:none!important;
}

/*---Signature---*/

.message .signature .bbCodeImage {
max-width:100%!important;
max-height:100%!important;
}
}
