css:
@-moz-document url-prefix("https://tenor.com/") {
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 1rem;
    line-height: 1.5;
    color: #bbbbbb;
    background-color: #1d1d1d;
}
h1, h2, h3, h4 {
    font-family: "Montserrat", helvetica, arial, sans-serif;
    font-weight: 600;
    line-height: 1.1em;
    margin-bottom: 20px;
    margin-left: 0;
    margin-right: 0;
    color: #bbbbbb;
    clear: both;
}
.QualityToggle {
    vertical-align: top;
    margin: 5px;
    margin-right: 15px;
    line-height: 30px;
    padding: 5px 10px;
    display: inline-block;
    border-radius: 3px;
    background-color: #00ADEE;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.08), 0 4px 6px 0 rgba(83, 83, 92, 0.11);
}
.FavButton {
    color: #d0d0d0;
    background-color: #00ADEE;
    font-size: 20px;
    margin: 5px;
    width: 40px;
    height: 40px;
    line-height: 40px !important;
    text-align: center;
    border-radius: 50%;
    box-shadow: 0 4px 6px 0 rgba(83, 83, 92, 0.11), 0 1px 3px 0 rgba(0, 0, 0, 0.08);
    border: solid 1px #e8e8e8;
    cursor: pointer;
    display: inline-block;
}
.Copybox input {
    cursor: pointer;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    width: 100%;
    background: #000;
}
.NavBar {
    width: 100%;
    background-color: #252525;
    height: 50px;
    border-bottom: 1px solid #000;
}
.TrendsTag .info .searchterm {
    font-size: 16px;
    font-family: "Montserrat", helvetica, arial, sans-serif;
    font-weight: 600;
    text-overflow: ellipsis;
    overflow: hidden;
    color: deepskyblue;
}
.NavBar {
    width: 100%;
    background-color: whitesmoke;
    height: 50px;
    border-bottom: 1px solid #000;
}

}
